url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAQCAIAAABY/YLgAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAE5JREFUeNoAQAC//wDx590A4c23Av/+/QL+/fsC/vf3Av319AL++/QC/ffzAMdBAADQOgAA2DIAAOMnAADuFQAA8goIAPUaIgD8k5gCDADKNiBCMeE9UQAAAABJRU5ErkJggg==") repeat-x center top !important;
}



/* MEDIUM & BIG ITEMS BG */

