border-bottom: solid 1px rgba(0,0,0,0.5) !important;
border-left: solid 1px rgba(32,32,32,0.5) !important;
border-right: solid 1px rgba(32,32,32,0.5) !important;
outline: none !important;
margin: 1px !important;
padding: 0 !important;
}

#downloadView > richlistitem[state="2"] progressmeter,
#downloadView > richlistitem[state="3"] progressmeter,
#downloadView > richlistitem[state="4"] progressmeter {
background: rgba(0,0,0,0.2) !important;
border-top: solid 1px rgba(48,48,48,0.3) !important;
border-bottom: solid 1px rgba(0,0,0,0.3) !important;
