css:
@-moz-document domain("livestream.com") {
a.user_link {
        color: #3987cc !important;
        font-weight: bold !important;
    }
    .icon_separator {
        display: none;
    }
    #chat .item .content {
        color: #b0b0b0 !important;
		font-size: 12px !important; /* Increase this number to increase the font size. Default 12px. Be sure to make the same edits to the other font tags below */
		line-height: 16px !important; /* This should be 3 or 4 more than font-size. Default 16px */
    }
    #chat .item.highlight {
        background: none repeat scroll 0 0 #202020 !important;
		font-size: 12px !important;
		line-height: 16px;
    }
    #chat .item.highlight .content, #chat .item.highlight .content .meta, #chat .item.highlight .content .chat_actions a {
        color: #666666;
		font-size: 12px !important;
		line-height: 16px;
    }
    #intercom-container, .intercom-sheet-loading, .intercom-sheet-spinner, #intercom-container, .intercom-conversations-fetching, .intercom-conversations-spinner, .intercom-reset {
        display: none !important;
    }
}
