css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document domain(youtube.com) {
    /*changes what to watch button, playlist text, and subscription text to color matching the blue hitchhiker theme*/
    .guide-item.guide-item-selected {
        background: none repeat scroll 0% 0% #1D7FCC;
    }
    .guide-item.guide-item-selected:hover {
        background: none repeat scroll 0% 0% #1D7FCC;
    }
    .guide-item.guide-item-selected .yt-deemphasized-text {
        background: none repeat scroll 0% 0% #1D7FCC;
    }
    .guide-item.guide-item-selected:hover .yt-deemphasized-text {
        background: none repeat scroll 0% 0% #1D7FCC;
    }
    .guide-collection-item .guide-item.guide-item-selected {
        background: none repeat scroll 0% 0% #1D7FCC;
    }
    li.guide-section h3, li.guide-section h3 a {
        width: 155px;
        color: #1D7FCC;
    }
    /*changes what to watch and subscription buttons under header to blue to match the hitchhiker them*/
    a.yt-uix-button-epic-nav-item.partially-selected:hover {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    button.yt-uix-button-epic-nav-item:hover {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    button.yt-uix-button-epic-nav-item.selected {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    button.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    .epic-nav-item:hover {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    .epic-nav-item.selected {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    .epic-nav-item.yt-uix-button-toggled {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    .epic-nav-item-heading {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    a.yt-uix-button-epic-nav-item:hover {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    a.yt-uix-button-epic-nav-item:selected {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
        border-bottom: 3px solid #1D7FCC;
        border-color: #1D7FCC;
    }
    /*changes about, share, and add to buttons on video to match the blue hitchhiker theme*/
    #watch7-secondary-actions .yt-uix-button:hover {
        border-bottom-color: #1D7FCC;
    }
    #watch7-secondary-actions .yt-uix-button:active {
        border-bottom-color: #1D7FCC;
    }
    #watch7-secondary-actions .yt-uix-button.yt-uix-button-toggled {
        border-bottom-color: #1D7FCC;
    }
    #watch7-secondary-actions .yt-uix-button.yt-uix-button-active {
        border-bottom-color: #1D7FCC;
    }
}
