css:
@-moz-document domain("hatebu.straightline.jp") {
body, #title a {
  background: #2d2d2d;
}

#wrapper {
	max-width: 770px !important;
}

.arrow_box:after {
	border-top-color: #666 !important;  	
}

.global-nav {
	background: #333;
	border: 0px solid !important;
}

.global-nav li a {
	border-right: 0px solid !important;
}

a, #sub .title a {
	color: #ccc !important;
	text-decoration: none !important;
	font-size: 12px !important;
}

a:visited, #sub .title a {
	color: #888 !important;
}

.title {
	background-color: #333;
	border-bottom: 1px solid #2d2d2d !important;
}

.section {
	background: #333 !important;
    border: 1px solid #333 !important;
	line-height: 150% !important;
}

.post-feed-section {
  border-bottom: 1px solid #2d2d2d !important;
    }

.pr .title {
	background-color: #333 !important;
}

.post-feed-hatebu a,
.post-feed-domain,
.social .post-feed-category a,
.it .post-feed-category a,
.life .post-feed-category a,
.economics .post-feed-category a,
.entertainment .post-feed-category a, .game .post-feed-category a,
.fun .post-feed-category a,
.life .post-feed-category a,
.knowledge .post-feed-category a,
#sub .title
{
  	color: #999 !important;
	background-color: #333 !important;
	font-size: 9px !important;
}

.post-share {
	display: none !important;
}
}
