css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jbhifi.com.au") {

#main, #page_view, #filters,
.grid, .container, .primary, .structure,
.minor li, .content, .input, .overlay-content,
.product-tile-added, .total, .block,
#filtersContainer ul li, .options, .ng-scope
{
background-color: #000000 !important;
}
.heading-filters, .heading
{
background: #FFEC0F !important;
}
.removed
{
background: #bfbfbf !important;
}
.list-view .product-tile .info:before
{
background-color: #000000 !important;
z-index: -5 !important;
}
#products_menu li h3 a:hover, #products_menu li h3:hover,
#products_menu li div, input, .server-message, .select
{
color: #FFEC0F !important;
background-color: #000000 !important;
}

#products_menu li div, .product-tile-added,
.address-collection .adr, .content 
{
border: 1px #FFEC0F solid !important;
}
.brand img, .qty
{
background-color: #FFEC0F !important;
padding: 3px !important;
}
header .logo div, header h1,
#main_menu li, .heading-filters
{
background-color: #FFEC0F !important;
}
#controls, #controls .total, header,
#search-overlay .total-results, .controls
{
background: #FFEC0F !important;
}
#search-overlay .search-contain .top-content,
#facets-dt.facets .collapsable li.active
{
background: #000000 !important;
}
.on, header, #products_menu li h3 a, #products_menu li h3,
label, #grandtotal
{
color: #FFFFFF !important;
background-color: #000000 !important;
}
.on p, .pickup h3, .post h3,
#filters li h2, .qty,
.sort p
{
color: #000000 !important;
font-weight: bold !important;
}

.input, .select, .textarea
{
border: 1px solid #FFEC0F !important;
background: #222222 !important;
}
input, #recent-searches
{
background: #222222 !important;
}
input
{
font-weight: bold !important;
}
.price .message, .price .offer,
#controls p, #controls strong,
.icon-heading, .heading h3, .product,
.ng-scope, .show em, .amount, 
.post .positive,
.overview .price .amount,
.overview .price .deliverycost
{
color: #000000 !important;
}
h1, h2, h3, h4, h5, h6, #breadcrumb a,
label, li, strong, p, .tab2 a, .tab3 a, .tab4 a,
.on p, .pickup h3, .post h3, .heading-filters h2,
.overlay-content, .ng-binding, .icon-heading,
.info, #products_menu li div a, .helper,
.details a, #wc-aplus.wc-responsive,
.wc-ppp-caption, p, ul, dd, dt,
.options li a, .select, select, option
{
color: #FFFFFF !important;
}
.amount
{
color: #FFFF00 !important;
}
}
