/* Last.fm CSS */
@-moz-document url-prefix('http://www.last.fm'),
 url-prefix('https://www.last.fm'),
 url-prefix('http://www.lastfm.fr'),
 url-prefix('http://www.lastfm.jp'),
 url-prefix('http://www.lastfm.de')
{
  
  
                                     /* bg */
  
body, .adaptive-skin-container, .content-top, .header--overview, .two-column-layout .container, table.chartlist, td, tr, .chartlist thead, .chartlist tbody, .footer, .mega-menu--open .dropdown-menu-dark, .features-footer-category, .container, .recs-feed .context, .masthead-search-form, body:not(.dashbody) .kerve.widget, .kerve a.btn_trendnav, .kerve .nowslider_imgwrap {
       background: #14151F !important;     
}  
  
.btn-primary, textarea, .masthead-search-submit, .wiki-cta, .label, select, input, field {
       background: #1d1e2a !important;     
}  
  
  
  .follower-btn .follower-btn-following::before, .follower-btn .follower-btn-add-follower::before {
     background-color: #1d1e2a !important; 
  }  
  
.recs-feed .recs-feed-title, .homefeed .secondary-nav-item-link--active::after, .top-bar, .player-bar, .header .secondary-nav-item-link--active::after, .whats-new .secondary-nav-item-link--active::after, .homefeed .secondary-nav-item-link--active::after, .header a.secondary-nav-item-link:hover::after, .whats-new a.secondary-nav-item-link:hover::after, .homefeed a.secondary-nav-item-link:hover::after, .header a.secondary-nav-item-link:focus::after, .whats-new a.secondary-nav-item-link:focus::after, .homefeed a.secondary-nav-item-link:focus::after, .recommended-next-button, .kerve a.btn_trendnav.current, .kerve .trenduser, .kerve .trendglobal {
       background: #1a1b26 !important;     
  } 
  
                               /* main text */
  
body, p, .metadata-title, .auth-dropdown-item-secondary, .recs-feed .context, .recs-feed .recs-feed-title, .metadata-display, .follower-btn .follower-btn-following, .follower-btn .follower-btn-add-follower, input, field, textarea, select {
     color: #888 !important;
}  
  
                                        /* links */
  
a, .footer a, .footer .mimic-link, .mega-menu--open .dropdown-menu-item-dark {
        color: #777 !important;
}  
  
  a:hover, a:focus, .footer a, .footer .mimic-link:hover, .masthead-nav-control {
        color: #555 !important;
        text-decoration: none !important;
  }  
  
  
  
                       /* remove borders and seperators */
  
.content-top, .content-top-inner-wrap, table.chartlist, td, tr, .chartlist thead, .chartlist tbody, .stationlink-list li, .section-with-settings .section-control::after, .shout, .footer .footer-bottom, textarea, .content-top::after, .mega-menu--open .dropdown-menu-dark, .recs-feed .context, .masthead-logo-loading, .wiki-cta, body:not(.dashbody) .kerve.widget, .kerve .trenduser, .kerve .trendglobal, .kerve .nowslider_img, select, input, field {
        border: none !important;
        box-shadow: none !important;
}  
  
  
  
                                  /* remove shit */
  
.feedback-message, .subscribe-cta, .recs-feed .recs-feed-item--album .context::after, .recs-feed .recs-feed-item--artist .context::after, .recs-feed .recs-feed-item--track .context::after, .player-bar .player-bar-progress-wrap, .stationlink-list {
    display: none !important;
}  
  
  
  
                                  /* hide shit */
  
.chartlist .chartlist-delete-button, .recs-feed .recs-feed-description {
    opacity: 0 !important;
}
  
.chartlist .chartlist-delete-button:hover, .recs-feed .recs-feed-description:hover {
    opacity: 0.8 !important;
}  
  
  
                                           /* changes */
  
  
  
          /* header */
     
.header .header-background::before {
        opacity: 0 !important;
}  
  
.header--overview .header-background {
     opacity: 0.3 !important;
}  
  
  
  
         /* spin spin */
  
 .header-avatar-inner-wrap, .player-bar .player-bar-artwork img, .auth-avatar-desktop {
  -webkit-transition: -webkit-transform .8s ease-in-out !important;
    transition: transform .9s ease-in-out !important;
  } 
  
.header-avatar-inner-wrap:hover, .player-bar .player-bar-artwork img:hover, .auth-avatar-desktop:hover {
    transform:rotate(360deg) !important;
    -webkit-transform:rotate(360deg) !important;
}   
  
                  /* main page */
  
.recs-feed .recs-feed-title {
         position: absolute !important;
         bottom: 0 !important;
         width: 100% !important;
         margin-left: -15px !important;  
         padding: 5px !important;
}  
  
                      /* fix dark icons */
  
.section-settings-toggle, .share-chart .share-button-profile, .selectbox .selectboxit-text {
             filter: invert(100) !important;
}    
  
.masthead-logo-loading, .player-bar .source-info {
    filter: grayscale(100) !important;
}  
  
                          /* logo */
  
  .masthead-logo {
            background-image: url('http://puu.sh/nKP6O/e67591bd8a.png') !important;
            transition: 0.5s ease-out !important;
  }  
  .masthead-logo:hover {
                opacity: 0.2 !important;
                transition: 0.5s ease-out !important;
  }    
  
                           /* grid */
  
.grid-items-item {
               transition: 0.8s ease-in-out !important;
  }  
.grid-items-item:hover {
                opacity: 0.3 !important;
               transition: 0.3s ease-in-out !important;
  }  
  
  .recs-feed .recs-feed-playlink::before {
    opacity: 0.5 !important;
  }  
  
                          /* compatibility */
  
  .tasteometer-donut-base {
    fill: transparent !important;
  }  
  
  .tasteometer-compat-low .tasteometer-compat-colour, .tasteometer-compat-super .tasteometer-compat-colour, .tasteometer-compat-very_high .tasteometer-compat-colour, .tasteometer-compat-medium .tasteometer-compat-colour {
    fill: #aaa !important;
    color: #aaa !important;
  }  
  
}
