css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
 *{background-color: transparent !important;} 
body{
background:  url(http://imgsrc.baidu.com/forum/pic/item/d1160924ab18972b7a6e075be6cd7b899f510ace.jpg) no-repeat  fixed center / cover !important;}
  #browse-items-primary,#footer-container,#watch7-main,#body-container,#watch7-sidebar{
  border-radius:10px !important;background : rgba(228,228,228,0.5) !important;
  }
  #masthead-positioner{
  position:absolute !important;
  }
  body a:hover{
  color:#000B0D!important;text-shadow:0 0 1px grey
  ;}
  body a:not(:hover),.weather-setting-content *:not(:hover){
  color:#000B0D!important;} 
  #watch8-ytcenter-buttons > BUTTON:nth-child(2){
  background:transparent!important; 
  }
  #watch-appbar-playlist .playlist-videos-list, #watch-appbar-playlist .playlist-videos-list .yt-ui-ellipsis,.playlist-info,.playlist-header-content,.playlist-title {
  color:#000B0D!important;
  } 
  #watch-appbar-playlist .playlist-videos-list > li:hover, #watch-appbar-playlist .playlist-videos-list > li.currently-playing {
  background : rgba(228,228,228,0.5) !important;
  }
  #watch8-ytcenter-buttons > SPAN :hover,#watch8-ytcenter-buttons > BUTTON:nth-child(2):hover,#watch8-ytcenter-buttons > BUTTON:nth-child(3):hover,#watch8-ytcenter-buttons > BUTTON:nth-child(4):hover,#watch8-ytcenter-buttons > BUTTON:last-child:hover{
  background : rgba(152,245,228,0.5) !important;
  } 
  #body > UL{
  background : rgba(152,245,228,0.5) !important;
  }
  #masthead-search-terms:hover{
  box-shadow: 0px 2px 18px  #BBFFFF inset;border-radius:10px !important;
  }
  SPAN[class=" ytcenter-thumbnail-quality"][style="background: none repeat scroll 0% 0% rgb(187, 0, 0); color: rgb(255, 255, 255);"],#header,#UCrS7AfZdvpoe7NP6vVM3LSg-guide-item{
  display:none !important;
  }
  #player-playlist :not(.watch-queue-nav) .playlist-videos-list > li.currently-playing{
  background : rgba(228,228,228,0.5) !important;width: 97%;margin-left:9px !important;
  } 
  #player-playlist .playlist-videos-list > li:hover{
  border-radius:10px !important;box-shadow: 0px 2px 18px  #BBFFFF inset;width: 97%;margin-left:9px !important;
  }
  .item-section:hover{box-shadow: 0px 2px 18px  #BBFFFF inset;border-radius:20px !important;}
  #yt-masthead-container{border-bottom: 0px solid }
  #appbar-guide-menu,.feed-item-container.browse-list-item-container.yt-section-hover-container.compact-shelf.shelf-item.branded-page-box.clearfix{border-radius:20px !important;background : rgba(228,228,228,0.5) !important; }
  .guide-notification-item :hover,.ytcenter-dialog-fg:hover,.guide-pinned .guide-pinning-enabled #appbar-guide-menu:hover,#guide-container:hover,.related-list-item .content-wrapper:hover{border-radius:10px !important;box-shadow: 0px 2px 18px  #BBFFFF inset;}
  .ytcenter-dialog-fg{border-radius:20px !important;background : rgba(248,258,258,0.9) !important; }
  .yt-shelf-grid-item:hover{background : rgba(187,228,228,0.5) !important;border-radius:10px !important;}
  .yt-valign:before, .yt-valign-container{color:#000000!important;}
}
