css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dobrochan.com") {
    html,body {
         background-image: none !important;
         background-color: #1D1F21 !important;
         color: #C5C8C6 !important;
         font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
         overflow-x: hidden !important;
         font-size: 16px!important;
         margin-left: 0 !important;
         margin-right: 0 !important;
         margin-top: 5px!important;
   }

    .logo, .fileinfo{
        color: #c5c8c6!important;
    }
    .replytitle{
         color: #b294bb !important;
    }
    
    .postername{
       color: #94bb9f !important;
    }
    
    label, .abbrev, .fileinfo{
        font-size: 11px!important;
    }
    
    .theader, passvalid{
        background-color:#b294bb!important;
        border: none!important;
        color:  #1D1F21!important;
    }
    
    .theader a{
        color:   #1D1F21 !important;
    }
    
     .theader a:hover{
        color:   #1D1F26 !important;
    }
    
     .passvalid a{
        color:   #1D1F21 !important;
    }
    
     .passvalid a:hover{
        color:   #1D1F26 !important;
    }
    
    .postblock{
            background-color: #282A2E !important;
          color: #C5C8C6 !important;
    }
    
    .rules, .stylebar{
        display: none!important;
    }
    
    .oppost{
          background-color: #1D1F21 !important;
          color: #C5C8C6 !important;
    }
    
    .spoiler{
         background-color: #C5C8C6 !important;
         color: #C5C8C6 !important;
    }
    
    .spoiler a{
         background-color: #C5C8C6 !important;
         color: #C5C8C6 !important;
    }
    
    .spoiler a:hover{
         background-color: #C5C8C6 !important;
         color: #308ddb !important;
    }
    
    .spoiler:hover{
         background-color: #C5C8C6 !important;
         color: #282A2E !important;
    }
    
    .spoiler blockquote {
        background-color: #C5C8C6 !important;
        color: #C5C8C6 !important;
    }
    
    .spoiler blockquote blockquote {
        background-color: #C5C8C6 !important;
        color: #C5C8C6 !important;
    }
    
    .spoiler blockquote blockquote blockquote {
        background-color: #C5C8C6 !important;
        color: #C5C8C6 !important;
    }
    
    .reply, .popup{
        background-color: #282A2E !important;
        color: #C5C8C6 !important;
        border-style: none !important;
    }
    
    .popup{
        border:1px dotted  #c6c5c8 !important;
    }
    
    .reply_ {
        background-image:url('data:image/png;base64,')!important;
    }
    
  form {
    font-size: 0.9em;
}

  p {
    margin: 4px 0 !important;
}
  
  th {
    border: 1px solid #111111 !important;
}
  
  hr {
    border: none !important;
    border-top: 1px solid #282a2e !important;
    height: 0 !important;
}
  a {
    color: #81A2BE !important;
    padding: 0px !important;
    text-decoration: none !important;
}

a:hover {
    color: #5F89AC !important;
    text-decoration: none !important;
}

.de-refmap {
    font-size: 10px !important;
}

.reflink a {
    color: #C5C8C6 !important;
    text-decoration: none !important;
}

.reflink a:hover {
    color: #5F89AC !important;
    text-decoration: none !important;
}

a.post-reply-link {
    color: #5F89AC !important;
    text-decoration: underline !important;
}

a.post-reply-link:hover {
    color: #81A2BE !important;
    text-decoration: underline !important;
}
    
     .hide {
        background-image:url('data:image/png;base64,')!important;
    }
    .hide:hover {
        background-color:transparent!important;
    }
    .signed {
        background-image:url('data:image/png;base64,')!important;
    }
    .signed:hover {
        background-color:transparent!important;
    }
    .unsigned {
        background-image:url('data:image/png;base64,')!important;
    }
    .unsigned:hover {
        background-color:transparent!important;
    }
    .delete {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApHHmzJn/QRjExoeR1YEVwgTwaUZXA7cBn2ZscihOw6YAl4EYfkJWiM8VWAMDXTO2AKOeRrKcSlbgkBUdZCcAspMcoTSKTR4ASAkWO/XaOY8AAAAASUVORK5CYII=')!important;
    }
    .checked {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApPFMGsN/EAax8WFkdWCFMAF8mtHVwG3ApxmbHIrTsCnAZSCGn5AV4nMF1sBA14wtwKinkSynkhU4ZEUH2QmA7CRHKI1ikwcAxqXv7McycPcAAAAASUVORK5CYII=')!important;
    }
    .delete:hover {
        background-color:transparent!important;
    }
    .close {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApHHmzJn/QRjExoeR1YEVwgTwaUZXA7cBn2ZscihOw6YAl4EYfkJWiM8VWAMDXTO2AKOeRrKcSlbgkBUdZCcAspMcoTSKTR4ASAkWO/XaOY8AAAAASUVORK5CYII=')!important;
    }
    .close:hover {
        background-color:transparent!important;
    }
    
}
