css:
@-moz-document domain("www.biblionetka.pl") {
/*tło*/
div#container {background: #ede8e1 !important;}
div#wsrodku_wrapper {background: #f9f7f4 !important;}
div#srodek_wrapper {background: #ede8e1 !important;}
div#szukaj {background: #ede8e1 !important;}
div#stopka {background: #ede8e1 !important;}


/* zamiana na stare logo*/
#ctl00_customizableLogo_imgLogo {
  box-sizing: border-box;
  background: url(https://imagizer.imageshack.us/v2/391x104q90/674/XqSH3C.png) no-repeat;
  width: 422px;
  height: 104px; /* Height of new image */
  padding-left: 24422px; /* Equal to width of new image */
  margin: -15px;} 

/*ocenione ksiazki w innym kolorze*/
  a.rated-book { color: #f29400 !important; } /* kolor ocenionych */
  a.rated-book { font-weight: 800 !important;  } /* pogrubienie ocenionych */
  
/* autorzy, nieocenione ksiazki, linki */
a, a:link,  a:visited, span.shortUrl, span.NewAnswerWarningDismissLink{ color: #5f5f5f; font-weight:400; font-size: 120%; font-family: Lucida Sans Unicode !important;}

/* kolor linkow do serii itp. */
h2 a { color: #f2940 !important;}

/* link do tekstu recenzji */
#wsrodku .item h4 .uppercas a { color: #5f5f5f !important; font-size:150% !important; }


/* menu gorne*/
div#topmenu a:link, div#topmenu a:visited { color: #b4b4b4  !important; }
div#topmenu { color: #b4b4b4; background: #f8f8f8 !important; }

/*szukaj*/
#ctl00_SearchButton {color: #fff; background: #0f72b5 !important;border-left: solid 10px #efedea; text-transform: lowercase; font-weight:normal;}
td.searchInputCell {border: solid 1px #b4b4b4;}
div#searchAlphabeticalLinks { color: #5f5f5f !important;}
div#szukaj a:link, div#szukaj a:visited { color: #009de0 !important;}
table#searchWrapper {border:none !important;}

/*okienko logowania*/
div#login {background: #006bb3 !important;}
#ctl00_loginUser_LoginButton {background: #f29400;} 

/*prawa kolumna*/
div#moja {  background-color: #fff !important;font-weight: 400; margin-top:10px; border: solid 1px #182983;}
#moja p { background-color: #006bb3 !important; color: #fff !important; }
#moja li a { background-color: #fff !important; color: #182983 !important; font-size: 110% !important;}
#moja li a:hover { background-color: #014a99  !important; color: #fff !important; font-size: 110% !important; text-decoration: none !important;}
#moja ul li a:link, #moja ul li a:visited {background: none;}

/*lewa kolumna*/
div#lewemenu { background: #f8f8f8; border:  solid 1px #b4b4b4 !important; margin-top:10px;}
 #navleft_rb li a {background: none; margin-left:-10px; text-align: left !important; font-size: 100% !important;}
div#lewemenuHeader {margin-top:-10px;}
.side-header {border-bottom: 1px solid #5f5f5f !important;}
body#polecksiazki #polecksiazkimenu a { border:  solid 1px #b4b4b4 !important; color: #f29400 !important; background-color: #fff6ed;}

/*menu glowne*/
body#start #startmenu a, body#katalog #katmenu a, body#ksiegarnia #ksiemenu a, body#recenzje #recemenu a, body#czytatnik #czytmenu a, body#forum #forumenu a,#navtop, li.menuActive a {  background-color: #fff; color: #5f5f5f !important; font-size:110% !important; font-weight: normal !important;} 
#navtop li {background-color: #fff; color: #5f5f5f !important;  border-style: solid;  border-left-color: #5f5f5f;  border-left-width: thin; }
#navtop a:hover {color: black !important; background: #b4b4b4 !important;}

/*belka*/
div#kontekstowe a {font-size:100% !important; font-weight: 400 !important;}
div#kontekstowe_background  { background: #006bb3 ; color: #fff !important;} 
div#kontekstowe_background a:hover{ background: #014a99; color: #fff !important; font-size:100% !important; font-weight: 400 !important; } 
#kontekstowe li a.tutaj{ background-color: #182983; color: #fff !important; font-size:100% !important; font-weight: 400 !important;}

/*narzednik*/
div#small_menu { background: #fff !important; color: #1071b5 !important; border-style: solid;  border-color: #b4b4b4;  border-width: thin; margin-top: 34px;} 
div#small_menu li a { background: #fff !important; color: #1071b5 !important; font-size: 100%} 
div#small_menu_header { background: #fff !important; border-style: solid;  border-bottom-color: #eeeeee;  border-bottom-width: thin; color: #000000 !important;}

/*nagłówki menu*/
.side-header{ color: #5f5f5f !important; text-align: center;}

/*okienko opiekunow*/
div#patronProgressBarMain { border: solid 1px #5f5f5f !important; background: #fff; color: #000000;}
div#patronProgressBarMain p { background: #eeeeee; margin: 0; padding: 0px; font-size: 12px}
div#patronProgressBarMain p:first-child a { color: #000000 !important; }
div#patronProgressBarMain a, span.shortUrl, span.NewAnswerWarningDismissLink  { color: #f29400; }
#ctl00_PatronProgressBar1_patronBar {background: #f29400 !important;}

/*polecane recenzje*/
#recenbox ul li {background: none !important; border:none;}
#recenbox {background-color: #fff6ed !important; margin-top: -10px;}
div#recenbox p {border-bottom:solid 1px #b4b4b4 !important;}

/*srodek*/
div#wsrodku h1 {border-bottom: 1px solid #5f5f5f !important; color: #f29400; font-weight: 200 !important;} 
div#ctl00_MCP_articlePanel h1 {text-transform: none !important;}
div#wsrodku .infoitem {background-color: #ede8e1;}
div#wsrodku .infoitem p {overflow:hidden; text-overflow:ellipsis;}
div#wsrodku .subitem {background-color: #a7acaf;  color: #000000;}
.statsTable tr:hover td{background: #ede8e1 !important;}
a.tag {padding-top:0px; padding-bottom:0px;background: #fff6ed; margin: 2px; font-size:100%;border-bottom: solid thin;border-right: solid thin; }

/*strona uzytkownika*/
div#wsrodku .contentHalfLeft h2, .contentHalfRight h2, .contentFullSize h2 {background:  #006bb3 !important; color: #fff; font-weight: 200 !important;} 
div#wsrodku .contentHalfLeft, .contentHalfRight, .contentFullSize {background:  #fcfbf7 !important;  border: solid 1px #182983 !important;} 
div#wsrodku .item h2 {background:  none !important; color: #5f5f5f !important; font-weight: 200 !important;}

/*forum*/
div#wsrodku .tabela_forum th {background:  #efb65f !important;} 
div#wsrodku .td_sub {background:  #eddabd !important;}
div#wsrodku .td_lewy, .td_male {background:  #f4f0ed !important;}
div#wsrodku .td_bold {background:  #efeae3 !important;}

/*rozniaste*/
input.zatwierdz, input.platnosci { background: #f29400 !important; }
a#AddTags { background-color: #f29400 !important;}
div#stopka a { color: #b4b4b4 !important;}
div.likeButton:hover {border-color: #f29400 !important;background: #fcf1cf}
#wsrodku p.likeButtonText { color:#5f5f5f;}
#wsrodku p.likeButtonText:hover { color:#4c2d00;}
a.LTU_Author {border: dashed thin white; background: #efefef;  border-radius:10px;}
a.LTU_Admin {color: red !important;}
.sideBoxContent {border: none !important; display:none;}
div#container .adsbygoogle {display:none !important; visibility:hidden !important;}
img[src$="tick.png"] { height: 0px !important;width:0px !important;} 
#ctl00_MCP_answerPanel tr th {background:#ede8e1;}
.tabela_srodek tr th {background:#ede8e1; color: #006bb3 !important;}
.tabela_srodek tr td {border: solid 1px #ede8e1; }
.tabela_srodek {border-color: #ede8e1 !important;}
}
