css:
@-moz-document domain("messageworks.com") {
body {
	min-width:860px;
	margin: 0;
	padding: 0;
	font-family: "Courier", monospace;
    color: #f7f3eb;
	font-size:16px;
    background: #F7F3EB;
}

hr {
	border:none;
	border-bottom:1px solid #d6c29b;
}

/* *************
Title Area
************* */


#title {
	background: #EAE0CC;
	border-bottom:1px solid #EAE0CC;
	color: black;
	position:relative;
	padding:0px;
	height:96px;
}
/*
#title * {
	box-sizing:border-box;
	border:1px solid rgba(255,0,0,.5);
}*/

/* ***** Logo ***** */

#title h1 img {
	margin:0px;
	margin-right:4px;
	vertical-align:middle;
	margin-top:-4px;
    
}

#title h1 {
	font-size:32px;
	padding-left:64px;
	top:25%;
	position:absolute;
	left:0px;
	margin:0px;
	height:50%;

}
#title .betaSign {
	font-size:.5em;
	color:#d6c29b;
	vertical-align:top;
}
/* ***** Navigation Bar ***** */

#navbar {
	top:25%;
	position:absolute;
	padding-right:64px;
	right:0px;
	height:50%;
	/*why doesn't it go in the damn title box*/
	margin:0px;
}

#navbar li {
	list-style-type: none;
	display: inline-block;
	height:100%;
	margin:0px;
	padding:0px;
	margin-left:32px;
}

#navbar li a {
	display:block;
	padding:8px;
	padding-right:32px;
	padding-left:32px;
	box-sizing:border-box;
	text-decoration:none;
	border-bottom: 1px solid #000000;
	color:#000000;
}

#navbar li a.selected {
	font-weight:bold;
	border-color: #d6c29b;
}

#navbar li a:hover {
	border-color: #B6D0EA;
}

#navbar li a:focus {
	background:#E4EFF8;
	border-color: #B6D0EA;
	outline:none;
}

/* *************
Body Area
************* */
#mainWrapper {
	padding-top:16px;
	width:80%;
	min-width:768px;
	margin-left:auto;
	margin-right:auto;
}

#chatLog {
	margin-bottom:0px;
	padding:20px;
	width: 100%;
	height: 36em;
	color: #000000;
	background-color:#F3F3F3;
	border:1px solid #F3F3F3;

	overflow: auto;
	box-sizing:border-box;
	position:relative;
}

#versionText {
	margin-top:64px;
}

input {
	font-size:1em;
}
/* ***** Name Bar ***** */
#infoBar {
	margin-bottom:16px;	
	font-weight:bold;
	height:64px;
	overflow:hidden;
	position:relative;
}

#infoBar span {
	height:100%;
	padding:1em;
	box-sizing:border-box;
	margin:0px;
}

#infoBar label {
	font-weight:bold;
	margin-right:16px;
}

#infoBar input {
	font-weight:bold;
	background:#F3F3F3;
	border:1px solid #CCCCCC;
	padding:1em;
	box-sizing:border-box;
	margin:0px;
}

#infoBar .right {
	float:right;
}

#infoBar input:focus {
	background: #DBDBDB;
	outline: 0;
}

#infoBar #disconnectOverlay {
	position:absolute;
	width:100%;
	height:100%;
	bottom:0px;
	right:0px;
	z-index:1;
	background:#F89922;
	text-align:center;
	color:#B93A02;
	text-decoration:none;
	padding:1em;
	box-sizing:border-box;
	font-family: "Open Sans";
	border:1px solid #EC740E;
}
/* ***** Send Bar ***** */

#sendBar {
	position:relative;
	height:64px;
}

#sendBar input {
	width:100%;
	height:100%;
	margin-right:128px;
	background:#F3F3F3;
	border:1px solid #CCCCCC;
	padding-left:1em;
	box-sizing:border-box;
	margin:0px;
}

#sendBar input:focus {
	background:#DBDBDB;
	outline: 0;
    font-family: "Courier", monospace;
}
#sendBar button {
	font-size:1em;
	font-weight:bold;
	width:160px;
	height:64px;
	position:absolute;
	right:0px;
	top:0px;
    font-family: "Courier", monospace;
	border:0px;
	padding-right:50px;
	background-color: #EAE0CC;
	background-image: none;
	background-size:160px 64px;
	box-sizing:border-box;
	color:#000000;
  	content: "Mail Post";
}

#sendBar button:focus {
	outline:none;
	background-image: none;
}
}
