css:
@-moz-document domain("www.biblionetka.pl", "www.test.biblionetka.pl") {

/*tło*/
body#start {background: #e7f2da !important; }
div#wsrodku_wrapper {background: #f3f9ef !important;}
div#container {background: #e7f2da !important; }
div#srodek_wrapper {background: #e7f2da !important; padding-top:0px !important; }
div#szukaj {background: #f3f9ef !important;padding: 10px;}
div#d0 {border:none !important;}

/*ocenione ksiazki w innym kolorze*/
  a.rated-book { color: /*[[kolorocenionych]]*/ !important; } /* kolor ocenionych */
  a.rated-book { font-weight: 800 !important;  } /* pogrubienie ocenionych */
  
/*szukaj*/
#ctl00_SearchButton {color: #590032; background: #e7f2da !important; text-transform: lowercase; border: 1px solid #b30066; margin-left:-2px !important; padding:5px !important;}
td.searchInputCell {border: solid 1px #b30066;}
#ctl00_SearchType td { color: #590032 !important;}
div#searchAlphabeticalLinks { color: #590032 !important;}
div#szukaj a:link, div#szukaj a:visited { color: #b30066 !important;}
table#searchWrapper {border:none !important;}
div#srodek .alphabetList a { color: #590032 !important;}
div#authorsAlphabetPopup, div#booksAlphabetPopup, div#seriesAlphabetPopup {border-color: #590032;}

/*nagłówki menu*/
.side-header{ color: #590032 !important; text-align: center; }

/*prawa kolumna*/
div#moja {   margin-top:10px !important; }
#moja li a:hover { font-weight: bold !important; color: #590032 !important;  text-decoration: none !important;}
#moja li.tutaj a {font-weight:bold  !important; color: #b30066 !important;}

/*lewa kolumna*/
div#lewemenu { background: #fff; border:  solid 1px #b30066 !important; margin-top:10px;}
div#lewemenuHeader {margin-top:-10px;}
.side-header {border-bottom: 1px solid #b30066 !important;}
body#polecksiazki #polecksiazkimenu a { border: none !important;  background-color: #e7f2da;}

/*menu glowne*/
body#start #startmenu a, body#katalog #katmenu a, body#ksiegarnia #ksiemenu a, body#recenzje #recemenu a, body#czytatnik #czytmenu a, body#forum #forumenu a,#navtop, li.menuActive a {  background-color: #fff; color: #590032 !important; font-size:110% !important; font-weight: normal !important;} 
#navtop li {background-color: #fff; color: #fff !important;  border-style: solid;  border-left-color: #590032;  border-left-width: thin; }
#navtop a:hover {color: black !important; background: #d8a2c0 !important;}

/*belka*/
div#kontekstowe a {font-size:90% !important; font-weight: 400 !important;}
div#kontekstowe_background  { background: #b28ea2 ; color: #fff !important;} 
div#kontekstowe_background a:hover{ background: #d8a2c0; color: #fff !important; font-size:90% !important; font-weight: 400 !important; } 
#kontekstowe li a.tutaj{ background-color: #b30066; color: #fff !important; font-size:90% !important; font-weight: 400 !important;}
.siteInfo span {font-size:90% !important;}

/*narzednik*/
div#small_menu { background: #fff !important; color: #1071b5 !important; border-style: solid;  border-color: #b30066;  border-width: thin; margin-top: 34px;} 
div#small_menu li a { background: #fff !important; font-size: 100%} 
div#small_menu_header { background: #d8a2c0 !important; border-style: solid;  border-bottom-color: #b30066;  border-bottom-width: thin; color: #590032 !important;}

/*okienko opiekunow*/
div#patronProgressBarMain { border: solid 1px #b30066 !important; background: #fff; color: #590032;}
div#patronProgressBarMain p { background: #edf9f3; margin: 0; padding: 0px; font-size: 12px}
div#patronProgressBarMain p:first-child a { color: #590032 !important; }
div#patronProgressBarMain a, span.shortUrl, span.NewAnswerWarningDismissLink  { color: #b30066; }
#ctl00_PatronProgressBar1_patronBar {background: #b28ea2 !important;}
#patronBarContainer {background: #f3f9ef !important;}

/*polecane recenzje*/
div#recenbox ul li {padding-left:20px !important;}
#recenbox {background-color: #fff !important; border: solid 1px #b30066 !important; padding-left:10px;}
div#recenbox p {border-bottom:solid 1px #b30066 !important; }
div#recenbox .side-header {margin-top:10px !important; margin-left:-10px;}
li#rec_coda {border:none !important;}

/*srodek*/
div#wsrodku h1 {border-bottom: 1px solid #590032 !important; color: #b30066; font-weight: 200 !important;} 
div#ctl00_MCP_articlePanel h1 {text-transform: none !important;}
div#wsrodku .infoitem {background-color: #e7f2da;}
div#wsrodku .subitem {background-color: #b28ea2;  color: #590032;}
#ctl00_MCP_answerPanel tr th {background:#e7f2da;}
div#wsrodku .infoitem p {overflow:hidden; text-overflow:ellipsis;}
.statsTable tr:hover td{background: #dbe5ce !important;}
a.tag {padding-top:0px; padding-bottom:0px;background: #e7f2da; margin: 2px; font-size:100%;border-bottom: solid thin;border-right: solid thin; }

/*schowek*/
.tabela_srodek tr th {background:#dbe5ce; color: #b30066 !important; font-weight:800 !important; font-size: 110%;}
.tabela_srodek tr td {border: solid 1px #dbe5ce; }
.tabela_srodek {border-color: #dbe5ce !important;}

/*forum*/
div#wsrodku .tabela_forum th {background:  #c8c7b4 !important;} 
div#wsrodku .td_sub {background:  #dbe5ce !important;}
div#wsrodku .td_lewy, .td_male {background:  #f8f7ef !important;}
div#wsrodku .td_bold {background:  #e7f2da !important;}

/*rozniaste*/
a.LTU_Author {border: dashed thin white; background: #efefef;  border-radius:10px;}
a.LTU_Admin {color: #65B300 !important;}
.sideBoxContent {border: none !important; display:none;}
div#container .adsbygoogle {display:none !important; visibility:hidden !important;}
img[src$="tick.png"] { height: 0px !important;width:0px !important;}
}
