css:
@-moz-document url-prefix('https://thenewboston.com'), url-prefix('https://www.thenewboston.com'){
	html, body{
		color: white !important;
		background:rgb(26, 26, 26) !important;
	}
	body p{
		color: white !important;
	}
	
	
	.btn.btn-outline, .btn.btn-outline.btn-flat, .btn.btn-outline.btn-primary, .btn.btn-outline.btn-flat.btn-primary{
		color: white !important;
	}
	
	.theme-default #main-menu-bg{
		background-color: rgb(21, 21, 21) !important;
	}
	textarea, .form-control, .panel-body, .post-panel-body, .panel-footer, .post-panel-footer, input, .has-feedback .form-control{
		background:transparent !important;
		color:white !important;
	}
	.form-control{
		border: 1px solid #383838  !important;
	}
	.theme-default #main-navbar .navbar-collapse>div>.navbar-nav{
		border-color: #383838  !important;
	}
	.theme-default #main-navbar .navbar-nav>li, .theme-default.main-menu-right #main-navbar .navbar-header{
		border-color: #383838 !important;
	}
	.theme-default #main-navbar .navbar-collapse, .theme-default #main-navbar .navbar-header, .theme-default #main-navbar .dropdown-menu{
		background: #000000 !important;
		color: white !important;
	}
	.theme-default #main-navbar .dropdown-menu>li>a, .theme-default #main-navbar .navbar-nav>li>a{
		color:white !important;
	}
	
	.theme-default #main-navbar #main-menu-toggle, .theme-default #main-navbar #main-menu-toggle .fa:before, .theme-default #main-navbar .dropdown-menu>li>a, .theme-default #main-navbar .dropdown>a, .theme-default #main-navbar .nav>li>a, .theme-default #main-navbar .navbar-brand, .theme-default #main-navbar .navbar-header a, .theme-default #main-navbar .navbar-toggle{
		color:white !important;
	}
	
	.theme-default #main-navbar #main-menu-toggle{
		background:black !important;
	}
	.theme-default #main-navbar .navbar-nav>li>a:hover, .theme-default #main-navbar .navbar-nav>li.active>a, .theme-default #main-navbar .navbar-nav>li.dropdown.open>a, .theme-default #main-navbar .navbar-nav>li>a:hover, .theme-default #main-navbar .dropdown-menu>li>a:hover{
		background:#333333 !important;
		color:white !important;
	}
	
	.theme-default .breadcrumb-page{
		background:transparent !important;
		border-bottom: 1px solid #383838;
	}
	.breadcrumb li, .breadcrumb li a, .breadcrumb li.active, .breadcrumb li.active a{
		color:white !important;
	}
	
	.page-profile .new-post-panel, .page-profile .widget-followers{
		border-color: #383838 !important;
		background: #171717 !important;
	}
	
	.nav-tabs, .nav-tabs > li > a{
		border-bottom: 2px solid #383838;
	}
	.nav-tabs > li > a{
		color:white !important;
		background:#333333 !important;
	}
	.theme-default .nav-tabs>li.active>a{
		background:#1D89CF !important;
	}
		
	.tab-content.tab-content-bordered{
		border: 1px solid #282828;
		background: inherit !important;
	}
	
	.notifications-table td{
		color:white !important;
	}
	
	.post-footer{
		border-top: 2px solid #383838;
	}
	
	.panel, .post-footer{
		background: #171717 !important;
		color: white !important;
	}
	.panel.panel-transparent{
		background: none !important;
	}
	.input-group-addon{
		border: 1px solid #383838;
	}
	.likes-table td a, .input-group-addon{
		background: #212121 !important;
		color: white !important;
	}
	
	.theme-default .bordered, .theme-default .panel, .theme-default .table, .theme-default hr, .notifications-table tr{
		border-color: #383838 !important;
	}
	.post-footer table tr.comment-row{
		border-color: #383838 !important;
	}
	/*index page specific*/
	.list-group-item{
		background: #171717 !important;
		color: white !important;
	}
	.list-group-item:hover{
		background:#333333 !important;
		color:white !important;
	}
	
	
	
	/*forum specific*/
	.ads-table, .forum-feed-table, .forum-feed-table tr:nth-child(odd) td, .marketplace-table, .moderator-table{
		background:#191919 !important;
		color:white !important;
	}
	.panel-heading{
		border-color: #383838 !important;
	}
	.pagination > li > a, .pagination > li > span{
		border-color: #383838 !important;
	}
	.forum-feed-table tr:nth-child(even) td, .panel-heading{
		background:#212121 !important;
		color:white !important;
	}
	.forum-following-panel .panel-body a.following-row{
		color: white !important;
	}
	.forum-following-panel .panel-body a.following-row:hover{
		color: white !important;
		background:#1D89CF !important;
	}
	.panel-title, .forum-heading-table .heading-col-1{
		color: white !important;
	}
	.forum-replies-table .reply-form-column, .reply-form-table .reply-form-column, .topic-table .reply-form-column, .sceditor-container, div.sceditor-toolbar{
		background: #171717 !important;
		color: white !important;
	}
	.sceditor-button{
		background: #171717 !important;
	}
	
	.forum-replies-table tr:nth-child(odd) td{
		background:#191919 !important;
		color:white !important;
	}
	.forum-replies-table tr:nth-child(even) td{
		background:#212121 !important;
		color:white !important;
	}
	
	/*view messages specific*/
	.page-mail, .page-mail .mail-controls, .theme-default.page-mail .mail-nav, .theme-default.page-mail .mail-nav .compose-btn, .theme-default.page-mail .mail-nav .sections li.active, .theme-default.page-mail .mail-nav:before{
		background: #171717 !important;
		color: white !important;
	}
	
	.theme-default.page-mail .mail-nav .sections a{
		color: white !important;
	}
	
	.theme-default.page-mail .mail-nav .sections a:hover{
		background:#333333 !important;
		color:white !important;
	}
	
	.page-mail .mail-item{
		background: #171717 !important;
		color: white !important;
	}
	.page-mail .m-subject a, .page-mail .m-from a{
		color: white !important;
	}
	.page-mail .mail-container-header{
		background: #171717 !important;
		color: white !important;
	}
	
	/*friend requests specific*/
	.theme-default .page-header, .page-header h1{
		background: #171717 !important;
		color: white !important;
	}
	.nav-tabs.nav-tabs-simple > li.active > a{
		color: white !important;
	}
	
	/*friends tab on profile page*/
	.widget-followers .follower-name{
		color: white !important;
	}
}
