@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://www.isl.be/moodle){

html,
body{
  background-image:none;
  background-color:#262626;
  color:#ccc}

body{
  background-repeat:repeat;
  background-image:url(data:image/png;base64,)}

a:link,
a:visited,
#frontpage-category-names h2{
  color:#fff}

a.logo{
  background:url("data:image/png;base64,") no-repeat}
 
#page-wrapper{border:1px solid #262626}

#page-header{background-color:#1a1a1a}

.breadcrumb a,
.breadcrumb a:link,
.breadcrumb a:visited{
  font-size:16px}

.loginbox h2{color:#fff}

#region-pre-box,/*sidebar gauche*/
#page-content{/*sidebar droite*/
  background-color:#323232}

.block .header .title h2{
  background:#BAD80A;/*vert*/
  border:1px solid #BAD80A;
  font-size:16px;
  text-shadow:0px 1px 1px #626d22}

h2#instance-3101-header{color:#fff}

.block .header .title h2{padding:2px 0}

#region-pre .block .header,
#region-post .block .header{
  background:#BAD80A;
  border-bottom:1px solid #BAD80A;
  color:#fff}

.navbar{
  background-image:none;
  background-color:#00CCFF;
  border-bottom:1px solid #0CF;
  border-top:1px solid #0CF;
  color:#fff}

/*calendrier*/
.calendar-controls{
  padding:5px 0;
  background-color:#1A1A1A}

.block .minicalendar th, .block .minicalendar td{font-size:11px}

.block .minicalendar td.weekend{
  background-color:#ff8c00;/*orange*/
  color:#262626}

.block .minicalendar a{color:#000}

.block .minicalendar .calendar-controls a{color:#fff}

.block .minicalendar caption{background-color:#1a1a1a}

.block .minicalendar caption h3{
  background-color:#1a1a1a;
  border:1px solid #1a1a1a}

.block .minicalendar h3 a{
  font-size:14px;
  color:#fff}

.block .minicalendar th, .block .minicalendar td{
  font-size:10px;
  padding:12px 0}

#calendar .event_course,
.minicalendar .event_course,
.block_calendar_month .event_course,
.calendar_event_course{
  background-color:#0CF;/*bleu*/
  border-color:#0CF !important}

/*fin calendrier*/

h1, h2, h3{
  background-color:#323232;
  border:1px solid #323232}

h3{font-size:95%}

h3.categoryname{
  background-color:#b6b6b6;
  border:1px solid #a1a1a1;
  border-bottom:1px solid #fff;
  -webkit-border-radius:2px;
  -moz-border-radius:2px;
  border-radius:2px}

a.collapseexpand,
h4.categoryname a{color:#505050}

/*contenu central*/
#region-main{
  background-color:#ececec;
  color:#262626}

#region-main h1,
#region-main h2{color:#fff}

#region-main a:link,
#region-main a:visited{color:#337f8c}

#region-main h2 a:link,
#region-main h2 a:visited,
#region-main h3,
#region-main h3 a:link,
#region-main h3 a:visited{color:#fff}

#region-main h3.categoryname a:link{color:#3c3c3c}

#page-content{border-bottom:0}

/*bouton fichers personnels*/
#loginbtn, input,
button, select{
  margin:7px 0 0;
  padding:1px}

select,
input,
button{
  background-color:#ff8c00;
  border:0;
  color:#fff}

input[type="image"]{background-color:transparent}

/*fin fichiers perso*/

/*footer*/
#page-footer{
  background-image:none;
  background-color:#1A1A1A;
  border-top:5px solid #0CF}

#page-footer{line-height:1em}

.logininfo{color:#999}

#page-footer a:link, #page-footer a:visited{color:#fff}

/*delete*/
 img[src="https://www.isl.be/moodle/theme/image.php/afterburner/theme/1396356449/footer/moodle-logo"]{display:none}

}
