css:
@namespace url(http://www.w3.org/1999/xhtml);
/*!
 * This is just a demo for personal site userstyle!
 * @version: 0.2
 * @author: alxzaur
 */
@-moz-document domain("vvmatveev.com") {
  html {
    position: relative;
    width: 100%;
    height: auto !important;
    min-height: 100% !important;
    background-color: #222 !important;
  }
  body {
    width: 100%;
    min-height: 100%;
    background-color: transparent !important;
  }
  body * {
    font-family: "Open Sans", Arial, Helvetica, sans-serif !important;
  }
  body:after {
    content: "";
    opacity: 0.2;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: -1;
    background-image: url("http://vvmatveev.com/sites/default/files/portfolio/slideshow/00_glavnaya_4.png");
    background-size: cover;
    background-position: 50% 50%;
    background-repeat: no-repeat;
  }
  /* ссылки навигации */
  .navigation {
    border-bottom: none !important;
    padding: 0 0 !important;
    height: auto !important;
    margin: 0 0 !important;
  }
  /* ссылки */
  #main-menu {
    margin-bottom: 0 !important;
  }
  #main-menu a:link,
  #main-menu a:visited {
    display: inline-block !important;
    color: #ccc !important;
    font-size: 15px !important;
    width: auto !important;
    text-align: center !important;
    padding: 15px 15px !important;
    margin: 0 0 !important;
  }
  #main-menu a:hover {
    color: #444 !important;
    background-color: #ccc !important;
  }
  #main-menu a.active {
    color: #fff !important;
  }
  /* основной блок */
  #block-system-main {
    background-color: #555 !important;
    box-shadow: 0 0 6px #111;
  }
  .container-12 .grid-4 {
    width: 320px !important;
    margin: 0 0 !important;
  }
  .container-12 .grid-8 {
    width: 620px !important;
    margin: 0 0 !important;
  }
  /* описание главной страницы */
  .front-info-hdr {
    font-size: 18px !important;
    font-weight: 400 !important;
    line-height: 20px !important;
    letter-spacing: 1px !important;
    color: #ddd !important;
    margin: 0 0 !important;
    text-align: center !important;
    padding: 25px 0 80px 0 !important;
  }
  .front-info-hdr span {
    font-size: 20px !important;
    font-weight: 600 !important;
    margin-top: 5px !important;
  }
  .front-info-img {
    margin: -40px auto 0 auto !important;
    text-align: center;
    width: 120px;
    height: 120px;
    overflow: hidden;
  }
  .front-info-list {
    margin: 40px 0px 0px 40px !important;
    text-transform: uppercase;
    list-style-type: square;
    font-size: 14px !important;
  }
  .front-info-list li:nth-child(7),
  .front-info-list li:nth-child(8) {
    display: none !important;
  }
  /* описание пункта портфолио */
  .portfolio-term {
    padding: 40px 0px 0px !important;
    z-index: 2 !important;
  }
  .portfolio-term h3 {
    font-size: 18px !important;
    font-weight: 400 !important;
    line-height: 18px !important;
    margin: 0 0 !important;
    letter-spacing: 1px !important;
    position: absolute !important;
    top: 0;
    left: 0;
    width: 100% !important;
    padding: 25px 2px 112px 0px !important;
    background-color: #444 !important;
    color: #ddd !important;
  }
  .portfolio-term h3:after {
    position: absolute;
    display: block;
    width: 160px;
    height: 160px;
    left: 75px;
    top: 70px;
    background-color: #444;
    border-radius: 50%;
    border: 5px solid #555;
    content: "";
  }
  .portfolio-term img {
    margin-top: 75px !important;
    position: relative !important;
    z-index: 1;
    max-width: 120px;
  }
  /* описание страницы */
  body.page-taxonomy-term .portfolio-term:after {
    display: block;
    padding: 80px 25px 0 25px;
    font-size: 14px;
    text-align: left;
    content: "";
  }
  body.page-taxonomy-term.page-taxonomy-term-1 .portfolio-term:after {
    content: "СТРУКТУРА - «скелет» определяет городскую ткань, нагрузку в узлах, и доминирование в связях. СРЕДА - «форма сосуществования» всех видов объектов. АНСАМБЛЬ - законченое «градостроительное произведение», подчиняющееся структуре, принадлежащее среде.";
  }
  body.page-taxonomy-term.page-taxonomy-term-6 .portfolio-term:after {
    content: "ФУНКЦИЯ - «здоровый организм», живуший в условиях обусловленных назначением. ЗРЕЛИЩНОСТЬ - «характер здания» вскрытый автором. ЗНАКОВОСТЬ - узнаваемое «лицо, гордость» вне времени.";
  }
  body.page-taxonomy-term.page-taxonomy-term-3 .portfolio-term:after {
    content: "КОМФОРТ - продолжительное «удобное времяпроведение». УЮТ - благоприятное, «обустроенное пространство». ЗАЩИЩЁННОСТЬ - безопасное, «спокойное пребывание»";
  }
  body.page-taxonomy-term.page-taxonomy-term-5 .portfolio-term:after {
    content: "КЛИМАТ - «условия обитания» живых существ. САД - «место и форма сосуществования» живых существ. МАЛАЯ ФОРМА - вневременной, обустоенный «элемент комфорта» на воздухе.";
  }
  body.page-taxonomy-term.page-taxonomy-term-4 .portfolio-term:after {
    content: "ЭРГОНОМИКА - «доступность и удобство» жизнедеятельных процессов. ПРОЖИВАНИЕ - «времяпровидение» в благоприяном пространстве. СТИЛЬ - предномеренно выбранное «решение организации пространства».";
  }
  body.page-taxonomy-term.page-taxonomy-term-2 .portfolio-term:after {
    content: "ЭВОЛЮЦИЯ - «исторические этапы» формирования проблемы и её решений. ИДЕЯ - «творческая мысль». ПРОДУКТ - «технологическое проектирование» функций объекта.";
  }
  /* слайдер с картинками */
  /* указатель категорий на главной */
  #category-marker {
    display: none !important;
  }
  .marker1 {
    left: 29px !important;
  }
  .marker2 {
    left: 132px !important;
  }
  .marker3 {
    left: 235px !important;
  }
  .marker4 {
    left: 338px !important;
  }
  .marker5 {
    left: 441px !important;
  }
  .marker6 {
    left: 544px !important;
  }
  div.anythingSlider {
    width: 618px !important;
    height: 464px !important;
    margin-left: 1px !important;
  }
  div.anythingSlider .anythingWindow {
    margin-left: 1px !important;
  }
  div.anythingSlider .anythingControls {
    width: 100%;
    height: 40px !important;
    text-align: center;
    bottom: 0 !important;
    left: 1px !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
    background-color: rgba(0, 0, 0, 0);
    transition: background-color 1s;
  }
  div.anythingSlider .anythingControls:hover {
    background-color: rgba(0, 0, 0, 0.25);
    transition: background-color 1s;
  }
  div.anythingSlider .anythingControls ul {
    float: none !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
  }
  div.anythingSlider .anythingControls ul li {
    display: inline !important;
    width: 30px !important;
    height: 30px !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
  }
  div.anythingSlider .anythingControls ul a {
    width: 30px !important;
    height: 30px !important;
    margin: 5px 0 0 !important;
    padding: 0 0 !important;
    border: none !important;
    background-color: transparent !important;
  }
  div.anythingSlider .anythingControls ul a.cur,
  div.anythingSlider .anythingControls ul a:hover {
    margin: 0 0 !important;
    border: none !important;
    background-color: transparent !important;
  }
  div.anythingSlider .anythingControls ul a span {
    display: block !important;
    width: 12px !important;
    height: 12px !important;
    margin: 9px 9px !important;
    border-radius: 50%;
    background-color: rgba(255, 255, 255, 0.5) !important;
  }
  div.anythingSlider .anythingControls ul a:hover span {
    background-color: #fff !important;
  }
  div.anythingSlider .anythingControls ul a.cur span {
    background-color: #0080ff !important;
  }
  .view-display-id-block_1 .slide a,
  .view-id-awards .slide a {
    width: 206px !important;
    height: 155px !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
    overflow: hidden;
    background-color: transparent !important;
  }
  .view-display-id-block_1 .slide a img,
  .view-id-awards .slide a img {
    width: 205px !important;
    height: 154px !important;
    opacity: 1.0;
    transition: all 1s;
  }
  .view-display-id-block_1 .slide a img:hover,
  .view-id-awards .slide a img:hover {
    transition: all 1s;
    opacity: 0.5;
  }
  .view-display-id-block_1 .slide .emptycell,
  .view-id-awards .slide .emptycell {
    width: 206px !important;
    height: 155px !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
    overflow: hidden;
    background-color: transparent !important;
    position: relative;
  }
  .view-display-id-block_1 .slide .emptycell:after,
  .view-id-awards .slide .emptycell:after {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    content: "";
    width: 205px !important;
    height: 154px !important;
    background-color: #999 !important;
  }
  #slider li.slide.views-row {
    background-color: #555 !important;
    width: 619px !important;
    height: 464px !important;
    margin-left: 1px !important;
  }
  /* содержимое портфолио */
  /* нижний блок */
  .region-postscript-first-inner {
    border-top: none !important;
    margin: 20px 0 30px 0 !important;
    padding: 0 0 !important;
  }
  /* ссылки на разделы портфолио */
  #block-views-portfolio-menu-block h2 {
    width: 300px !important;
    margin: 0 0 0 20px !important;
    font-weight: 300 !important;
    font-size: 36px !important;
    line-height: 36px !important;
    letter-spacing: 1px !important;
    color: #fff !important;
  }
  #block-views-portfolio-menu-block ul {
    height: 93px !important;
  }
  #block-views-portfolio-menu-block ul li {
    margin: 0 5px !important;
    width: 93px !important;
    height: 93px !important;
  }
  #block-views-portfolio-menu-block ul li a {
    height: 93px !important;
    display: block !important;
    box-shadow: 0 0 6px #222;
    border-radius: 50%;
    background-image: url("data:image/png;base64,") !important;
    background-repeat: no-repeat !important;
    transition: all 1s;
  }
  #block-views-portfolio-menu-block ul li a.active,
  #block-views-portfolio-menu-block ul li a:hover {
    background-color: #fff !important;
    transition: all 1s;
  }
  /* иконки портфолио */
  .portfolio-1 a {
    background-position: 0px 6px !important;
    background-color: #bb4690 !important;
  }
  .portfolio-1 a:hover,
  .portfolio-1 a.active {
    background-position: 0px -74px !important;
  }
  .portfolio-6 a {
    background-position: -94px 6px !important;
    background-color: #be4c39 !important;
  }
  .portfolio-6 a:hover,
  .portfolio-6 a.active {
    background-position: -94px -74px !important;
  }
  .portfolio-3 a {
    background-position: -188px 6px !important;
    background-color: #e18728 !important;
  }
  .portfolio-3 a:hover,
  .portfolio-3 a.active {
    background-position: -188px -74px !important;
  }
  .portfolio-5 a {
    background-position: -282px 6px !important;
    background-color: #4ca454 !important;
  }
  .portfolio-5 a:hover,
  .portfolio-5 a.active {
    background-position: -282px -74px !important;
  }
  .portfolio-4 a {
    background-position: -376px 6px !important;
    background-color: #9351a6 !important;
  }
  .portfolio-4 a:hover,
  .portfolio-4 a.active {
    background-position: -376px -74px !important;
  }
  .portfolio-2 a {
    background-position: -470px 6px !important;
    background-color: #4472b9 !important;
  }
  .portfolio-2 a:hover,
  .portfolio-2 a.active {
    background-position: -470px -74px !important;
  }
  /* подвал страницы */
  .region-footer-first-inner {
    border-top: none !important;
    margin: 0 0 !important;
    padding: 0 0 !important;
    width: 320px !important;
    position: absolute;
    left: 0;
    bottom: 20px;
    font-size: 13px;
    line-height: 1.2;
  }
  .region-footer-first-inner:after {
    display: block;
    margin: -30px 0 0 20px;
    content: "© 2011-1014 Вячеслав Матвеев.";
  }
  .region-footer-first-inner .content {
    display: none !important;
  }
  .region-footer-first-inner p {
    margin-bottom: 0 !important;
  }
  /* страница "о себе" */
  .aboutme {
    height: auto !important;
  }
}
