css:
@-moz-document domain("8chan.co") {

    body{
        background:#1e1f21;
    }
    body, div.report {
        color: rgb(197, 200, 198);
    }
     
    a, a:visited {
        color: rgb(129, 162, 190);
    }
    a:hover {
        color: rgb(95, 137, 172);
    }
     
    a.bodylink {
        color: rgb(95, 137, 172);
    }
    a.bodylink:hover {
        color: rgb(129, 162, 190);
    }
     
    span.trip {
        color:#8398A3;
    }
     
    header h1, header div.subtitle, div.blotter {
        color:#D9D9D9;
    }
     
    div.post.reply, div.banner, form table tr th {
        background: rgb(40, 42, 46);
        border: none;
    }
    div.post.reply.highlighted{
        background:#3E3F43;
    }
    .post-hover, .post-inline {
        border: 1px solid rgb(17, 17, 17) !important;
        box-shadow: none !important;
    }
    .post-inline {
        background-color: rgba(0, 0, 0, 0.14) !important;
    }
     
    p.intro span.name{
        color: rgb(197, 200, 198);
    }
    p.intro a.email span.name {
        color: rgb(129, 162, 190);
    }
    p.intro a.email:hover span.name {
        color: rgb(95, 137, 172);
    }
    span.quote{
        color:#99BF87;
    }
    p.intro span.subject{
        color:#C4AADE;
    }
     
    div.pages {
        background: none;
        border: none;
    }
     
     
    .boardlist.top{
        background: rgb(185, 185, 185);
        box-shadow: 0 -1px 5px rgb(150, 150, 150);
    }
    .boardlist.top .boardlistpart a, .boardlist.top .boardlistpart a:visited {
        font-size: 14px;
        padding-top: 0;
        padding-bottom: 0;
        border-top: 2px solid transparent;
        border-bottom: 2px solid transparent;
        color: rgb(75, 90, 110);
    }
    .boardlist.top .boardlistpart a[href="/"] {
        padding-top: 0;
        padding-bottom: 0;
    }
     
    .boardlist.top .boardlistpart a.boardlistactive, .boardlist.top .watcherButton.open {
        background: none;
        border-top: 2px solid rgb(98, 98, 98);
        border-bottom: 2px solid rgb(98, 98, 98);
        padding-top: 0;
        padding-bottom: 0;
    }
    .boardlist.top .boardlistpart a:hover {
        background: none;
        border-top: 2px solid rgb(100, 100, 190);
        padding-top: 0;
    }
     
    div.hidetext{
        background:#47533C;
    }
    #settingsScreen h1{
        color:#1e1f21;
    }
    #updateSettings {
        color: black;
    }

}
