css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http(|s)://(|www.)dobrochan\.(ru|com|org).*") {

html,body {
    background: #1d1f21 none;
	color:#c5c8c6 !important;
	font-family:Segoe UI,arial,sans-serif;
	font-size:10pt;
}
a:hover {
    color:#5F89AC;
}
div.thread {padding-bottom: 15px}
a {
    color:#81a2be;
}
.theader, .passvalid {
    background: #282A2E none repeat scroll 0% 0%;
    color: #FFF;
}
.postblock {
    background: #282A2E none repeat scroll 0% 0%;
    color: #c5c8c6;
}
.reflink a {
    color: #c5c8c6;
}
.fileinfo {
    color: #c5c8c6;
	font-size: 0.78em;
}
label {
    color: #c5c8c6;
	font-size: 0.9em;
}
.logo {
    color: #B294BB;
}
.replytitle {
    color: #B294BB;
}
.postarea {text-align: center;}
.doubledash {
display:none;
}
hr {
   border:#111111 ;
}
.oppost.post, .reply {
    background-color: #282A2E;
    overflow:  auto;
    display: block;
    border-color: #282A2E;
	color:#c5c8c6 !important;
    word-wrap: break-word;
    padding: inherit;
    border-radius: 5px;
	}
.reply, .highlight, .popup {
    background-color:#282A2E;
    color:  #B294BB;
}
.oppost, .reply, div.thread, .de-parea{ 
	width: 1024px;
    margin-left: auto;
    margin-right: auto;
	clear: both;}
.oppost .reply{
    width: 100%;
    clear: both;
}
.adminbar {
    font-size: 0.9em;
}
.abbrev {
    font-size: 0.9em;
}
.spoiler:hover {
    background: #1d1f21 none repeat scroll 0% 0%;
    color: #B294BB;
}
.spoiler {
    background-color: #333 ;
color: rgba(0,0,0,0);
}
.de-menu {
width: auto;
}
.de-cfg-tab {
    width: auto;
}
.pages {
    border: 1px solid #111111;
}
.de-pview{
    border: 1px solid #111111;
    width: auto;
}
.list {
background: #282A2E;
border-left: 1px solid #c5c8c6;
border-right: 1px solid #c5c8c6;
border-bottom: 1px solid #c5c8c6;
}
.list div a {
    color: #c5c8c6;
}
.newsbg {
background-color: #282A2E;
}
.newstop {
background-image: none;
}
.newsbottom {
background-image: none;
border-bottom: 1px solid #c5c8c6;
}
.featuredimgbottom {
background-image: none;
}
.featuredimgtop {
background-image: none;
}
.list div a:hover {
background: #5F89AC;
}
.header {
background: #1d1f21;
color: #c5c8c6;
border-left: 1px solid #c5c8c6;
border-right: 1px solid #c5c8c6;
border-top: 1px solid #c5c8c6;
border-bottom: 1px solid #c5c8c6;
}
.de-entry {
    width: auto;
}
}
