css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asiandvdclub.org") {

/* Images */
  
img.postimg {
    max-width: 500px;
}  
    
table.description img {
    max-width: 150px;
    height: auto;
    vertical-align: bottom;
    border-radius:3px;
}
  
  
/* Links */
    
div.navigation a:hover {
    transition: all 0.4s ease 0s;
}
    
/* content */
    
/*div.logo {
    position: relative;
    height: 104px;
    background: url("http://i.imgur.com/mtm74qz.png") no-repeat scroll center top #840001;
}*/
    
body {
    font-family: Tahoma,Arial,Helvetica,sans-serif;
    font-size: 11px;
    background: none repeat scroll 0% 0% #840001;
    color: #000;
    margin-top: 0px;
    margin-bottom: 0px;
}
  
div.outer {
    margin: auto;
    width: 1000px;
    background: none repeat scroll 0% 0% #F5F4EA;
    border: 1px solid transparent;
    border-radius: 10px;
    padding: 0 8px;
    box-shadow: 1px 1px 5px 0px rgba(50, 50, 50, 0.75);
}
    
div.navigation {
    padding: 0.8em;
    border-bottom: 1px transparent;
}
    
div.main {
    padding: 20px 0px;
}
    
   
div.alerts {
    width: 300px;
    font-family: Verdana;
    background-color: rgba(255, 81, 0, 0.17);
    border: 1px solid darkred;
    border-radius: 0px;
    margin-bottom: 0.8em;
    color: red;
}
.alerts:hover {
    background-color: #ECE9D8;
    border: 1px solid #BBBCBF;
    border-radius: 10px;
    transition: all 0.4s ease 0s;
}
.alerts a {
    color: darkred;
}
.alerts a:hover {
    color: black;
}

    
/* Torrent page */
    
.torrenttable tbody tr.even:hover, .torrenttable tbody tr.odd:hover {
  background-color: rgba(255, 81, 0, 0.17);  
}
    
table.description img {
    max-width: 500px;
    height: auto;
    vertical-align: bottom;
}
    
table.alternate tr:hover {
    background-color: rgba(255, 81, 0, 0.17);   
}
    
/* Forum*/   
    
div.postleft .postavatar img {
    max-width: 150px;
    border-radius: 3px;   
}
    
    
.pun tr:hover {
    font-size: 11pxpx;
   background: rgba(255, 81, 0, 0.17);
}
    
    
}
