@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("epw.in") {
*{
  font-family: "Microsoft MHei";
  line-height:150%;
  background-color:white;
}
  
  
  #p-personal, #header-top-wrapper, #header-group-wrapper, #content-header, #content-top, .print-link, #sidebar-last-inner,
   #footer-wrapper-out, #block-block-8, #block-ad-25, #footer, #block-block-25, #block-disqus-disqus_comments,
 .node-links, #block-block-24, #footer-two
  {
    display: none !important;
}
  .field-field-pdf{
    display: none !important;
  }
  
  
  
  .content {
    font-family: "Microsoft MHei"!important;
    font-size: 18pt !important;
    }
  
  .meta{
    background: #FFF !important; 
    padding:0;
    margin:0 auto;
    border-width:0;
    display:inline;
    font-size: 0.80em;
  }
  .teaser.inner.meta{
    padding:0 0 2000px 0;
  }
  
  h1.title{
    margin:0 auto;
    text-align:center;
    -moz-box-shadow: 0px -10px 30px #BBB;
    -webkit-box-shadow: 0px -10px 30px #BBB;
    box-shadow: 0px -10px 30px #BBB;
    margin: auto !important; 
    width: 750px !important;
    padding: 40px 80px 40px 80px !important; 
    padding-top: 20px !important;
    border: 0 !important;
    line-height:1.5em;
    font-size:250%;
    font-weight:700;
  }
  #page-outer{
    border:0;
    width:auto;;
  }
  .node-type-epw_article{
    border:0;
  }
   #page{
    width:990px;
    border:0;
  }
  
  .node.inner{
    background-color:red;
  }
  
  #content-group{
    text-align: justify;
    overflow:auto;
    font-family: "Microsoft MHei"!important;
    font-size: 14pt !important;
    width:auto;
  }
  .nested{
    overflow:auto;
    float:none;
  }
  #content-region{
    float:none;
    overflow:auto;
  }
  
  #content-content{
    -moz-box-shadow: 0px -10px 30px #BBB;
    -webkit-box-shadow: 0px -10px 30px #BBB;
    box-shadow: 0px -10px 30px #BBB;
    margin: auto !important; 
    width: 750px !important;
    padding: 80px!important; 
    padding-top: 20px !important;
    border: 0 !important;
  }
  #page-outer, #content-inner{
    padding: 0;
  }
}
