@-moz-document domain(youtube.com) {
a{text-decoration:none!important;}
#guide-container{height:0px;padding:0px;}
#yt-masthead-container{border:none;}
#masthead-appbar{border-bottom: 2px solid #A8A8A8;}
.yt-ui-ellipsis{background:none!important;}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading,.multirow-shelf .yt-uix-expander-head,a,.yt-uix-button-subscribe-branded+.yt-subscription-button-subscriber-count-branded-horizontal{color:#000!important;}
.multirow-shelf .yt-uix-expander-head{margin-top:15px!important;}
.yt-uix-expander-collapsed .multirow-shelf-expander, .multirow-shelf-collapser{background:#e62117;box-sizing: border-box;color:#fff!important;border-radius: 2px;padding: 5px;}
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item{color:#333;}
.feed-item-container:hover .feed-item-header a,.yt-lockup-title a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a{color:#000;}
.yt-lockup-thumbnail{box-shadow:0px 0px 10px #000;margin-top:10px;margin-left:10px;background:#cc181e}
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a{margin:0px!important;}
.g-hovercard yt-uix-sessionlink yt-user-name  spf-link {margin-left:-10px}
.feed-item-container:hover .feed-item-header a, .yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup:hover{margin-left:10px;}
#yt-masthead-container{background:rgba(110, 110, 110, 1)!important;}
.branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box{border-bottom: 2px solid #A8A8A8;}
.guide-channels-list{width:auto!important;}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu{border:none;}
#appbar-guide-menu, .guide-flyout{background:none;}
#content-container, #body-container{padding-bottom:135px;}
.guide-toplevel,#watch-header,#action-panel-details,#watch-discussion,#watch7-sidebar-contents,.upload-import .starting-box,.initial-upload-page .upload-import .upload-sidebar,.initial-upload-page .upload-import #upload-hub-footer,#creator-page-content,#creator-page-sidebar,.c4-module-is-editable,#channel-feed-post-form,.branded-page-v2-secondary-col .yt-card,.yt-card,#player-api,.yt-uix-clickcard-card-border,.guide-flyout-visibility.flyout-shown,#guide-container,#watch-action-panels{box-shadow: 0px 0px 10px #000;}
#masthead-positioner{box-shadow: 0px 0px 5px #000;}
body #footer-container{background:rgba(110, 110, 110, 1)!important;text-align:center;}
.guide-notification-item,li.guide-section,.guide-flyout-visibility.flyout-shown, #guide-container,.guide-toplevel,.guide-notification-item, li.guide-section, .guide-flyout-visibility.flyout-shown, #guide-container, .guide-toplevel, .guide-notification-item, .guide-flyout-visibility.flyout-shown, #guide-container, .guide-flyout{background:#fff!important;}
.guide-section-separator{border:none; margin:none;}
li.guide-section h3{background: #cc181e;width:235px!important;margin-left: -5px!important;margin-right: -5px!important;padding-top:5px!important;margin-top:-10px!important;width: 230px!important;}
li.guide-section h3 a{color:#fff!important;text-decoration:none;}
.guide-sort-container{margin-bottom:-20px!important;}
#player{padding-top:5px;}
.guide-notification-item, li.guide-section, .guide-flyout-visibility.flyout-shown, #guide-container, .guide-flyout{background:none;}
.guide-channels-list{width:auto;}
#guide-container{width:auto; padding:0px!important;}
.guide-channels-list .guide-channel a{width:230px!important;}
.branded-page-v2-top-row #channel-subheader,#watch-appbar-playlist .control-bar{border-top:4px solid #cc181e;}
#footer-links-secondary a:hover,.pl-header .pl-header-title,.pl-video:hover .pl-video-title-link,.feed-item-container:hover .feed-item-header a, .feed-item-container:hover a.yt-user-name,.feed-item-container:hover .feed-item-header a,.c4-checklist-module .checklist-item:hover .checklist-item-title-text .todo-title-text,.actionable #watch-like.yt-uix-button-toggled .yt-uix-button-content,#watch7-sidebar .watch-sidebar-head{color:#fff!important;}
.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus{background:#cc181e!important;border-color:#cc181e!important;}
.video-extras-sparkbar-likes{background:#cc181e!important;}
.branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box,.guide-section-separator,.search-header{border-bottom:4px solid #cc181e!important;}
#gh-banner{margin-bottom:10px;}
#footer-links-secondary a, #footer-links-secondary .copyright{color:#000;}
.branded-page-related-channels-item .yt-uix-button-subscription-container,.branded-page-related-channels-item .yt-uix-button-subscription-container{margin:none;}
#yt-masthead #logo-container span.content-region{color:#000!important;font-weight: bold!important;}
.yt-thumb-196{width:195px!important;}
.pl-header-inlineedit .c4-module-is-editable:hover,.pl-header-inlineedit .c4-module-is-editable{box-shadow:none;}
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected{color:#fff!important;}
.watch-stage-mode #theater-background{background:#F3F3F3!important;}
