.yt-alert-default.yt-alert-info, .yt-alert-actionable.yt-alert-info, .yt-alert-naked.yt-alert-info .yt-alert-icon, .yt-alert-small.yt-alert-info,.autoplay-bar .checkbox-on-off input[type=checkbox]:checked+label{background:#000!important}
.yt-uix-button-default.yt-uix-button-toggled{background:#000!important;}
li.guide-section h3{color:#fff;margin-left:0px!important;}
.autoplay-bar .checkbox-on-off{color:#cc181e!important;font-size:12px!important;}
#watch7-sidebar-modules .autoplay-bar .watch-sidebar-head,#watch7-sidebar .watch-sidebar-head{font-size:14px;}
.yt-uix-button-default:hover{background:#cc181e!important;}
.watch-non-stage-mode .watch-playlist{width: 390px;margin-left: 1px!important;}
li.guide-section h3{color:#fff;text-transform: none;padding-left:10px!important;width:220px!important;}
.yt-uix-button-primary{color:#fff!important;text-align:center!important;margin-bottom:2px;}
#appbar-guide-menu{background:transparent!important;}
#guide-builder-promo-buttons,.guide-header p{text-align:center;}
#appbar-onebar-upload-group #upload-btn .yt-uix-button-content{color:#000;}
.yt-uix-button-default{background:rgba(200, 200, 200, 1)!important;}
.yt-uix-button-default:hover{background:#cc181e;}
.yt-masthead-picker-header{background:#cc181e;color:#fff!important;font-weight:bold;text-transform:capitalize;}
.yt-masthead-picker-header a{color:#fff!important;}
.autoplay-bar{line-height:20px;}
.autoplay-bar .checkbox-on-off input[type=checkbox]:checked+label{margin-top:3px;}
.yt-uix-button-opacity{opacity: 1;}
.autoplay-bar .autoplay-info-icon{height: 18px!important;}
#watch7-sidebar .watch-sidebar-section{margin:0px}
.yt-uix-simple-thumb-related:hover{margin-left:10px;}
#watch7-sidebar .video-list-item a:hover .title{color:#cc181e;margin-left:10px;}
.pl-video:hover .pl-video-title-link{color:#cc181e!important;text-decoration: none;}
.pl-message-panel .panel-content{margin-top:4px;}
.pl-header .privacy-icon-present .pl-header-title,.yt-uix-button-primary, a.yt-uix-button{color:#cc181e!important;}
#watch7-sidebar .watch-sidebar-head,#watch7-sidebar .video-list-item a:hover:visited .title,.shelf-item .branded-page-module-title .branded-page-module-title-text{color: #cc181e!important;}
#watch-related{margin-bottom:10px;}
.yt-card.yt-card-has-padding.yt-uix-expander .yt-uix-button-expander{background:rgba(200, 200, 200, 1);color:#000;}
.yt-card.yt-card-has-padding.yt-uix-expander .yt-uix-button-expander:hover{background:#cc181e;}
.watch-sidebar-head{text-transform:uppercase;}
 .yt-lockup:hover a{text-decoration:none;color:#cc181e!important;}
.feed-item-container:hover a.yt-user-name{margin:0px;}
#appbar-nav .epic-nav-item-heading,.epic-nav-item-heading{border-bottom:4px solid #cc181e;text-transform: uppercase;font-weight: bold;font-size: 13px!important;}
.yt-uix-button-primary, a.yt-uix-button{font-size:12px!important;color:#333!important;}
#watch7-sidebar .watch-sidebar-head, .shelf-item .branded-page-module-title .branded-page-module-title-text{font-size: 15px;}
.yt-uix-button-subscribe-branded .subscribe-label,.guide-item:hover{color:#fff!important;}
.yt-uix-button-subscribed-branded .subscribed-label{color:#fff;font-weight:bold;}
#appbar-guide-button-notification-check, .appbar-guide-notification{background:#cc181e!important;}
.actionable #watch-like.yt-uix-button-toggled .yt-uix-button-content,.watch8 #action-panel-details a{color:#cc181e!important;}
#footer-links-secondary a:hover,#footer-links-primary a:hover{text-decoration:none;color:#fff!important;}
.yt-pl-thumb .sidebar{width: 30%;background: rgba(0, 0, 0, 0.5);}
.yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus,.yt-uix-button-subscribed-branded+.yt-uix-subscription-preferences-button{background:#e62117!important;color:#fff!important;border:none!important;}
#watch7-sidebar-contents{min-height: initial!important;}
.guide-section{margin-top:-5px!important;}
.feed-item-container:hover a.yt-user-name{color:#cc181e!important;}
.yt-lockup-meta a:hover{text-decoration:none;color:#cc181e!important;}
.yt-lockup-title:hover a{text-decoration:none;color:#cc181e!important;}
.yt-uix-expander-collapsed .multirow-shelf-expander:hover, .multirow-shelf-collapser:hover{background:#cc181e!important;}
.yt-uix-button-subscribed-branded.hover-enabled:hover .unsubscribe-label{color:#333!important;font-weight: bold;}
.yt-uix-button{border:none!important;}
#watch7-sidebar .watch-sidebar-separation-line{border-bottom:4px solid #cc181e!important;margin-top: 10px;}
#watch8-action-buttons{border:none!important;}
.yt-lockup.yt-lockup-tile .yt-lockup-thumbnail{text-align:center!important;}
.shelf-annotation{font-weight: bold!important;color: #000!important;}
.browse-list-item-container .branded-page-module-title .video-thumb{margin:0px!important;}
.pl-video:hover .pl-video-owner a{color:#cc181e!important;}
.watch-stage-mode #player, .watch-stage-mode #placeholder-player{margin-top:0px;}
