css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("devcenter.heroku.com") {
  #sheet {
     background-color: #252530;
  }
  
  #home-page .main {
    background: unset;
  }
  
  .container, body {
    background-color: #231933;
    border-radius: 5px;
    padding-bottom: 5px;
  }
  
  table th {
    background: -moz-linear-gradient(center top , #303035, #121212) repeat scroll 0% 0%;
  }
  
  table, table th, table tr, table td, table td:last-child, table th:last-child {
    border-color: #000;
  }
  
  article, .nav-search input {
     background-color: #101010;
     color: #DDD;
  }
  
  article .heavy-header, article .contribution, #home-page .listing h2 {
    background: -moz-linear-gradient(center top , #020202, #222222) repeat scroll 0% 0% transparent;
    border-bottom: unset;
    box-shadow: 0px 1px 1px #201320 inset;
    color: #DDD;
    text-shadow: 0px 1px 1px #101010;
  }
  
  article .heavy-header h1, article h1.doc, #home-page .main h1 {
    color: #DDD;
    background-color: transparent;
    text-shadow: 0px 1px 1px #111;
  }
  
  article h2, article h4 {
    color: #CCC !important; 
  }
  
  article .warning, article .deprecated {
    background-color: #332005;
    color: #B2A283;
    border-color: #1F1315;
  }
  
  article .note {
    background-color: #212A43;
    color: #A4A2B7;
    border-color: #151923;
  }
  
  article .callout {
    background-color: #303030;
    color: #9FA2AA;
    border-color: #151515;
  }
  
  .article-extras {
    background-color: #1A1A1A;
    border-color: #AAA;
  }
  
  .article-extras h2, #home-page .listing ul {
    background: unset;
    background-color: #1B1B1B;
    color: #DDD;
  }
  
  code {
    background-color: #30363C;
    border-color: #323E16;
    color: #DDD;
  }
  
  .CodeRay pre {
    background: unset;
    background-color: #232E36 !important;
    border-color: #181818;
  }
  
  .secondary-nav, ul.secondary-nav a:link, ul.secondary-nav h2 {
    background-color: #20262C;
    color: #CDCDCD;
    text-shadow: 0px 1px 1px #333;
  }
}
