css:
@-moz-document domain("www.furaffinity.net") {
/* FA Nocturnal Theme by iErrored (www.furaffinity.net/user/ierrored) */
/* You can also find me on Weasyl @ www.weasyl.com/~Taw */
/* For installation details please view my profile above! */
/* Version 3 - Updated April 9th, 2013 */
/* Based off of the Darker FA user CSS by http://www.furaffinity.net/user/chazcatrix/ */
/* Any questions or concerns? Post in my submission or on my profile page! */
/* Please note that my knowledge on CSS is limited but I'll try and fix any bugs if you find any~ */

body, body>div, hovertable_body, .innertable, .logo, .menu li ul li{ /* BG Colours */
background-color: #303030 !important;
}

.block-banners.clearfix { /* Header overhaul */
background-color: #202224;
border: 1px solid #444444;
border-top: none;
box-shadow:0px 1px 0px #000000;
-moz-box-shadow:0px 1px 0px #000000;
-webkit-box-shadow:0px 1px 0px #000000;
padding-top: 30px;
padding-left: 15px;
margin: 0 auto;
margin-bottom: 5px;
}

.footer { /* Footer overhaul */
background-color: #202224;
border: 1px solid #444444;
box-shadow:0px -1px 0px #000000;
-moz-box-shadow:0px -1px 0px #000000;
-webkit-box-shadow:0px -1px 0px #000000;
margin-top: 25px;
padding-top: 10px;
}

.footer a:hover { 
color: #030303 !important;
}

ul.dropdown li a:hover { /* Navigation mouseover font */
color: #FFFF66 !important;
background-color: #303030 !important;
} 

ul.dropdown li:hover > a.dir { /* Navigation mouseover font */
color: #FFFF66 !important;
} 

table, tr, td, th, body { /* Site-wide font swap */
font-family:"Trebuchet", sans-serif !important;
}

ul.dropdown, ul.dropdown li, ul.dropdown ul { /* Navigation font swap */
font-family:"Trebuchet", sans-serif !important;
}

.alt1, .alt2, .cat, #admin_notice_do_not_adblock td { /* Tweak backgrounds */
background-color: #202224 !important;
}
.lead, .cat, .tab a, .tab u { /* Remove borders on profile nav tabs and such */
    border: none !important;
}

table.block-menu-top { /* Fixed position navbar with transparency */
position:fixed;
z-index:1000;
opacity:0.85;
}

ul.dropdown > li { /* Navbar size reduction and font testing */
line-height:1em !important;
font-family: 'Arial', sans-serif;
}

.container-comment { /* Submission comment shadows and such */
background-color: #202224;
border-color: #303030 !important;
-moz-box-shadow: 0px 1px 4px #000000;
-webkit-box-shadow: 0px 1px 4px #000000;
box-shadow: 0px 1px 3px #000000;
}

*[id*='shout-']  { /* Shout comment shadows */
-moz-box-shadow: 0px 1px 3px #000000;
-webkit-box-shadow: 0px 1px 3px #000000;
box-shadow: 0px 1px 3px #000000;
}

#news { /* News fix to match theme */
border: 1px solid #444444 !important;
background-color: #431B1B !important;
}

input#chars_left.button {
padding-left: 10px !important;
}

#submission .prev, #submission .next { /* More rounded buttons for prev and next */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:0px solid #ffffff !important;
box-shadow:0px 1px 1px #000000;
-moz-box-shadow:0px 1px 1px #000000;
-webkit-box-shadow:0px 1px 1px #000000;
border-radius:2px 2px 2px 2px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
width:55px;
}

.container-comment td.cat a { /* Comment Reply Buttons */
color:#ffffff;
background-color:#1a1b1c !important;
border:1px solid #444444 !important;
border-radius:2px 2px 2px 2px;
-moz-border-radius:10px 10px 10px 10px;
-webkit-border-radius:10px 10px 10px 10px;
margin-right:15px !important;
}

.listbox { /* Search button CSS and Dropdowns */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:2px 2px 2px 2px;
-moz-border-radius:2px 2px 2px 2px;
-webkit-border-radius:2px 2px 2px 2px;
}

div#messagecenter-submissions div.actions .remove-nuke, div#messagecenter-other fieldset h3 input, .personal-messages .priority_high, .viewmessage .priority_high, a.inline-button.red { /* All red buttons */
color:#ffffff;
background-color:#990E0E !important;
border:0px solid #ffffff;
box-shadow:0px 1px 0px #000000;
-moz-box-shadow:0px 1px 0px #000000;
-webkit-box-shadow:0px 1px 0px #000000;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

div#messagecenter-submissions div.actions .remove-checked, div#messagecenter-other ul.message-stream li.section-controls input.remove,
div#messagecenter-other div.global-controls input.remove, div#messagecenter-other #messages-watches ul.message-stream li.section-controls input.remove, .personal-messages .priority_medium, .viewmessage .priority_medium, a.inline-button.blue { /* All blue buttons */
color:#ffffff;
background-color:#0E4199 !important;
border:0px solid #ffffff;
box-shadow:0px 1px 0px #000000;
-moz-box-shadow:0px 1px 0px #000000;
-webkit-box-shadow:0px 1px 0px #000000;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

.blue, .red { /* Commission Tab Button Text Color */
color: #ffffff !important;
}

.personal-messages .priority_low, .viewmessage .priority_low { /* Green low priority note button */
color:#ffffff;
background-color:#2A990E !important;
border:0px solid #ffffff;
box-shadow:0px 1px 0px #000000;
-moz-box-shadow:0px 1px 0px #000000;
-webkit-box-shadow:0px 1px 0px #000000;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

div#messagecenter-submissions div.navigation .oldest, div#messagecenter-submissions div.navigation a, div#messagecenter-submissions div.navigation .newest { /* Oldest and newest sort buttons */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

.button,
#sessions-form input.logout-selected, #browse input.active, div#messagecenter-submissions div.navigation .active, input.button.blue
 { /* Black generic rounded buttons */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:0px solid #ffffff;
box-shadow:0px 1px 0px #000000;
-moz-box-shadow:0px 1px 0px #000000;
-webkit-box-shadow:0px 1px 0px #000000;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
outline:none !important;
font-family: "Trebuchet", sans-serif;
}

.forminput
 { /* Dropdown fix */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:2px 2px 2px 2px;
-moz-border-radius:2px 2px 2px 2px;
-webkit-border-radius:2px 2px 2px 2px;
-moz-appearance: none !important;
}

.button:focus {
  outline: 1px solid #697CC1 !important;
}

div#messagecenter-submissions div.navigation .more { /* Submission more button */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:10px 10px 10px 10px;
-moz-border-radius:10px 10px 10px 10px;
-webkit-border-radius:10px 10px 10px 10px;
}

.textbox, .textarea, textarea { /* Textboxes, needs work still, some stick out slightly */
color:#ffffff !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

#JSMessage {
color:#ffffff;
-moz-appearance: none !important;
background-color:#1a1b1c !important;
border:1px solid #444444;
border-radius:5px 5px 5px 5px;
-moz-border-radius:5px 5px 5px 5px;
-webkit-border-radius:5px 5px 5px 5px;
}

.tab a, .tab u { /* Rounded profile tabs, needs work */
box-shadow:0px -1px 0px #444444;
-moz-box-shadow:0px -1px 0px #444444;
-webkit-box-shadow:0px -1px 0px #444444;
border-radius:3px 3px 0px 0px;
-moz-border-radius:3px 3px 0px 0px;
-webkit-border-radius:3px 3px 0px 0px;
}

.lead { /* Fancy pattern on profiles and banners */
background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAUElEQVQYV2NkYGAwBuKzQAwDID4IoIgxIikAMZE1oRiArBDdZBSNMIXoJiFbDZYDKcSmCOYimDuNSVKIzRNYrUYOFuQgweoZbIoxgoeoAAcAEckW11HVTfcAAAAASUVORK5CYII=);
}

.tab a, .tab u { /* BG Color */
background-color: #202224 !important;
}

.maintable, hovertable, hovertable_head, .container-comment TH {
    background-color: #444444 !important;
}

center.flow b.r-adult s>a, center.flow b.r-adult s>a:hover{
    border: 2px solid #ca2121 !important;
}

center.flow b.r-mature s > a, center.flow b.r-mature s > a:hover {
    border: 2px solid #697CC1 !important;
}

center.flow img, center.flow.messagecenter b, center.flow s > a{
    border-radius: 0 !important;
}

.header_bkg {
    border-bottom: 1px solid #444444 !important;
}

ul.dropdown li:hover > a.dir {
    background-color: #444444 !important;
}

ul.dropdown ul { /* Transparency on dropdowns on navigation */
    background-color: #202224 !important;
    border: 1px solid #444444 !important;
    border-top: none !important;
    border-radius: 0 0 4px 4px;
    opacity:0.85;
}

#description_popup { /* Transparency on description mouseovers */
    background-color: #202224 !important;
    border: 1px solid #444444 !important;
    border-radius: 4px 4px 4px 4px;
    opacity:0.90;
}

#my-username, #page-commission-info .types-table .info dt.open { /* Navbar username color and commission header colors */
    color: #aaff00 !important;
}

.container-comment td.cat { /* Hide bottom of comments' background */
border: 0;
background: none !important;
}

#logout-link {
    color: #D40000 !important;
}

.commissionentrycontainer2 {
    padding-bottom: 5px !important;
}

#page-commission-info #addedit-form #cancel {
top: 2px !important;
}

#page-commission-info .types-table tr.even th, #page-commission-info .types-table tr.even td {
background-color: #303030 !important;
}

center.flow b.r-adult s>a, center.flow b.r-adult s>a:hover { /* Adult red Border Tweak - remove if this chunk of code to make it back to default */
border-style: solid;
border-width: 0 2px 2px 0 !important;
overflow: hidden;
text-indent: -999em;
}

center.flow b.r-mature s > a, center.flow b.r-mature s > a:hover { /* Mature blue Border Tweak - remove if this chunk of code to make it back to default */
border-style: solid;
border-width: 0 2px 2px 0 !important;
overflow: hidden;
text-indent: -999em;
}

.footer:after /* Skin credits */
{
content: "| Nocturnal theme by iErrored (www.furaffinity.net/user/ierrored)"
}
}
