@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guerretribale.fr") {


    a{color:#303030;}
    a:hover{color:#555555;}
    .forum.selected a,.modemenu td.selected a{color:#f00;}
   .forum.selected,.forum,.shared_forum{border:1px solid #fff;background:#0f0f0f;padding:0.4em;}
.forum a{color:#f00;}
.forum{border:1px solid transparent;}
    .forum:hover{border:1px solid #f00;color:#fff;}
.forum:hover a{color:#fff;}
    .shared_forum{background:#727272;color:#fff;}
    .shared_forum a{color:#fff;}
   
    .modemenu td:hover{background:#303030;}
    .modemenu td:hover a{color:#FF0400}
    .btn,.btn-default{
        background: #111;
        color:#fff;
        border-radius:0;
        border:none;
        padding:1em;
    }
    .btn:hover,.btn-default:hover{
        background: #222;
        text-shadow:0em 0em 0.3em #fff;
        color:#fff;
    }
    #show_outgoing_units .vis tr:hover td,#show_outgoing_units .vis tr:hover td a{background:#303030;color:#fff}
   .top_shadow{display:none;}
    .build_options .float_right{}
    #menu_row > .menu-item,.top_bar,#menu_row > td.menu-item > a,.top_bar .bg_left,.top_bar .bg_right{background:#0f0f0f;}
    #menu_row > td.menu-item > a{background:#0f0f0f;
        height:40px;left:0px;margin-left:-2px;}
    #topContainer{top:-2px;}
    
    
    #menu_row > .menu-item:hover,#menu_row > td.menu-item:hover > a{background:#555;}
    

    
    
    .menu_column tr .menu-column-item a,.menu_column tr .menu-column-item,.menu_column tr:first-child .menu-column-item a,.menu_column tr:first-child .menu-column-item, .menu_column tr:first-child .menu-column-item a,#topdisplay .menu_column .menu-column-item a,#topdisplay .menu_column tr:first-child .menu-column-item a{background:#333;}
    
        .menu_column tr .menu-column-item a:hover,.menu_column tr .menu-column-item:hover,.menu_column tr:first-child .menu-column-item a:hover,.menu_column tr:first-child .menu-column-item:hover,#topdisplay .menu_column .menu-column-item a:hover,#topdisplay .menu_column tr:first-child .menu-column-item a:hover{background:#151515;color:#000;}
    
#topdisplay .menu_column .menu-column-item a, .menu_column .menu-column-item a {left:-22px;}    
    #topTable .menu_column a:hover {color:#c00}
    .menu_column tr .bottom {display:none;}
    
   
     body{background:#111; }
    
