css:
@-moz-document domain("ask.metafilter.com")
{
	body
	{
		background-color:#fafffb !important;
		background-image:url(data:image/png;base64,) !important;
		color:#000 !important;
	}
	
	.side,.tags
	{
		background-color:#d0e9d5 !important;
		border:4px solid #afd3b7 !important;
		-moz-border-radius:15px !important;
	}
	
	.sidebar
	{
		background-color:#d0e9d5 !important;
		border:none !important;
	}
	
	.best
	{
		background-color:#cae5d0 !important;
		-moz-border-radius:15px !important;
		padding:8px !important;
	}
	
	div#menufooter
	{
		background-color:#ededff !important;
	}
	
	div#bottomline
	{
		background-image:none !important;
		background-color:transparent !important;
	}
	
	a:link
	{
		color:#55bd69 !important;
	}
	
	a:visited
	{
		color:#398046 !important;
	}
	
	#maintab li.selected a
	{
		background-color:#d2ead7 !important;
		-moz-border-radius:11px 11px 0 0 !important;
	}
	
	#maintab li a
	{
		background-color:#b1cdb7 !important;
		-moz-border-radius:11px 11px 0 0 !important;
	}
	
	#maintab li a:hover
	{
		background-color:#eaf6ec !important;
		-moz-border-radius:11px 11px 0 0 !important;
	}
	
	.button
	{
		background:#b8dfc0 !important;
	}
	
	input[style="width: 200px; font-size: 18px;"]
	{
		margin-left:50px !important;
		padding-bottom:2px !important;
		padding-top:2px !important;
	}
	
	#yellowbar
	{
		font-size:60% !important;
	}
	
	.posttitle
	{
		color:#000 !important;
		background-color:#b1cdb7 !important;
		-moz-border-radius:15px !important;
		width:50% !important;
		padding:8px !important;
	}
	
	.smallcopy
	{
		color:#5F6475 !important;
	}
	
	h2
	{
		background-color:#b1cdb7 !important;
		-moz-border-radius:15px !important;
		width:30% !important;
		padding:8px !important;
	}
	
	div[style="border: 1px solid rgb(60, 90, 63); margin: 0px 10px 20px 0px; padding: 2px; width: 486px;"],div[style="border: 1px solid rgb(60, 93, 64); margin: 0px 10px 10px 45px; padding: 2px; width: 736px;"],div[style="background: transparent none repeat scroll 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; margin-bottom: 20px;"],p[style="padding: 3px; margin-top: 8px; text-align: center; font-size: 10px; width: 115px;"],iframe,div#menu div[align="center"],p[style="padding: 3px; margin-top: 4px; text-align: center; font-size: 9px; width: 160px;"]
	{
		display:none !important;
	}
	
	.bestleft
	{
		border-color:#B1CDB7 !important;
	}
	
	#footer,#topline
	{
		background-image:url(data:image/png;base64,) !important;
	}
	
	.reason,.mycomments
	{
		background-color:#B1CDB7 !important;
		-moz-border-radius:15px !important;
		padding:8px !important;
	}
	
	.darker,#commentform .comments
	{
		background-color:#B1CDB7 !important;
	}
}
