css:
@-moz-document domain("www.youtube.com") {
#behavior-id-guide-playlists-section,
#inproduct-guide-modal,
#logo-container,#alerts,
hr,
#footer-container,
#c4-header-bg-container,
.lohp-newspaper-shelf .branded-page-module-title,
#appbar-onebar-upload-group,
.cookie-alert,
#footer-container,
.branded-page-v2-secondary-col {
    display: none !important;
}
body,
input,
button,
textarea,
select,
.yt-ui-ellipsis {
    line-height: 1.1!important;
    font-family: arial, sans-serif!important;
    font-size: 12px!important;
}
.yt-uix-button-has-icon:before,
a.yt-uix-button:before {
    vertical-align: top!important;
}
#yt-masthead-account-picker {
    margin-right: 50px!important;
}
#yt-masthead-container,
#guide-container,
h3,
.guide-item {
    padding: 0!important;
}
.browse-items-primary>li,
#browse-items-primary>li,
.primary-header-contents.clearfix,
#content-container,
#body-container {
    padding-top: 0!important;
    padding-bottom: 0!important;
}
h3,
.guide-item *,
.browse-items-load-more-button {
    margin: 0!important;
}
.guide-item,
.yt-valign:before,
a.yt-uix-button.yt-uix-button-epic-nav-item,
button.yt-uix-button-epic-nav-item,
.epic-nav-item,
[id*='alert'],
[id*='alert'] *,
#guide,
#guide [class^='yt-'],
#alerts [class^='yt-'],
#header [class^='yt-'],
#player [class^='yt-'],
#footer-container [class^='yt-'],
.display-name,
#content [class^='epic'],
#content [class^='epic'] *,
.formatted-video-count-label,
.formatted-video-count-label *,
h3 {
    height: auto !important;
    line-height: inherit !important;
}
#masthead-appbar,
#appbar-content,
#appbar-nav * {
    height: 20px !important;
    line-height: 18px !important;
}
#masthead-positioner-height-offset {
    height: 50px!important;
}
.yt-dialog-base,
.yt-uix-overlay-base {
    -moz-user-select: all;
    z-index: 3 !important;
}
}

@-moz-document url-prefix(https://www.youtube.com/results?){
#page{
  transition-property: none!important;
}
#page,
.result-item-padding{
  padding:0!important;
}
.result-item-padding:hover{
  background-color: rgba(0,0,0,.1)!important;
}
.yt-lockup-thumbnail{
  width: auto!important;
}
#yt-masthead-user .video-thumb{
  visibility: hidden!important;
}
}
