css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*Demo: http://mediacrush.net/In-9PKWf5r94.webm*/

.tab-text {
      -moz-box-ordinal-group: 1 !important;
      margin-left: 2px !important;
      margin-right: -2px !important;
    }
.tab-throbber {
    -moz-box-ordinal-group: 2 !important;
    margin-right: 5px !important;
    }
.tab-close-button {
    opacity: 0 !important;
    -moz-box-ordinal-group: 2 !important;
    position: fixed !important;
    margin-right: -18px !important;
    }
.tabbrowser-tab[selected="true"][pinned="true"]:hover .tab-close-button,

.tabbrowser-tab:not([pinned]):hover .tab-close-button {
    display: -moz-box !important;
    opacity: 0 !important;
    margin-right: -18px !important;
    }


#TabsToolbar {margin-left: -3px;}
#titlebar {margin-top: -2px !important;}

#FindToolbar {
	position: fixed;
	right: 7.6%;
	top: 1%;
	transition: none;
	opacity: 0.7;
 }

.findbar-find-fast, .findbar-closebutton, .findbar-find-next.tabbable, .findbar-find-previous.tabbable, .findbar-highlight.tabbable {display: none;}

.findbar-textbox.findbar-find-fast {display: inherit;}

.findbar-textbox.findbar-find-fast {
	width: 250px;
	height: 26px;
 }

.checkbox-check, .checkbox-label-box {display: none;}

.findbar-textbox.findbar-find-fast {
	margin-top: -3px;
	border-style: none;
 }

.tabbrowser-tab:not(:-moz-lwtheme), .tabs-newtab-button:not(:-moz-lwtheme) {background-image: linear-gradient(to top, rgba(200, 200, 200, 0.4) 1px, transparent 1px), linear-gradient(transparent, rgba(114, 114, 114, 0.1) 1px, rgba(200, 200, 200, 0.2) 80%, rgba(200, 200, 200, 0.2)), linear-gradient(rgb(200, 200, 200), rgb(250, 250, 250));}

.tabbrowser-tab {
	margin-bottom: 0px !important;
	margin-left: 0px !important;
 }

#navigator-toolbox {margin: -2px}

.tabbrowser-tab, .tabs-newtab-button {
	-moz-appearance: none;
	background-color: transparent;
	background-attachment: scroll, scroll, scroll;
	background-clip: border-box, border-box, border-box;
	background-origin: border-box;
	background-position: 1px 2px;
	background-size: calc(100% - 2px) calc(100% - 2px);
	background-repeat: no-repeat;
	margin-bottom: 2px;
	padding: 0px 0px 0px;
	border: 0px;
 }

#identity-box {display: none;}

#notification-popup-box {display: none;}

#page-proxy-favicon {display: none;}

#urlbar-reload-button {display: none !important;}

.urlbar-history-dropmarker {display: none !important;}

#urlbar-icons {display: none;}

.tabs-newtab-button {opacity: 0 !important;}

.tabbrowser-tab[fadein]:not([pinned]) {
	min-width: 100px !important;
	max-width: 100px !important;
	margin-right: -2px !important;
 }

.tabbrowser-tab[fadein][pinned] {
	min-width: 28px !important;
	max-width: 43px !important;
	margin-right: -2px !important;
 }

.tabbrowser-tab[fadein]:not([pinned]):not([selected]) {
	min-width: 100px !important;
	max-width: 100px !important;
	margin-right: -2px !important;
 }

.tabbrowser-tab[fadein][pinned]:not([selected]) {
	min-width: 28px !important;
	max-width: 43px !important;
	margin-right: -2px !important;
 }

#urlbar, #urlbar-container {
	min-width: 100px !important;
	max-width: 250px !important;
	min-height: 26px !important;
	max-height: 26px !important;
	margin-left: -3px !important;
	padding-left: 4px !important;
}
.findbar-textbox.findbar-find-fast[focused] ~ #urlbar{display: none !important;}

#urlbar[focused] {opacity: 0.7 !important;}

#urlbar:hover {opacity: 0.7 !important;}

#urlbar {
	opacity: 0 !important;
	box-shadow: none !important;
	border: none;
	border-radius: 0px;
 }

#appmenu-button-container {display: none !important;}

.tabbrowser-tab[selected="true"] {
  background-image: linear-gradient(#e3e3e3, #e3e3e3) !important;
  color: grey !important;
}
.tabbrowser-tab[selected="true"]:hover {
  background-image: linear-gradient(#e3e3e3, #e3e3e3) !important;
  color: grey !important;
}
.tabbrowser-tab {
  background-image: linear-gradient(#a0a0a0, #a0a0a0) !important;
  color: #555555 !important;
  border-style: solid !important;
  border-right: 1px !important;
  border-radius: 2px !important
}
.tabbrowser-tab:hover {
  background-image: linear-gradient(#acacac, #acacac) !important;
  color: #555555 !important;
}
    .tab-icon-image {
      -moz-box-ordinal-group: 3 !important;
    }
    .tabbrowser-tab{
border-right-style: solid !important;
border-width: 1px !important;
border-color: grey !important;
}
.tabbrowser-tabs, #urlbar-container{background-color: #a0a0a0 !important;}
