css:
/* version 0.1.4 */
@-moz-document domain("saivryth.org") {

body, table, tr, td, th, p, span { 
  background: white;
}

body, body > table {
  width: 8in;
  max-width: 8in;
}

table, tr, th, td {
	border: 1px solid black;
  	border-collapse: collapse;
}

table {
 width:100%;
 table-layout:fixed; 
}

td table {
	width: 100%;
 /* 	border-style: solid;
    border-right-color: #ff0000;
  	border-bottom-color: #ff0000;
  	border-left: none;
  	border-top: none;*/
}

td table tr, 
td table th 
/*td table td*/ { 
  border: none; 
  border-collapse: none; 
}

td { 
	width:auto;
	/*overflow:hidden;*/
	white-space:normal;
	word-wrap: break-word;
	padding: .25em; 
	border-style: solid;
    border-right-color: #000;
  	border-bottom-color: #000;
  	border-left: none;
  	border-top: none;
}

body, b, i, p, div, h1, h2, h3, h4, h5, font,
td font,
p i b font,
p font b font i {
	color: #000;
}

a { color: #000 }

p[align="center"],
p[align="center"] b,
p[align="center"] b i,
p[align="center"] b i font {
	margin: 0;
    padding: 0;
  	border: 0;
}
p[align="center"] b i font a { font-size: 1.25rem; }

td img {
 max-width: 100%; 
}

.menu { display:none; }

/* end moz bit */
}

@-moz-document url("http://saivryth.org/kingdoms_of_saivryth.htm") {
table.tbod { 
  border: black
}

table.tbod td {
  width: 25%;
}
}
