css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodgame.ru") {
#wrapper { top:0px; }

#adinner {display:none;}

#chat .premium a.nick:before {
    font-size: 85%;
    margin-right: 1px;
    display: inline;  
    
}
#chat .premium a.nick:after {
    font-size: 85%;
    margin-right: 1px;
    display: inline;
    content: "";
    font-family: "icomoon";
}
body {background:#000000 !important;}

    #partners{display:none;}
    #partners2{display:none;}

#chat #content #messagesframe #messages div.msg.delete_mess{text-decoration:underline}
    #content .streamer-block.streamer-bg img {display: none}
    #content-right img{display: none}
/*.widget .announce-block .bg-block {height:0px}
    .widget .announce-block .blured-block{top:0px}*/
    .widget .announce-block .blured-block{display: none}

    #menu ul#menu-links li a {height: 23px;}
    #menu, #menu .content {line-height: 16px;height: 30px;}
    .social-list {margin-top: 4px;}
    div.swiper-container div div {width:125px !important;}
    div.swiper-container div div a img{width:100px;}
    .favorite-game swiper-slide swiper-slide-visible{height:220px;}
    .favorite-game swiper-slide {height: 220px;}
    #favorite-games-wrap .arrow {height:220px;}
/*    swiper-wrapper favorite-games-list{height: 220px !important;}*/
    div.swiper-container div.swiper-wrapper {height: 220px !important;}    
    
    .block-header {padding: 10px 10px 10px;}
    
    .h2, #menu ul#menu-links li a, .tabs li a, .a2 {text-transform: capitalize !important;}
    
    .channel-list .channel {width: 150px;}
    .channel-list .channel .link .prev{height:80px;}
    li.channel a div.prev{width:100%}
    
    #streams-header .block-header h2 .content-name {text-transform: lowercase;}
    
    .channel-list .channel .link.premium .info .subscribe {display: none;}
    .channel-list .channel .link .info .stats {text-shadow: 1px 1px 4px black;}
    
    #chat #content #messagesframe #messages div.msg.status {display: none;}
#favorite-games-wrap .favorite-games-list .favorite-game .game-title {white-space:normal;}    
}
