css:
@-moz-document url-prefix("http://www.nicovideo.jp/") {
#siteHeader {
  box-shadow: 0 1px 10px rgba(0,0,0,.5);
  width: auto !important;
  right: 0;
}
#siteHeader #siteHeaderInner {
  background: transparent;
}

#searchFormWrap {
  background-color: #fcfcfc;
  box-shadow: 1px 1px 2px rgba(0,0,0,0.2);
}
#searchWord {
  padding-left: 6px;
}

#NewServiceList ul {
  padding: 0;
}
#NewServiceList li {
  height: auto;
}
#NewServiceList li a {
  line-height: 24px;
  padding-top: 0;
  padding-bottom: 0;
}
#NewServiceList li:hover {
  background-color: #444455;
  color: #ffffff;
}
#NewServiceList li:last-child a {
  padding-bottom: 5px;
}
#NewServiceList .service_main li a {
  border-left: 5px solid transparent;
  padding-left: calc(5px + 28px);
}

.title_index {
  border-radius: 2px 2px 0 0;
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/video_top"), url-prefix("http://www.nicovideo.jp/ranking") {
nav.mainMenu,
div.rankingHeader {
  border: 0 !important;
}
nav.mainMenu>:not(.complementary) ul.navigation li {
  margin-right: 2px !important;
  border-radius: 4px 4px 0 0;
}
nav.mainMenu>:not(.complementary) ul.navigation li:not(.current) {
  background-color: #eeeeee;
  font-weight: bold;
}
nav.mainMenu>:not(.complementary) ul.navigation a {
  padding: 8px 18px !important;
}

nav.mainMenu>.inner>.navigation li:nth-of-type(1) {
  background-color: rgba(191, 191, 191, 0.8) !important;/*--TOP--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(2) {
  background-color: rgba(174, 0, 10, 0.5) !important;/*--エンタメ音楽--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(3) {
  background-color: rgba(243, 152, 0, 0.5) !important;/*--生活一般スポ--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(4) {
  background-color: rgba(218, 191, 0, 0.5) !important;/*--政治--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(5) {
  background-color: rgba(51, 149, 93, 0.5) !important;/*--科学技術--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(6) {
  background-color: rgba(0, 104, 183, 0.5) !important;/*--アニメゲーム--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(7) {
  background-color: rgba(29, 59, 136, 0.5) !important;/*--その他--*/
}
nav.mainMenu>.inner>.navigation li:nth-of-type(8) {
  background-color: rgba(165 ,91 ,154, 0.5) !important;/*--R１８--*/
}
nav.mainMenu>.inner>.navigation li.current {
  background-color: rgba(51, 51, 51, 1.0) !important;/*--選択中--*/
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/video_top") {

}
