css:
@document domain("huffingtonpost.com") {
/* Removing crap from the top */

#mast_date, #topnav_big_news_module, .social_list {
  display: none !important;
}

.social_search_mod, .new-topnav_second_row, #updated_and_search {
  display: none !important;
}
  

/* Removing crap from the hero content */
  
#featured_content {
    margin-top: 30px;
    margin-bottom: 20px;
}
  
.meta_links, .mod-follow {
    display: none !important;  
}
  
#right_col {
    margin-top: -15px;    
}
 
/* Removing crap from the bottom */
  
#footer {
    display: none !important;  
}

.footer_search {
    transform: scale(1.5,1.5);
  margin-bottom: 30px;
}

#wrapper {
    transform: translateY(655px) scale(1.1, 1.1);
}

#splash_block a {
  text-decoration: none;
}

#headline_image_container {
  margin-bottom: 20px; 
}


}
