@-moz-document domain("iccf.com") {

  body {
    background-color: #000000 !important;
    background-image:url(https://raw.githubusercontent.com/adversary-org/stylish-styles/master/iccf.com/images/chess-wallpaper.jpg);
    background-size: cover !important;
    background-position: left top !important;
    background-repeat:no-repeat !important;
    background-attachment: fixed !important;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
  }

  textarea {
    background-color: #000000 !important
  }

.ui-helper-hidden{
	display:none
}

.ui-helper-hidden-accessible{
	border:0;
	clip:rect(0 0 0 0);
	height:1px;
	margin:-1px;
	overflow:hidden;
	padding:0;
	position:absolute;
	width:1px
}

.ui-helper-reset{
	margin:0;
	padding:0;
	border:0;
	outline:0;
	line-height:1.3;
	text-decoration:none;
	font-size:100%;
	list-style:none
}

.ui-helper-clearfix:before,.ui-helper-clearfix:after{
	content:"";
	display:table;
	border-collapse:collapse
}

.ui-helper-clearfix:after{
	clear:both
}

.ui-helper-clearfix{
	min-height:0
}

.ui-helper-zfix{
	width:100%;
	height:100%;
	top:0;
	left:0;
	position:absolute;
	opacity:0;
	filter:Alpha(Opacity=0)
}

.ui-front{
	z-index:100
}

.ui-state-disabled{
	cursor:default!important
}

.ui-icon{
	display:block;
	text-indent:-99999px;
	overflow:hidden;
	background-repeat:no-repeat
}

.ui-widget-overlay{
	position:fixed;
	top:0;
	left:0;
	width:100%;
	height:100%
}

.ui-resizable{
	position:relative
}

.ui-resizable-handle{
	position:absolute;
	font-size:.1px;
	display:block
}

.ui-resizable-disabled .ui-resizable-handle,.ui-resizable-autohide .ui-resizable-handle{
	display:none
}

.ui-resizable-n{
	cursor:n-resize;
	height:7px;
	width:100%;
	top:-5px;
	left:0
}

.ui-resizable-s{
	cursor:s-resize;
	height:7px;
	width:100%;
	bottom:-5px;
	left:0
}

.ui-resizable-e{
	cursor:e-resize;
	width:7px;
	right:-5px;
	top:0;
	height:100%
}

.ui-resizable-w{
	cursor:w-resize;
	width:7px;
	left:-5px;
	top:0;
	height:100%
}

.ui-resizable-se{
	cursor:se-resize;
	width:12px;
	height:12px;
	right:1px;
	bottom:1px
}

.ui-resizable-sw{
	cursor:sw-resize;
	width:9px;
	height:9px;
	left:-5px;
	bottom:-5px
}

.ui-resizable-nw{
	cursor:nw-resize;
	width:9px;
	height:9px;
	left:-5px;
	top:-5px
}

.ui-resizable-ne{
	cursor:ne-resize;
	width:9px;
	height:9px;
	right:-5px;
	top:-5px
}

.ui-selectable-helper{
