css:
@-moz-document url-prefix("http://9gag.com/"), url-prefix("https://9gag.com/"), url-prefix("http://9gag.tv/") {
body, #container, .page,.badge-toolbar-pre, .post-afterbar-a.in-post-top, .horizontal-vote a.up span, section.block-subscribe form .field input, .section-nav, section.footer, section.section-control li p, section.modal, .loading a.btn.spin, input[type='text'], input[type='email'], input[type='password'], input[type='url']   {
	background-color: #444;
}

header#top-nav, a.back-to-top,  section.section-control .title, section.section-control .btn-container, span.toggle, .overlay-box-container .box {
	background-color: #222;
}

span.toggle .switch {
	background-color: #ee0005;
  	box-shadow: 0 0 0 1px #ee0005;
}

.visitor-function a.link:hover, .headbar-items a:hover, .overlay-box-container .box .close{
	color: #fff;
}

#container, section.block-feature-cover .info-container a, input[type='text'], input[type='email'], input[type='password'], input[type='url'], textarea, section#list-view-2 h2 a, section.section-control li p, .overlay-box-container .box h4   {
	color: #aaa
}  

input[type='text'], input[type='email'], input[type='password'], input[type='url'], textarea, a.back-to-top, section.section-control li, section.section-control .btn-container, section.section-control .title{
 	border: 1px solid #777;
}

section#list-view-2 article{
 	border-top: 1px solid #777;
}

.section-nav, header#top-nav, section#signup .social-signup{
 	border-bottom: 1px solid #777;
}

}

@-moz-document url-prefix("http://comment-cdn.9gag.com/") {
html, body, .post-comment, textarea  {
	background-color: #444;
}

a.cmnt-btn.grey  {
	background-color: #333;
}

a.cmnt-btn.grey:active{
	background-color: #222;
}
  
textarea, .content.badge-content{
	color: #aaa;
}
  
.username.badge-author-name.badge-normal, a.cmnt-btn.grey {  
  	color: #ccc;
}
textarea, a.cmnt-btn.grey{
 	border: 1px solid #777;
}

}
