css:
@-moz-document domain('www.tumblr.com') {

Body {

  background:url("http://i.imgur.com/pHaL3vQ.jpg") no-repeat fixed !important;

  background-color: rgba(0,0,0, 1) !important;

  background-position: 0% 0% !important;

  background-size: cover !important;

}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: rgba(0,0,0,.0) !important;
}
  .l-container.l-container--two-column-dashboard .left_column{
background-color: rgba(0,0,0,0) !important;
}




.tab_bar .tab.iconic .tab_anchor{
  background-color: rgba(4,0,30,.4) !important;
  border-radius: 20px !important;  
}

    
#xkit-control > a {
  background-color:rgba(4,0,30,.4) !important;
  border-radius: 20px !important;
}


#new_post_notice_container.tab_notice, .tab_notice {
color: #f75f59 !important;
background-color: #04001e !important;
}


.search_form_row {
    background-color: rgba(4,0,30,.3) !important;
}

.mceContentBody {
  background: rgb(255, 255, 255) !important; 
  color: rgb(68, 68, 68) !important; 
}

#new_post_label_text {
    color: rgba(87,84,85,1) !important;
}

#new_post_label_photo {
    color: rgba(220,108,82,1) !important;
}

#new_post_label_quote {
    color: rgba(243,161,66,1) !important;
}

#new_post_label_link {
    color: rgba(103,190,146,1) !important;
}

#new_post_label_chat {
    color: rgba(92,175,220,1) !important;
}

#new_post_label_audio {
    color: rgba(176,137,200,1) !important;
}

#new_post_label_video {
    color: rgba(130,138,147,1) !important;
}


#posts .post .post_content {
  background: transparent !important;
}


#posts .post {
  background: rgba(4,0,30,.4) !important;
  border-radius: 10px !important;
}


.post_footer {
    background-color: rgba(255,239,234,0) !important;
    border-radius: 20px !important;
}


.controls_section.user_list li .follow_list_item_blog .tumblelog_title{
  color: rgba(255,255,255,1) !important;
}

.controls_section.user_list li .follow_list_item_blog.has_follow_button:before{
visibility: hidden !important;
}


.post_notes{
  color: rgba(255,255,255,1) !important;
}

.tracked_tags {
background-color: rgba(4,0,30,1)  !important;
border-radius: 0px !important;
}



.popover_menu_item {
  background: rgba(4,0,30,1) !important;
  border-bottom: 1px solid #f75f59 !important;
  
}



.note_item {
    background-color: rgba(4,0,30,.7) !important;
    border-radius: 15px !important;
    border: hidden !important;
}


.post.fan_mail {
    color: #4d3337 !important;
}

.quote {
    color: #fff !important;
}


.link_text_container{
    background-color: #04001e !important;
}
  
  
  

.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(247,95,89,1)  !important;

}



.plexi.color.active.show {
  background-color: rgba(4,0,30,.3) !important;
}
.drop_target {
  background-color: rgba(4,0,30,.5) !important;
}
.drop_zone {
  border: solid 2px rgba(247,95,89,1) !important;
  border-radius: 2px !important;
}
#tumblelog_select > .txt {
  color: rgba(255,255,255,1) !important;
}
#tumblelog_select > .txt:hover,
.photo_field.drop_zone_container .drop_zone .url_button {
  color: rgba(255,255,255,1) !important;
}
.chrome.flat.close:hover,
.photo_field.drop_zone_container .drop_zone .url_button:hover {
  color: rgba(255,255,255,1) !important;
}
#tumblelog_select > .txt:hover:after,
.post_options_wrapper .cog:hover,
.chrome.blue.options {
  opacity: 1 !important;
}
#tumblelog_select > .txt:after,
.post_options_wrapper .cog {
  opacity: 1 !important;
}







.post_content DIV:last-child,BLOCKQUOTE{border-color:rgba(247,95,89,1)!important}


pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small{background:rgba(4,0,30,.5)!important;color:rgba(245,117,112,1)!important;font:bold .9em "arial",consolas,courier!important;white-space:pre-wrap!important;white-space:-moz-pre-wrap!important;white-space:-pre-wrap!important;white-space:-o-pre-wrap!important;word-wrap:break-word!important}


.return_to_top_icon{background: url('http://i.imgur.com/Ff4lNPR.png')!important;
width:40px!important;
height:40px !important;
position:absolute!important;
margin-top: -10px !important;
margin-left: -30px !important;}

.chat_line:nth-child(even),PRE{background-color:rgba(255,255,255,0)!important;
color:rgba(255,255,255,1)!important}


.answer_container,
.notes LI{background:rgba(4,0,30,1)!important;
}




li.selected, li.recessed, a.active, div.post_filter_tabs a {

  background: none !important;

}







.post.photo_post .photo, .post.photo_post .video_thumbnail {

 box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}


#content_top.content_cap,

#content_bottom.content_cap {

  visibility: hidden !important;

}

div#edit_post_controls {

  top: 20px !important;

}

#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  border-radius: 14px !important;

}

#auto_pagination_loader{

background: rgba(4,0,30,0) !important;
border-radius: 10px !important;


}




.post_content,
.new_post_label,

#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,


.post_controls A,

.post_info,

.post_info A,

.footer_links.with_tags.with_source_url A,

.footer_links A,

.footer_links,

.block,


#dashboard_controls A,

#dashboard_controls DIV SPAN,

.add_and_remove,

.notification.single_notification,

.notification,

.notification .hide_overflow A,

.notification.last_notification,

.notification.last_notification A,

.answer_container SPAN,

.notes,

.notes LI,

.notes_hide_link A,

.read_more_container,

#footer, #footer DIV A,

.post_question_asker,

.source_url,

.label_and_follow,

.follow, #search_query, #search_controls_button,

.posts, .followers, .messages, .drafts, .queue, .flat.no_hover DIV,

A[class^="category"],

a:link,

a[href^="http"],

a[href^="/edit"],

a[href^="/likes"],

a[href^="/new"],

a[href^="/tagged"],

a[href^="/login"],

a[href^="/docs"],

a[href^="/explore"],

a[href^="/mega-editor"],

.count, .customize, .settings,

a span[id^="note_link"],

.answer_container,

.no_posts_found,

.following, H1[id="page_heading"], div.l-content P, H1, p SMALL,

.notes_loader,

.small_links A .hide_overflow, .sorry P, .cell.lead_editor, .sidebar_nav_item A, .tag,

 H2, #posts .post .post_controls .reblog_count span{

  color: rgba(255,255,255,1) !important;

}
    
strong {
  font-weight: bold !important;
  color: #f75f59  !important;
}
    


a:link:hover,

A[href^="http"]:hover,

a[href^="/edit"]:hover,

a[href^="/likes"]:hover,

a[href^="/new"]:hover,

a[href^="/tagged"]:hover,

a[href^="/customize"]:hover,

a[href^="/explore"]:hover,

a[href^="/mega-editor"]:hover,

a[id^="ask_answer"]:hover,

a span[id^="note_link"]:hover,

.block_link:hover,

.following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,

SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover, #footer DIV STRONG:hover,

BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,

#posts .post .post_controls .reblog_count span:hover {

  color: rgba(247,95,89,1) !important;

}

.source_url_gradient, #toggle_reasons {

  display: none !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  border-bottom: solid thin rgba(64,64,64,1.0) !important;

}

.post_content P A,

.post_title A {

  color: rgba(255,255,255,1) !important;

}

.tag {color:rgba(247,95,89,1)!important;
}

.post_tags:after{
    visibility:hidden!important;
}
.post_permalink {
background: url('http://i.imgur.com/SFJquh6.png') !important;
width: 16px !important;
height: 16px !important;
}

.post_permalink:hover {
background: url('http://i.imgur.com/3yo8dDZ.png') !important;
width: 16px !important;
height: 16px !important;
} 

.post_full .post_permalink:before {
border-right: 16px transparent !important;
}

#tumblr_radar {display: none !important;}


#sidebar_footer_nav {
 display: none !important;
}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #fff !important;
  background-color: rgba(43,43,43,0)!important;
  border-radius: 10px !important; 
}



#right_column .controls_section li a:hover {

  color: #f75f59 !important;
  background-color: rgba(4,0,30,.3)!important;
  border-radius: 20px !important;
}





#left_column.followers {
    background: rgba(0,0,0,0) !important;
}


.left_column .white_pane {
    background: rgba(4,0,30,.4) !important;
}
    
  #left_column   {
    background: rgba(0,0,0,0) !important;
}   

.notifications_page .notification_pane {
    background: rgba(4,0,30,.4)  !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
    font-size: 15px !important;
    position: relative !important;
    letter-spacing: 0 !important;
}



.ui_notes .ui_note.is_friend {
    color: #fff!important;
    background-color: rgba(29,20,82,.4) !important;
    border-bottom: 1px solid #f75f59 !important;
    border-top: 1px solid #f75f59 !important;
    margin-top: -1px !important;
}


.ui_notes .date_header,.date_header {
    color: #fff !important;
    line-height: 22px !important;
    font-size: 11px !important;
    padding: 0 10px !important;
    background: #40377A !important;
}



#x1cpostage_remove_caption {
    background: #04001e !important;
}

#x1cpostage_caption {
    background: #04001e !important;
    color: #f75f59 !important;
}

#x1cpostage_replace {
    background: #04001e !important;
}

#x1cpostage_tags {
    background: #04001e !important;
    color: #f75f59 !important;
}

#x1cpostage_blog {
    background: #04001e !important;
    color: #f75f59 !important;
}




.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification {

  background: none !important;

  border: none !important;

}

.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification,

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  color: rgba(247,95,89,1) !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

    border-bottom: solid thin rgba(101,28,13,0) !important;

}

.notification.single_notification A:hover,

.notification.first_notification A:hover,

.notification A:hover,

.notification.last_notification A:hover,

.notification .hide_overflow A:hover {

  color: rgba(255,0,0,0) !important;

}



ol#posts li.notification.alt {

   background: rgba(4,0,30,.4) !important;
border-radius: 5px!important;


}





#content_top, #content_bottom{display:none !important;}





ol#posts li.notification {

   background: rgba(4,0,30,.6) !important;

   border-radius: 5px!important;



}







#left_column #posts .notification a {

                        color:#f75f59 !important;

                        border-bottom:0px !important;

                    }




div#right_column,

div#right_column:after {

  background: none !important;

}
}@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {body {background:none!important;}}
