css:
@-moz-document url-prefix("http://store.steampowered.com/") {
.discovery_queue_ctn, .steam_curators_ctn, #content_more, #content_callout, .home_smallcap_area, .recommended, #content_out,
#v6takeover, .content_loading {
    display: none !important;
}

body {
    background: url('http://i.imgur.com/ol0416W.png') !important;
}

.tab_item_details span.platform_img, .col.search_name .platform_img {
    opacity: 0.7 !important;
}

.tab_item_details {
    color: #555 !important;
}

body.v6 > div#global_header,div#global_header .content, body.v6 #global_header, body.v6 #global_header .content,
#store_nav_area .store_nav_bg, .searchbox, .home_page_gutter_block, .home_cluster_ctn {
    background: transparent !important;
}

div#global_actions {
    width: 342px;
}

#global_action_menu {
    margin-top: -13px;
}

.searchbox {
    border: 1px solid #9da7ae;
    border-radius: 5px;
}

.searchbox:hover, .searchbox:active, .page_background, .header_early_access, .page_header_ctn {
    background: transparent !important;
}

.store_nav .tab > span {
    color: white;
}

.main_cluster_content {
    height: inherit;
}

#store_header {
    margin-top: -40px;
}

.game_page_background {
    background: transparent !important;
}

.main_cap_desc {
    line-height: 18px;
}

.searchbox {
    border-color: #454545;
}

.searchbox input.default {
    color: #6e6e6e;
}

.store_nav .tab:hover > span, .store_nav .tab:hover, .store_nav .tab.focus > span, .store_nav .tab.focus {
    background: transparent !important;
}

body.v6 .store_nav .popup_block_new .popup_body, body.v6 #footer_nav .popup_block_new .popup_body {
    background: #90ba3c !important;
}

.popup_menu .popup_menu_item {
    color: black;
}

.popup_menu .popup_menu_item:hover {
    color: white;
}

.gutter_header, body.v6 h2, .home_block_footer {
    color: #90ba3c;
}

.slider_ctn .slider_left:hover, .slider_ctn .slider_right:hover, .slider .handle:hover, .pagebtn:hover, a.pagebtn:hover {
    background: #90ba3c !important;
}

.slider_right span, .slider_left span {
    background: none;
}

.home_tab.active .tab_content, .tabarea .tab.active .tab_content {
    background: #90ba3c !important;
    font-weight: bold;
}

.home_tab .tab_content:hover, .tabarea .tab .tab_content:hover {
    background: #90ba3c !important;
    color: white;
}

.home_tab .tab_content, .tabarea .tab.active, .tabarea .tab {
    background: #558000 !important;
    color: white;
}

.btnv6_blue_hoverfade {
    background: #90ba3c !important;
    color: black !important;
}

.btnv6_blue_hoverfade:not(.btn_disabled):not(.btn_active):hover:not(.active):hover {
    background: #558000 !important;
    color: white !important;
}

.home_tab.active {
    background: none;
    border: 1px solid #558000;
    padding: 0;
}

.tab_item_overlay img:hover, .large .tab_item_overlay img:hover, a.steam_curator_row:hover {
    border: 2px solid #90ba3c;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}

a.steam_curator_row {
    border: 2px solid transparent;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}

body.v6 .supernav_content {
    background: #90ba3c !important;
    font-weight: bold;
}

div#global_header .submenuitem {
    color: black;
    font-weight: normal;
    width: 90%;
    padding-left: 15px;
}

div#global_header .submenuitem:hover {
    background-color: #212d3d;
}

.supernav_content {
    padding-left: 0;
}

a#store_search_link img {
    background-image: url('http://i.imgur.com/SFX2eOd.png');
    margin-top: 7px;
}

.searchbox {
    height: 31px;
    padding-top: 5px;
}

.tabarea .tab .tab_content {
    color: white;
}

body.v6 .header_early_access h2 a, .paged_items_paging_pagelink, .paged_items_paging_controls .commentthread_pagelink {
    color: #558000;
}

body.v6 .header_early_access h2 a:hover, .paged_items_paging_summary {
    color: #90ba3c;
}

.block_content {
    border-color: transparent;
}

.paged_items_paging_controls .pagebtn, .search_pagination_right .pagebtn, .pagebtn, a.pagebtn {
    background: black;
    color: transparent;
    opacity: 0.3;
}

.pagebtn:hover, .pagebtn.disabled:hover, a.pagebtn.disabled, a.pagebtn.disabled:hover {
    color: transparent;
    background: #90ba3c;
}

.block .block_header {
    background: #558000;
}

.block .block_header div {
    color: white;
}

.see_all_expander {
    color: #558000;
    background: none;
}

.see_all_expander:hover {
    color: #90ba3c;
}

.tab_filter_control, .faq_question, .Curators_paging_summary {
    color: #90ba3c;
}

.tab_filter_control:hover {
    background: #212d3d;
}

.main_cluster_content .home_btn.home_customize_btn, .home_btn.home_customize_btn {
    background: transparent;
    color: white;
}

.main_cluster_content .home_btn.home_customize_btn:hover, .home_btn.home_customize_btn:hover {
    background: #90ba3c;
    color: white;
}

.explore_rightcol_box, body.v6 #footer, .curators_searchbox {
    background: none;
}

.curators_searchbox, .explanation_box {
    border: 1px solid transparent;
}

.Curators_paging_pagelink {
    color: #558000;
}

.Curators_paging_pagelink:hover {
    color: #90ba3c;
}

.btn_blue_white_innerfade:not(.btn_disabled):not(.btn_active):hover:not(.active):hover > span {
    background: #90ba3c;
    color: white;
}

.btn_blue_white_innerfade:not(.btn_disabled):not(.btn_active):not(.active) > span {
    background: #558000;
    color: black;
}

.btn_blue_white_innerfade > span, .btn_blue_white_innerfade, .btn_blue_white_innerfade:not(.btn_disabled):not(.btn_active):hover:not(.active):hover {
    background: #558000;
}

div#global_header, div#main, div#footer, .block_content {
  background-image: url('http://i.imgur.com/ol0416W.png') !important;
  background-color: transparent;
}
}
