css:
@-moz-document domain("intuit.ru") {
/**
 * Most of elements
 */

.keywords, #right-panel, #y5_direct1, .topbanner, 
.rtxt, td[width="160"], .lecture_write_to_us, 
#lecture_banner, .adv_top, .copy, .border, 
#social_share, #block-intuit_menu-social_subs, 
#main-wraper #panel-container #left-panel,
#main-wraper #right-panel, #Ctrl-Enter,
.row .links, .top .additional, 
#main-wraper .main-wraper-tr:first-child,
#site-logo, #id-study-content-lecture-bottom-1,
#footer-panel, #main-wraper #header-panel, 
#search-box-region, #course-controls, #leave-comment-for-task {
  display: none;
}
#main-wraper #center-panel {
  width: 50%;
}
body, .eoi, .eoi #test-task-form .answer .wrapper label .left, .ui-dialog .ui-dialog-titlebar,
#test-results-table, #test-results-table tbody, #test-results-table tbody tr,
.even, .title-column, .info-column, .start-date-column,
#informer-panel .center-part, #informer-panel .right-part, #informer-panel .left-part {
  background: #2B2B2B !important;
}
.entities-standard-block .container .content table, .entities-standard-block .container .content thead, .entities-standard-block .container .content tbody, .entities-standard-block .container .content tr, .entities-standard-block .container .content th, .entities-standard-block .container .content td {
  background-color: #2B2B2B !important;
}
#lecture-block .table.header, #course-table-of-contents td.test,
.entities-standard-block .table.header.plated {
  background-color: #3A3A3A !important;	
}
body, .eoi, .entities-standard-block .title h3, 
.entities-standard-block .title h1, .rtitle,
.entities-standard-block .title span.zag, 
.objectName, .eoi, #lecture-block .table.header,
#UserLoginBlock #Greeting, .eoi p, .eoi #test-task-form .question,
.about-entity, .spelling-content-entity,
.even, .title-column, .info-column, .start-date-column, .description, #course-table-of-contents td.test,
.entities-standard-block .table.header.plated, .entities-standard-block .title.secondary h3{
  color:#C0C0C0 !important;
}

/**
 * etc
 */
#UserLoginBlock #Greeting {
  display: block !important;
}
#main-menu .top, #header, #informer-panel .right-part .right-part-border,
#informer-panel .center-part .center-part-li, #informer-panel .left-part .left-part-border {
  background-image: none !important;
}
.entities-standard-block .table.header,
#lecture-block .pager {
  border-bottom: 1px solid #F49810;
}  
#lecture-block .pager {
  border-top: 1px solid #F49810;
}  
.command-button .command-button-border-1 .command-button-border-2 .command-button-border-3 .button-center {
  color: #2B2B2B !important;
}
.command-button .command-button-border-1, .command-button {
  border-color: #CCCCCC !important;
}
.command-button {
  border: 1px solid #C0C0C0 !important;
}
              /*
.command-button .command-button-border-1 .command-button-border-2 .command-button-border-3 {
  color: #333333 !important;
  background:#6ECFF6 !important;
  border-color:#6ECFF6 !important;
}*/
.xml_table {
  background-color: #808080 !important;
}
.eoi th {
  background-color: #303030 !important; 
}
.eoi td {
  background-color: #3A3A3A !important;
  border-color: #282828 !important;
}
#lecture_annotation, body, p, td {
  font: 16px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
}
.objectName, .eoi {
  font: 16px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
  font-weight: normal !important;
}
b { 
  font-size: 16px;
  font-family: Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
}
.rtitle {
  font-weight: normal !important;
}
h3 {
  font: 22px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
  color:#E0E0E0 !important;
  font-weight: normal !important;
}
.attention {
}
.lecture_title {
  font: 24px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
}
.example, .texample {
  font: 16px Monospace !important;
  color: #CC7832 !important;
}
.example {
  font-size: 16px !important;
  background-color: #3A3A3A;
  border-left: 3px solid #606060;
  padding: 0px 0 0px 5px;
}

/*test window*/
.ui-dialog .ui-dialog-title .title-ex, .ui-dialog .ui-dialog-title, .eoi #test-task-form .task_title, .eoi #test-task-form .answer .tip {
  color: #C2C2C2 !important;
}
.ui-widget-content {
  border: 1px solid #F49810 !important;
  background: #2B2B2B !important;
}
.ui-dialog .ui-dialog-titlebar {
  border-bottom: none !important;
  background-color: #2B2B2B !important;	
}
.eoi #test-task-form .answer .tip {
  border-bottom: 1px solid #F49810 !important;
}
.eoi #test-task-form .command-buttons {
  border-top: 1px solid #F49810 !important;
}
}
