css:
@-moz-document domain(webmail.ecuad.ca) {

.unseen{
  background: #fcfcfc !important;
}
.seen{
  background: #ddd !important;
}
.messageList tr:hover{
  background: #3fb2d4 !important;
  color: #fff !important;
}
.messageList td:hover{
  color: inherit;
}
.messageList th{
  padding: 0.5em !important;
}
.messageList td{
  padding: 0.6em !important;
}
.messageList img {
    vertical-align: top !important;
    padding-left: 0.5em !important;
}
.header{
  background: #32A7C8 !important; 
}
.current{
  background: #32A7C8 !important; 
  border-top-right-radius: 1em !important;
  border-top-left-radius: 1em !important;
  color:#fff !important;
  font-weight:bold !important;
}
#menu{
  background: #226e85;
  background: -moz-linear-gradient(left, #226e85 0%, #2ea1c3 100%);
  background: -webkit-gradient(linear, left top, right top, color-stop(0%,#226e85), color-stop(100%,#2ea1c3));
  background: -webkit-linear-gradient(left, #226e85 0%,#2ea1c3 100%);
  background: -o-linear-gradient(left, #226e85 0%,#2ea1c3 100%);
  background: -ms-linear-gradient(left, #226e85 0%,#2ea1c3 100%);
  background: linear-gradient(to right, #226e85 0%,#2ea1c3 100%);
  min-height:58px !important;
  max-height:1000px !important;
  padding-top:10px !important;
}
#expandedSidebar #menu{
  background:#226E85 !important;
  
}
#menu ul{
  padding: 0 !important;
}
#menu li{
  margin: 0 !important;
}
#menu h1{
  font-family: Helvetica, sans-serif !important;
  font-size: 1.8em !important;
  font-weight: 400 !important;
}
#menu a{
  font-size:1em !important;
  border-top-right-radius: 1em !important;
  border-top-left-radius: 1em !important;
  padding: 1em !important;
  transition: 0.2s ease all !important;
}
#menu a:hover{
  color: #123 !important;
}
body{
  letter-spacing: 0.015em !important;
}
#menu a img{
  margin-bottom:0.3em !important;
}
#menu .rightFloat{
  margin-right:2em !important;
}
.text{
  padding:1em !important;
}
form[name="messages"] table:nth-of-type(3) .rightAlign{
  background: #2EA1C3 !important;
  color: #fff !important;
  padding:0.5em !important;
  border-bottom: 1px solid #226E85 !important;
  font-size:1.1em !important;
}
form[name="messages"] table:nth-of-type(3) .msgheader{
    padding-left:1em !important;
  
}
form[name="imp_login"] table table{
  padding:2em !important;
  background:#fff !important;
  color:#fff !important;
  border-radius: 1em !important;
  box-shadow: 1px 1px 8px rgba(0,0,0,0.3) !important;
}
form[name="imp_login"] table table .light{
  color:#2EA1C3 !important;
}
form[name="imp_login"] table table input{
  padding:0.5em !important;
  border: 0.2em solid #2EA1C3 !important;
  border-radius:0.5em !important;
  margin-left:1em !important;
  width:80% !important;
}
form[name="imp_login"] table table select{
  padding:0.5em !important;
  border: 0.2em solid #2EA1C3;
  border-radius:0.5em !important;
  margin-left:1em !important;
  width:87% !important;
}
form[name="imp_login"] table table .button{
  background:#2EA1C3;
  width:86% !important;
  margin-top:0.5em !important;
  padding:0.5em !important;
  font-size:1.2em !important;
  font-weight:bold !important;
  color:#fff !important;
  border-bottom: 0.3em solid #226E85 !important;
  cursor:pointer !important;
  transition: 0.2s ease all !important;
}
form[name="imp_login"] table table .button:hover{
  background:#3fb2d4 !important;
  border-bottom: 0.3em solid #2d91b2 !important;
  color: rgba(0,0,0,0.4) !important;
}
form[name="imp_login"] table{
  margin-top:1.5em !important;
}
form[name="select1"] select{
  border: 1px solid #999 !important;
  padding: 0.2em 0.15em 0.15em !important;
  margin-right:0.25em !important;
  margin-top:0.25em !important;
}
form[name="copymove1"]{
  position:absolute !important;
  margin-top:-1em !important;
}
form[name="copymove1"] a{
  background: #2EA1C3 !important;
  padding:0.35em 0.35em 0.2em 0.35em !important;
  color:#fff !important;
  font-size:1em !important;
  border-radius:10% !important;
  border-bottom: 0.2em solid #226E85 !important;
  transition:0.2s ease all !important;
}
form[name="copymove1"] a:hover{
  background:#3fb2d4 !important;
  border-bottom: 0.2em solid #2d91b2 !important;
  text-decoration:none !important;
  color: rgba(0,0,0,0.7) !important;
}
form[name="copymove1"] select{
  border: 1px solid #999 !important;
  padding: 0.2em 0.15em 0.15em !important;
  margin-right:0.25em !important;
  margin-top:0.25em !important;
}
form[name="pagenav1"]{
  margin-right:0.5em !important;
}
select[name="flag1"],select[name="flag2"], select[name="target1"], select[name="target2"]{
  border: 1px solid #999 !important;
  padding: 0.2em 0.15em 0.15em !important;
  margin-right:0.25em !important;
  margin-top:0.25em !important;
}
.control .leftAlign{
  padding-left:0.25em !important;
}
#html-message{
  max-width:60em !important;
}
  
}
