css:
/*
name:       deviantNAV v7 (rotane)
version:    0.4.1
author:     rotane.deviantart.com
created:    September 25, 2014
updated:    January 21, 2015
short info: Restores and tweaks many features of the navbar that is currently in beta.
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com"){

/* block it (needed for right float) */
#overhead 
{ width: 100% !important; }

#deviantART-v7 #overhead-collect > table 
{ display: block !important; }

#deviantART-v7 #overhead
{ height: 50px !important; }


/* colours */
/* #deviantART-v7 #overhead
{ background: linear-gradient(to bottom, #657964, #526351) !important; height: 43px !important; }
*/

#deviantART-v7 #overhead a:not(#friendslink):not(#collectlink):not(.gmbutton2top)
{ color: #ddd !important; }

/*
#deviantART-v7 .oh-keep
{ text-shadow: 0 1px 1px rgba(0,0,0,0.5) !important; }
*/


/* reset submit button/link */
#deviantART-v7 #overhead #oh-menu-submit span 
{ background: none !important; box-shadow: none !important; color: inherit !important; font-weight: normal !important; padding: 0 !important; }

#deviantART-v7 #overhead #oh-menu-submit:hover,
#deviantART-v7 #oh-menu-upgrade a:hover
{ background: #3C4441 !important; }


/* friends/favourites links */
#deviantART-v7 #overhead #oh-menu-deviant,
#deviantART-v7 #overhead .oh-mc-split,
#deviantART-v7 #overhead #oh-menu-split
{ background: none !important; border: none !important; }

#deviantART-v7 #overhead #oh-menu-friends,
#deviantART-v7 #overhead #oh-menu-collect
{ background: rgba(0,0,0,0.1) !important; }

#deviantART-v7 #overhead #oh-menu-friends
{ border-left: 1px solid rgba(0,0,0,0.25) !important; }

#deviantART-v7 #overhead #oh-menu-friends a,
#deviantART-v7 #overhead #oh-menu-collect a
{ font-size: 27px !important; }


/* re-arrange */
#deviantART-v7 #overhead .oh-gap,
#deviantART-v7 #overhead #oh-menu-mobile
{ display: none !important; }

#deviantART-v7 #oh-menu-deviant,
#deviantART-v7 #oh-menu-split 
{ position: relative !important; right: 1px !important; }

#deviantART-v7 #oh-menu-upgrade 
{ position: absolute !important; right: 72px !important; display: none !important; }

#deviantART-v7 #oh-menu-friends 
{ position: absolute !important; right: 36px !important; }

#deviantART-v7 #oh-menu-collect 
{ position: absolute !important; right: 0px !important; }

#deviantART-v7 #oh-menu-more:hover 
{ cursor: pointer !important; }

#deviantART-v7 #overhead #oh-menu-friends:hover,
#deviantART-v7 #overhead #oh-menu-collect:hover,
#deviantART-v7 #overhead .oh-mmain:hover
{ background: rgba(0,0,0,0.33) !important; }


/* promo */
#deviantART-v7 #oh-menu-promo
{ position: absolute !important; right: 186px !important; }


/* dropdowns */
#deviantART-v7 #oh-menu-deviant .oh-menu,
#deviantART-v7 #oh-menu-split .oh-menu
{ left: 0 !important; }




/* search */
#deviantART-v7 #overhead #search7-ctrl input
{ border-radius: 4px !important; box-shadow: inset 0 1px 2px rgba(0,0,0,0.3) !important; border: 1px solid rgba(0,0,0,0.4) !important; font-size: 14px !important; letter-spacing: 0 !important; height: 26px !important; }

#deviantART-v7 #overhead #search7-ctrl input:hover,
#deviantART-v7 #overhead #search7-ctrl:hover input,
#deviantART-v7 #overhead #search7-ctrl input:active,
#deviantART-v7 #overhead #search7-ctrl input:focus
{ border-radius: 4px !important; border: 1px solid rgba(0,0,0,0.5) !important; background: #BBCDB9 !important; }


/* prev/next buttons */
#deviantART-v7 #overhead .gmbutton2top
{ box-shadow: inset 0 1px 0px rgba(255,255,255,0.4) !important; }

}
