css:
@-moz-document domain("store.steampowered.com"), domain("steamcommunity.com") {


/*---------------------------------------------------*/
/*----- Dark Steam UI - Compact - Version 2.1.4 -----*/
/*---------------------------------------------------*/


/* This removes various parts of the Steam pages such as the curators, the discover your queue, the recently updated, the new on Steam, the recommendations on the right and the the endless scrolling list from the bottom */
.steam_curators_ctn, .home_smallcap_area, .discovery_queue_ctn, .recommended, .steam_curators_block, .apps_recommended_by_curators_ctn, #v6takeover, #content_callout, #content_loading, #content_out, .home_ctn.best_selling_vr_ctn, .home_ctn.recommended_creators_ctn {
	display: none !important;
}

#content_more {
    overflow: hidden !important;
	height: 3000px !important;
    position: fixed !important;
}


/* Front page main background */
body.v6 {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* Front page header (top part with Steam logo, etc) background */
body.v6 .supernav_content, body.v6 #global_header, body.v6 #global_header .content
{
	background: #242527 !important;
}


/* Global header background on other parts of Steam */
div#global_header {
	background: #242527 !important;
}


/* Global header background middle section */	
div#global_header .content {
	background: #242527 !important;
	height: 97px;
}


/* This kills the annoying blue glow background underneath the main scrolling banners on the front page */	
.home_cluster_ctn {
	background: none !important;
}


/* Front page navigation (horizontal) menu's item color when the mouse hovers over it */
.popup_menu .popup_menu_item:hover, .popup_menu .popup_menu_item.focus {
	background-color: #869197 !important;
}


/* Front page nav (horizontal) menu's dropdown list background */
body.v6 .store_nav .popup_block_new .popup_body, body.v6 #footer_nav .popup_block_new .popup_body 

{
	background: #1F2225 !important;
}


/* Front page nav (horizontal) menu's dropdown list text color */
.popup_menu .popup_menu_item {
	color: #c7d5e0 !important;
}


/* Front page nav (horizontal) menu's dropdown list section titles (e.g. Browse by category) */
body.v6 .store_nav .popup_menu_subheader,
body.v6 #footer_nav .popup_menu_subheader{
	color: #84A5B9 !important;
}


/* Front page nav menu's dropdown list divider lines */
body.v6 .store_nav .popup_menu .hr,
body.v6 #footer_nav .popup_menu .hr{
	background-color: #2D3D46 !important;
}


/* The little down arrows in the navigation menu's buttons - this changes them to white */
.store_nav .tab > span.pulldown > span {

	background-image: url('http://store.akamai.steamstatic.com/public/images/v6/btn_arrow_down_padded_white.png' ) !important;
}



/* Game/app page background */
body.v6.game_bg {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* This removes the blueish screenshot-like background at the top of the game pages */
.game_page_background.game:not(.sale_page_background) {
	background-image: none !important;
}


/* This gets rid of another annoying glow effect in the game page background underneath the screenshot tab */
.game_background_glow {
	background: none !important;
}


/* Game/app hub background */
.apphub_background
{
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* Some more game/app hub background - because we can't get rid of the blue that easy! */
body.apphub_blue
{
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background:  linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* The light grey color of the forum topic list - this is the original color but we might want to change it later */
.forum_topic {
	background-color: #2f2f30 !important;
	/*background-color: #272727;*/
	margin-bottom: 1px !important;
	height: 54px !important;
}


/* Page background on some community pages - the blue strikes back */	
body.flat_page.blue {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background:  linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* The footer background of the aforementioned community pages - I think he's really dead now, Jim */
body.flat_page #footer {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* This is also used to be some blue background somewhere... forgot where... */
body.flat_page .pagecontent.bluebg {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* The background behind the main discussions hub's welcome text - yes, that also had a blue glow */	
.discussions_page {
	background-image: none !important;
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* Same background, different layer */	
.group_content.bottomless {
	background: rgba(30, 30, 31, 0) !important;
}


/* The details text (mostly tags) on the front page's game lists - this makes it brighter to be more readable */	
.tab_item_details {
	color: #8E969E !important;
}


/* The pricetag text in the aforementioned front page lists - also made brighter (and less blue) */
.tab_item_discount .discount_final_price {
	color: #C4C6C9 !important;
}


/* The dropdown menu's background on the headers of the community pages - for some reason it was an ancient black texture image different from the front page */
.supernav_content {
	background: #242527 !important;
}


/* The background of the subforum list (dark column on the right) on the forum pages */
.rightbox { 
	background: #1A1A1B !important;
}


/* Kills the blue background on the Browse by Category pages */
.page_background {
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* Remove the blue bar on the top of the Browse by Category pages listings */
.page_header_ctn {
	background: none !important;
}


/* The background and text color of the green discount box showing the discount percentage on sales */
.discount_block .discount_pct, .discount_pct {
	color: #a4d007 !important;
	background: #2A3A14 !important;
}


/* ----------------------------------------------------------------------- */
/* -------- V1.1.0 changes (after the forum update) - 2014.10.08. -------- */
/* ----------------------------------------------------------------------- */


/* Front page left sidemenu's title text color (e.g. "Browse by genre") */
.gutter_header {
	color: #84A5B9 !important;
}


/* Front page left sidemenu's background */
.home_page_gutter_block {

	background: linear-gradient(90deg, rgba(36, 40, 44, 1) 0%,rgba(133, 133, 133, 0) 100%) !important;
	background: -moz-linear-gradient(0deg, rgba(36, 40, 44, 0.28) 0%, rgba(133, 133, 133, 0) 100%) !

important;
}


/* Global header menu text color */
div#global_header .menuitem {
	color: #B8BFC5 !important;
}


/* Global header dropdown menu text color */
div#global_header .submenuitem
{
	color: #B8BFC5 !important;
}


/* Title color of a forum thread with unread comments - slightly toned down the white to be less blinding */
.forum_topic.unread .forum_topic_name {
	color: #EBEBEB !important;
}


/* Background color of the discussion hub's forum list items (normal and mouse-over) */
.discussions_forum_row {
	background-color: rgba(130, 136, 141, 0.2) !important;
}

.discussions_forum_row:hover {
	background-color: rgba(133, 140, 146, 0.33) !important;
}


/* Background of forum topics on mouse-over */
.forum_topic:hover {
	background-color: rgba(133, 140, 146, 0.33) !important;
}


/* Background color of the discussion hub's recent game shortcuts (normal and mouse-over) */
a.recent_game_discussion {
	background-color: rgba(130, 136, 141, 0.2) !important;
}

a.recent_game_discussion:hover {
	background-color: rgba(133, 140, 146, 0.33) !important;
}


/* Color of the blue discussion hub title text (e.g. Welcome to the New Steam Discussions) */
.grouppage_content_area h2, a.large_title, h2.large_title {
	color: #6892C0 !important;
}


/* Background of the forum topic mouse-over preview tooltip */
.forum_topic_tooltip {
	width: 341px !important;
	border: 1px solid #707070 !important;
	background-color: #383838 !important;
	color: #C9CED1 !important;
	box-shadow: 0 0 15px #1F1F1F !important;
}


/* Color of the dark text on these preview boxes */
.topic_hover_row {
	color: #9E9E9E !important;
}


/* Forum comments - Background and text color, padding fix and box shadow removal */
.forumtopic_comments .commentthread_comment {
	background-color: #343435 !important;
	border: 1px solid #3d3d3e !important;
	padding: 12px 20px 20px 60px !important;
	box-shadow: none !important;
}

.forumtopic_comments .commentthread_comment_text {
	color: #cccccc !important;
}


/* Forum comments - Comments' text size - yes, they made it smaller! This brings it back to size 14 */
.commentthread_comment_text {
	font-size: 14px !important;
}


/* Forum OP - Background of a thread's Original Post (OP) with some borders added back + box shadow removed */
.forum_op {
	border: 1px solid #45484c !important;
	background: linear-gradient(160deg, rgba(96, 109, 128, 0.39) 0%, rgba(53, 53, 53, 0.35) 600px) !important;
	box-shadow: none !important;
	padding: 7px 20px 21px 63px !important;
	/*background-image: none !important;*/
}


/* Forum OP - Font type of the thread title in the OP */
.forum_op .topic {
	font-family: Arial, Helvetica, Verdana, sans-serif !important;
}


/* Game Forum List - Game title background (from black to gray + smaller margin between title and thread list) */
.discussions_game_header {
	background-color: #2f2f30 !important;
	margin-bottom: 1px !important;
}


/* Game Forum List - Footer background (from black to gray) */
.discussions_game_footer {
	background-color: #2f2f30 !important;
}


/* Link Hover Color - The color of text links when you mouse-over them (changed from that awful blinding toy-blue to something more gentle) */
a.whiteLink:hover, .whiteLink:hover, a.linkStandard:hover, a.linkTitle:hover, a.underlinedLink:hover {
	color: #AFCEE6 !important;
}


/* Browse Discussion Hubs Bar - Background and text color */
.discussions_game_browseoption {
	background-color: #2f2f30 !important;
	color: #A3A3A3 !important;
}


/* Game Hub Navigation Bar - Active tab background */
.apphub_sectionTab.active > span
{
	background: -moz-linear-gradient(top, #516D94 5%, #3E4963 95%) !important;
	background: linear-gradient(to bottom, #516D94 5%,#3E4963 95%) !important;
}

.apphub_sectionTab.active
{
	background: -moz-linear-gradient(top, #516D94 5%, #3E4963 95%) !important;
	background: linear-gradient(to bottom, #516D94 5%,#3E4963 95%) !important;
}


/* Game Hub Navigation Bar - Line under the tabs */
.apphub_sectionTabsHR
{
	background-color: #3E4963 !important;
	height: 5px !important;
}


/* Subscribe to Forum Button - Background and text color */
.btn_grey_black {
	color: #C5C5C5 !important;
	background: #2f2f30 !important;	
}


/* Discussion Hub background for Firefox */
body
{
	background: -moz-linear-gradient(right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
	background: linear-gradient( to right, #111111 2%, #1E1E1F 15%, #1E1E1F 85%, #111111 98%) !important;
}


/* Forum text input area (e.g. comment writing/editing) background */
input.gray_bevel, textarea.gray_bevel, .gray_bevel, .commentthread_comment_edit_textarea_ctn {
	background-color: rgba(240, 240, 240, 0.2) !important;
}


/* Forum text input font size */
.gray_bevel input, .gray_bevel textarea {
	font-size: 15px !important;
}


/* Border around text input area */
.forum_newtopic_box, .forum_formarea_box {
	background-color: rgba(107, 107, 107, 0.2) !important;
}


/* 'More discussions' list (on the right side of the forum topic view) item height */
.forum_topic_mini {
	max-height: 42px !important;
}


/* App hub header (the original stylish background) */
.apphub_HeaderTop
{
	background-image: url('http://steamcommunity-a.akamaihd.net/public/shared/images/apphubs/bg_hub_header.png') !important;
	background-repeat: no-repeat !important;
	background-position: -1px !important;
}


/* Push the app hub header's title a bit to the right so that it doesn't sit smack on the border*/
.apphub_AppName
{
	padding-left: 18px !important;
}


/* Locked forum topic background */
.forum_topic.locked {
	background: rgb(36, 36, 36) !important;
}


/* 'Last edited by' text color at the bottom of the forum posts and the color of the author's name */
.forum_audit {
	color: #7B7B7B !important;
}

.forum_audit a {
	color: #969696 !important;
}


/* Color of the timestamp on forum posts */
.forumtopic_comments .commentthread_comment_timestamp {
	color: #FFFFFF !important;
}


/* Locked and stickied forum topic background */
.forum_topic.locked.sticky {
	background: #2f2f30 !important;
	background-image: none !important;
}


/* Moved forum topic background */
.forum_topic.moved {
	background-color: #2f2f30 !important;
}


/* ---------------------------------------------------------------------- */
/* ---------- V1.2.0 changes (achievement pages) - 2015.01.12. ---------- */
/* ---------------------------------------------------------------------- */


/* Background color of the individual achievement boxes */
.achieveTxtHolder {
	background: #1B1B1B !important;
}


/* Color of the line below the "Personal Achievements" tab */
#subtabs {
	height: 8px !important;
	background-color: #414141 !important;
	background-image: none !important;
}


/* Background color of the "Personal Achievements" tab */
.tabOff,.tabOn {
	background: -moz-linear-gradient( top, #5A5A5A 5%, #414141 95%) !important;
	background: linear-gradient( to bottom, #5A5A5A 5%, #414141 95%) !important;
}


/* Background color of the top summary box */
#topSummaryBoxContent {
	background-color: #1A1A1A !important;
}


/* Background color of the achievement page (center section) */
.profile_subpage_column .maincontent, body.migrated_profile_page .profile_subpage_column 

#BG_bottom.maincontent {
	background-image: none !important;
	background-color: #262627 !important;
}


/* A little spacing added below the "Personal Achievements" tab line */
#personalAchieve {
	padding-top: 16px !important;
}


/* Background border of the blue header */
.profile_subpage_column .profile_small_header_bg {
	background-image: none !important;
	background: rgba(30, 30, 31, 0) !important;
}


/* Remove the blue border of the individual achievements on the global achievement list */
.achieveSurround {
	display: none !important;
}


/* Progress bar (filled part) background on global achievements */
.achieveFillCover {
	background-color: #464647 !important;
}


/* Color of the global achievement percentages text */
.achievePercent {
	color: #9D9D9D !important;
}


/* Color of the global achievement title text and the game's name just below that */
.profile_subpage_column h2 {
	color: #EBEBEB !important;
}
.profile_subpage_column h1 {
	color: #EBEBEB !important;
}


/* Text color of achievement names on the global achievements page */
.achieveTxt h3 {
	color: #DADADA !important;
}


/* Text color of achievement descriptions on the global achievements page */
.achieveTxt h5 {
	color: #C3C3C3 !important;
}


/* -------------------------------------------------------------------- */
/* ---------- V1.2.1 changes (color tweaks for the forums) ------------ */
/* -------------------------------------------------------------------- */


/* Discussions search box background */
.discussionSearchTextContainer
{
	background-image: url(http://steamcommunity-a.akamaihd.net/public/images/sharedfiles/ig/searchbox_workshop_bg_wide_blue.png?v=1) !important;
	-webkit-filter: hue-rotate(17deg) grayscale(43%) brightness(105%) !important;
}


/* Discussion hub inactive tab background color */
.group_tab {
	background-color: rgba(49, 50, 52, 1) !important;
}


/* Discussion hub inactive tab hover background color */
a.group_tab:hover {
	background-color: rgba(52, 70, 95, 1) !important;
}


/* Color of the line under the discussion hub tabs */
.discussions_header {
	border-bottom: 5px solid #32445C !important;
}


/* Discussion hub active tab border color */
.group_tab.active {
	background: -moz-linear-gradient( top, #354861 5%, #31435A 95%) !important;
	background: linear-gradient( to bottom, #354861 5%, #31435A 95%) !important;
}


/* Discussion hub active tab background color */
.group_tab.active > span {
	background: -moz-linear-gradient( top, #55719A 5%, #32445C 95%) !important;
	background: linear-gradient( to bottom, #55719A 5%, #32445C 95%) !important;
}


/* Game hub inactive tab background color */
.apphub_sectionTab:hover
{
	background: -moz-linear-gradient( top, #516D94 5%, #3E4963 95%) !important;
	background: linear-gradient(to bottom, #516D94 5%,#3E4963 95%) !important;
}


/* Start new discussion button border and text colors */
.btn_darkblue_white_innerfade {
	color: #ffffff !important;
	background: -moz-linear-gradient(top, #57749e 5%, #364963 95%) !important;
	background: linear-gradient(to bottom, #57749e 5%,#364963 95%) !important;
}


/* Start new discussion button background color */
.btn_darkblue_white_innerfade > span {
	color: #ffffff !important;
	background: -moz-linear-gradient(top, #445b7c 5%, #364963 95%) !important;
	background: linear-gradient(to bottom, #445b7c 5%,#364963 95%) !important;
}


/* Start new discussion button hover color */
.btn_darkblue_white_innerfade:not(.btn_disabled):not(.btn_active):hover:not(.active):hover {
	background: -moz-linear-gradient(top, #688bb1 5%, #415877 95%) !important;
	background: linear-gradient(to bottom, #688bb1 5%,#415877 95%) !important;
}
.btn_darkblue_white_innerfade:not(.btn_disabled):not(.btn_active):hover:not(.active):hover > span {
		background: transparent !important;
}

/* Game hub community content object (like videos, artwork, etc) background */
.apphub_Card
{
	background-color: #111315 !important;
}


/* -------------------------------------------------------------------- */
/* ---------- V1.3.0 changes (fixing the forum link boxes) ------------ */
/* -------------------------------------------------------------------- */

/* Forum link box description font color */
.dynamiclink_content {
	color: #BDBDBD !important;
}


/* Forum link box "by:" font color */
.dynamiclink_author {
	color: #BDBDBD !important;
}


/* Forum link box author font color */
.dynamiclink_url a, .dynamiclink_authorname {
	color: #E4E4E4 !important;
}


/* Forum link box border and background color */
.dynamiclink_box {
	border: 1px solid rgba(92, 92, 92, 1) !important;
	/*background-color: rgba(69, 69, 69, 1);*/ /* Original style */
	background: linear-gradient(160deg, rgba(135, 138, 144, 0.39) 0%, rgba(53, 53, 53, 0.35) 350px) !important;
}


/* Forum link box type font color */
.dynamiclink_name .dynamiclink_type {
	color: #BDBDBD !important;
}
.dynamiclink_type {
	color: #BDBDBD !important;
}


/* Forum link box title font color */
.dynamiclink_name {
	color: #E4E4E4 !important;
}


/* Forum link box hover border color */
.dynamiclink_box:hover {
	border-color: #A6A6A6 !important;
}


/* -------------------------------------------------------------------- */
/* ---------- V1.6.0 changes (forum icons and color fixes) ------------ */
/* -------------------------------------------------------------------- */


/* Color of the thread starter's name on the topic lists within forums (normal and hover) */
.forum_topic_op {
	color: #767B7E !important;
}
.forum_topic:hover .forum_topic_op {
	color: #B1B6B8 !important;
}


/* Color of the timestamp on the topic lists within forums (normal and hover) */
.forum_topic_lastpost {
	color: #8D8E90 !important;
}
.forum_topic:hover .forum_topic_lastpost {
	color: #B1B6B8 !important;
}


/* Color of the forum topic icons (envelopes, padlocks) on the topic lists */
.forum_topic_icon {
	-webkit-filter: grayscale(65%) hue-rotate(29deg) !important;
	filter: grayscale(65%) hue-rotate(29deg) !important;
}


/* Color of the paging buttons at the top of the topic lists */
.pagebtn, a.pagebtn {
	background-color: #000 !important;
	border: 1px solid #000 !important;
	color: #939393 !important;
}

.pagebtn:hover, a.pagebtn:hover {
	background-color: #97C0E3 !important;
	border-color: #97C0E3 !important;
	color: #3C3D3E !important;
}

.pagebtn.disabled, .pagebtn.disabled:hover, a.pagebtn.disabled, a.pagebtn.disabled:hover {
	border: 1px solid #333 !important;
    background-color: transparent !important;
	color: #333 !important;
}


/* Color of the forum icons (envelopes) on the community forum list */
.discussions_forumlist_icon img {
	-webkit-filter: grayscale(65%) hue-rotate(29deg) !important;
	filter: grayscale(65%) hue-rotate(29deg) !important;
}

.recent_game_discussion .recent_game_discussions_unread_indicator {
	-webkit-filter: grayscale(65%) hue-rotate(29deg) !important;
	filter: grayscale(65%) hue-rotate(29deg) !important;
}

.discussions_recentlyplayed.unread {
	-webkit-filter: grayscale(65%) hue-rotate(29deg) !important;
	filter: grayscale(65%) hue-rotate(29deg) !important;
}


/* Forum paging bar background and text color */
.forum_paging
{
	background-color: rgba(0, 0, 0, 0.15) !important;
    color: #909090 !important;
}


/* Greenlight Yes/No button background after voting */
.btn_darkblue_white_innerfade.toggled > span {
	color: #323b49 !important;
	background: -moz-linear-gradient(top,  #989b9e 0%, #aeb1b5 100%) !important;
	background: linear-gradient(to bottom,  #989b9e 0%,#aeb1b5 100%) !important;
}



/* Color of the blue "arrow" background highlighting the currently selected forum on the right of the forum pages */
.rightbox_list_option.selected .rightbox_list_selection_background {
	-webkit-filter: hue-rotate(21deg) saturate(0.55) brightness(1.49) !important;
    filter: hue-rotate(21deg) saturate(0.55) brightness(1.49) !important;
}


/* ------------------------------------------------------------------------------ */
/* ---------- V1.6.3 changes (fixed background during announcements) ------------ */
/* ------------------------------------------------------------------------------ */


/* Background color of the front page during special Steam announcements */
.home_page_body_ctn.has_takeover {
	background: -webkit-linear-gradient(#000, #000 45%, #141414 65%, #1E1E1F) !important;
	background: -moz-linear-gradient(#000, #000 45%, #141414 65%, #1E1E1F) !important;
	background: linear-gradient(#000, #000 45%, #141414 65%, #1E1E1F) !important;
}

/* Disabling the gap between the store list and the Valve footer */
body.v6.infinite_scrolling #footer_spacer.small_footer {
    height: 0px !important;
}


/* ------------------------------------------------------------------------------------- */
