css:
#main {
    border-right: 1px solid #f2f2f2;
    border-left: 1px solid #f2f2f2;
    background-image: none;
}
.v6-beta nav.tabs {
     margin-top: 75px !important;
}
body.section-post .v6 .v6-container .v6-content{
     margin-top: 75px !important;
}
body.section-post .v6 .sidebar{
     margin-top: 75px !important;
}
a {
    color: #666;
    text-decoration: none;
    font-weight: bold;
    cursor: pointer;
}
    
a:hover {
    color: #1a1a1a;
    text-decoration: none;
}
    
a:active {
    color: #3d3d3d ;
    text-decoration: none;
    font-weight: bold;
    cursor: pointer;
}    
    
a:focus {
    color: #3d3d3d;
    text-decoration: none;
}    
  
.small-list.activity-log, .small-list.quick-reply {
    border-right: 0px solid #ffd700;
    border-width: 0px 0px 0px;
}    
    
#footer .wrapper {
    border-top: 0px solid #EEE;
    border-bottom: 1px solid #EEE;
    background: none;
}
    
a.pie-wiroos {
    background: none;
}    
    
#asd input {
    border: 1px solid #c4c4c4;
    border-radius: 0px;
}    
    
.last-action {
    border-right: 1px solid #f2f2f2;
    border-left: 1px solid #f2f2f2;
}    
    
    
.v6 nav.header {
    background: none repeat scroll 0% 0% #1A1A1A;
    border-top: 1px solid #1A1A1A;
    border-bottom: 4px solid #1A1A1A;
    position: fixed;
    transition: all 500ms ease 0s;
    box-shadow: 0px 5px 20px 0px rgba(50, 50, 50, 0.5);
}
    
.v6 header h1 a {
    opacity: 0.5;
    transition: all 500ms ease 0s;
}   
    
.v6 header h1 a:hover {
    opacity: 1;
    transition: all 500ms ease 0s;
}  
    
#scroll-up {
    top: 85px;
    background: url('http://o1.t26.net/img/arrowtop.png') no-repeat scroll left top transparent;
    box-shadow: 0px 0px 4px 0px rgba(50, 50, 50, 0.5);
    opacity: 0.5;
    border-radius: 30px;
    transition: all 500ms ease 0s;  
}
    
#scroll-up:hover {
    top: 85px;
    background: url('http://o1.t26.net/img/arrowtop.png') no-repeat scroll left top transparent;
    box-shadow: 0px 0px 6px 0px rgba(50, 50, 50, 0.75);
    opacity: 0.75;
    border-radius: 30px;
    transition: all 500ms ease 0s;  
}
    
#scroll-up:active {
    top: 85px;
    background: url('http://o1.t26.net/img/arrowtop.png') no-repeat scroll left top transparent;
    box-shadow: 0px 0px 8px 0px rgba(50, 50, 50, 1);
    opacity: 1;
    border-radius: 30px;
    transition: all 500ms ease 0s;  
}    
    
.v6 .nav-principal > ul > li > a {
    color: #fff;
    border-top: 1px solid transparent;
    opacity: 0.5;
    transition: all 500ms ease 0s;
    font-weight: normal;
}   
    
.v6 .nav-principal > ul > li.tops > a:after {
    border-color: #fff transparent transparent;
}
    
.v6 .nav-principal > ul > li > a:hover {
    color: #fff;
    border-top: 1px solid #fff;
    opacity: 1;
    transition: all 500ms ease 0s;    
}   
    
.v6 .nav-principal > ul > li.active > a, .v6  {
    color: #fff;
    border-top: 1px solid #fff;
    opacity: 1 !important;
    font-weight: bold;
}    
    
.v6 .nav-principal > ul > li > a .badge {
    background:  #fff;
    color: #1a1a1a;
}    
    
.v6 .ico-shout {
    color: #FFF;
    background: none;
    opacity: 0.5;
    transition: all 500ms ease 0s;
}    
    
.v6 .ico-shout:hover {
    background: none;
    opacity: 1;
    transition: all 500ms ease 0s;
}     
    
.v6 .ico-shout span.title, .v6 .share span.title {
    background: none repeat scroll 0% 0% padding-box #1a1a1a;
    color: #FFF;
} 
    
.v6 .nav-user .login .user-actions {
    background: none;
}       
    
.v6 .nav-user .login .user-actions .user-action > a {
    color: #fff;
    opacity: 0.5;
    transition: all 500ms ease 0s;
}    
    
.v6 .nav-user .login .user-actions .user-action > a:hover {
    color: #fff;
    opacity: 1;
    transition: all 500ms ease 0s;
}    
       
.v6 .nav-user form.buscar {
    border: 0px solid #fff;
    background: none;
    opacity: 0.5;
    transition: all 500ms ease 0s;
}    
    
.v6 .nav-user form.buscar:hover {
    border: 0px solid #fff;
    background: none;
    opacity: 1;
    transition: all 500ms ease 0s;
}    
    
.v6 .nav-user form.buscar p button {
    color: #fff;
    transition: all 500ms ease 0s;
}
    
.v6 #tool-profile.tools li a {
    color: #fff;
    background: none repeat scroll 0% 0% #1A1A1A;
}
    
.v6 #tool-profile.tools li a:hover {
    opacity: 1;

}   
    
.v6 #tool-profile.tools:before {
    border-bottom-color: #1A1A1A;
}    

.v6 #tool-profile.tools {
    background: none repeat scroll 0% 0% padding-box #1A1A1A;
    border: 1px solid #1a1a1a;
}    
    
.v6 .tools h4 {
    font-size: 14px;
    font-weight: bold;
    color: #1A1A1A;
    text-align: center;
    transition: all 500ms ease 0s;
}    
    
.v6 .tools {
    box-shadow: 5px 5px 10px 10px rgba(0, 0, 0, 0.1);
}
    
.v6 .tools ul li {
    border-top: 0px solid transparent;
}
  
body, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, a, abbr, acronym, address, del, dfn, em, img, q, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, dialog, figure, footer, header, hgroup, nav, section {
    transition: all 500ms ease 0s;
}
       
.v6 .tools ul li a:hover {
    background: none repeat scroll 0% 0% #e0e0e0;
}
   
.v6 .tools ul li a {
    opacity: 0.5;
}
    
.v6 .tools ul li a:hover {
    opacity: 0.75;
}
    
.v6 .tools ul li.unread a {
    opacity: 1;
    background: none repeat scroll 0% 0% #f9f9f9;
} 
    
.v6 .tools ul li.unread a:hover {
    opacity: 1;
    background: none repeat scroll 0% 0% #f9f9f9;
}     
    
.v6 .tools ul li.unread a .ico-point {
    background: none repeat scroll 0% 0% #1A1A1A;
}   
    
.v6 .tools .actions a {
    color: #1a1a1a;
}      
    
.v6 .tools .mas {
    display: block;
    background: none repeat scroll 0% 0% #fff;
    color: #1a1a1a;
    border-top: 0px solid #EBEBEB;
}  
    
.v6 .tools .mas:hover {
    display: block;
    background: none repeat scroll 0% 0% #1a1a1a;
    color: #fff;
    border-top: 0px solid #EBEBEB;
}  
    
.v6 .panels .subnav li a.active {
    color: #1A1A1A;
    border-bottom-color: #1A1A1A;
    text-align: center;
    transition: all 500ms ease 0s;
    font-weight: bold;
    padding-right: 11px;
}    
    
.v6 .panels .subnav li a {
    color: #7e7e7e;
    border-bottom-color: #fff;
    text-align: center;
    transition: all 500ms ease 0s;
    padding-right: 11px;
}     
   
.v6 .panels .panel ol li h4 {
    color: #1a1a1a;
    border-bottom: 1px solid #fff;
    text-align: center;
    padding-right: 11px;
}    
    
.v6 .panels .panel ol li p a {
    color: #7E7E7E;
} 
    
.v6 .panels .panel ol li p a:hover {
    color: #1a1a1a;
}    
    
.v6 .panels .panel ol li .mas {
    background: none repeat scroll 0% 0% #fff;
    color: #1a1a1a;
    border-top: 1px solid #fff;
}    
    
.v6 .panels .panel ol li .mas:hover {
    background: none repeat scroll 0% 0% #fff;
    color: #1a1a1a;
    border-top: 1px solid #fff;
}     
    
.v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count:after {
    border: 3px solid #1A1A1A;
}  
    
.v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count:before {
    border-color: #1a1a1a transparent transparent;
}    
    
.v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count {
    background: none repeat scroll 0% 0% padding-box #fff;
    color: #1a1a1a;
}    
    
.v6 #tool-shout {
    border: 1px solid #f2f2f2;
    border-radius: 3px;
}    
    
.v6 #tool-shout textarea {
    border: 1px solid #f2f2f2;
}    
    
.v6 #tool-shout .botones > a {
    background: none;
    opacity: 0.5;
}    

.v6 #tool-shout:hover .botones > a:hover {
    background: none;
    opacity: 1;
}     
    
.v6 #tool-shout .botones > a.ico-camera {
    color: #1A1A1A;
}    
   
.v6 #tool-shout .botones > a.ico-video {
    color: #1A1A1A;
}    
    
.v6 #tool-shout .botones > a.link {
    color: #1A1A1A;
    text-decoration: none;
}    
    
.v6 #tool-shout .share > a.ico-fb {
    background: none repeat scroll 0% 0% #1a1a1a;
}    
    
.v6 #tool-shout button {
    transition: all 500ms ease 0s;
    border-radius: 0px;
}    
    
.v6 #tool-shout button:hover {
    transition: all 500ms ease 0s;
}    
    
.v6 #tool-shout .share > a.ico-tw {
    background: none repeat scroll 0% 0% #1a1a1a;
}    
    
.v6 .nav-user .login .user-actions img.user-picture {
    border-radius: 0px;
} 
    
body.v6-beta .v6 nav.tabs ul li.active a {
