css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uesp.net"), domain("khwiki.com"), domain("naruto.wikia.com") {

#content, #column-content, tr, th, td, li a {
background:#000 !important;
}

a, h1, h2, h3, h4, p, h5, tr, th, li, span {
color:#00bb00 !important;
}

td {
border:none !important;
}

#toc, .toc {
  background-color: #000
}

#p-tb li, #p-lang li {
  background-color: #000;
}

#p-search, #p-lang, #p-lang .pBody, #p-lang .pBody ul, #searchform, #searchBody {
 background-color: #000; 
}

div {
 background-color: #000; 
}

div.catlinks {
  background: -webkit-linear-gradient(top,#000,#000);
}

i, cite, em, var, address, dfn {
font-style: italic;
  color: #00bb00;
}

font{
color: #00bb00;
}

element.style {
border: 0 solid #00bb00;
}

.suite_tab {
background: #131313;
}

.infobox {
border: 1px solid #131313;
background-color: #131313;
color: #000;
}

div#footer {
background-color: #000
}

ul {
 background-color: #000; 
}

#siteNotice {
background-color: #131313;
}

.mw-search-formheader {background-color: #000;}

.warningtemplate {
background-color: #000;
border: 2px solid #000;
}

.mainpage-box {
background: #000;

}

div.smwfact {
background-color: #000;
border: 1px solid #000;
}

.article-categories {
background-color: #000;
background-image: -moz-linear-gradient(top,white 50%,#000 100%);
background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#000),color-stop(100%,#000));
background-image: -o-linear-gradient(top,white 50%,#000 100%);
background-image: -ms-linear-gradient(top,white 50%,#000 100%);
border: 1px solid #000;
}

.WikiaArticle .ForumActivityModule {
box-shadow: 0px 2px 5px 0 #000;
background: #000;
border: 1px solid #000;
}
    
    
.global-navigation {
background-color: #000;
border-bottom: 1px solid #c8d2d7;
color: #092140;
}
    
.WikiHeader .navbackground {
background-color: #000;
}
    
.WikiHeader > nav li.marked {
background-color: #00bb00;
}
    
.WikiHeader > nav li.marked > a {
border-top: 1px solid #00bb00;
}
      
.WikiHeader > nav li {
border-bottom: 1px solid #00bb00;
}

.Search .SearchInput {
background: #000;
border-top: 1px solid #00bb00;
border-bottom: 1px solid #00bb00;
}

.RailModule {
zoom: 1;
background: #000;
border: 1px solid #000;
}

.hubs-menu .hub-link .label {
background-color: #000;
border-bottom: 1px solid #000;
}

.hubs-menu .hub-link .icon:after {
background-color: #000;
}

.hubs-menu {
background-color: #000;
}

.WikiaRail .module, .WikiaRail #RIGHT_SKYSCRAPER {
background: #000;
border: 1px solid #000;
}
    
div.smwfact td, div.smwfact tr, div.smwfact table {
background-color: #000;
}
    
div.smwfact {
background-color: #000;
border: 1px solid #000;
}
    
#p-Interaction li {
display: block;
list-style-image: none !important;
      background-color: #000
}

div.thumb div {
  background-color: #000;
}

.thumbcaption {
  color: #00bb00;
}
  
div.thumbinner {
  border: 1px solid #00bb00;
}

.mw-content-ltr dd, .mw-content-rtl .mw-content-ltr dd {
  color: #00bb00;
}
  
#toc, .toc, .mw-warning {
  border: 1px solid #00bb00;
}
  
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
  border: 1px #00bb00solid;
}
  
table.wikitable {
  border: 1px #00bb00solid;
}

h1, h2, h3, h4, h5, h6 {
  border-bottom: 1px solid #00bb00;
}

html .thumbimage {
  border: 1px solid #00bb00;
}

div#content {
  border: 1px solid #00bb00;
}

.catlinks {
  border: 1px solid #00bb00;
}

#p-cactions li {
  border: 1px solid #00bb00;
}
}
