css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(zip.4chan.org),
domain(img.4chan.org),
domain(orz.4chan.org),
domain(cgi.4chan.org) {
html,body {
font-family: Tahoma !important;
font-size: 0.95em !important;
background-color:#CCCC66 !important;
background-image:none !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important
}
DIV[class="4chan_ext_thread"] > BLOCKQUOTE {
background-color:#FFFFFF !important;
}
BLOCKQUOTE {
max-width:500px !important
}
DIV.postarea {
margin-left: -430px !important
}
.reply{
font-family: Tahoma !important;
font-size: 0.95em !important;
-moz-border-radius-bottomright:12px !important;
-moz-border-radius-topright:12px !important;
background-color:#CC9900 !important;
opacity: .80 !important;
}
}
@-moz-document url-prefix("http://zip.4chan.org/a/src/";),
url-prefix("http://img.4chan.org/b/src/";),
url-prefix("http://zip.4chan.org/c/src/";),
url-prefix("http://orz.4chan.org/d/src/";),
url-prefix("http://orz.4chan.org/e/src/";),
url-prefix("http://zip.4chan.org/x/src/";),
url-prefix("http://zip.4chan.org/mu/src/";){
html,body {
background: none !important
}
}
