css:
@-moz-document url-prefix("http://wiimmfi.forumotion.com/") {
/* Color Library
------------------
 * Light gray: #D9D9D9
 * Dark gray: #262626
 * Sky blue: #4AA2E6
 * Orange: #FF9900
 */





/* Main
*********************/

/* Light gray text */
p, .table-title, .table-title h2, .module, ul.topiclist, p.page-bottom, li.header dd, li.header dt,.content, .postprofile, .sceditor-container textarea, .inputbox {
  color:#D9D9D9;
}

/* Orange text (for links) */
a, a:visited, a:hover, a:active {
  color:#FF9900;
}

.content h2, .h3, .panel h2 {
  border-bottom: 1px solid #4AA2E6;
}

.headerbar {
  background-color: #262626;
    border:none;
    box-shadow:none;
}
.module {
  background:#262626;
}

body {
  background-image: url(http://i.imgur.com/GWejyyr.png);
  background-repeat: repeat;
}
/**********************
End - Main */





/* Forum Grid
*********************/
.forabg, .forumbg {
    background-color: #262626;
    border:none;
    box-shadow:none;
}

li.row:hover {
  background-color: #353535;
}
    
ul.forums, li.row {
  background:rgba(0,0,0,0);
}

/* Icons */
.topiclist .row dl.icon {
 background:url(http://i.imgur.com/6C8NCWB.png) no-repeat scroll 10px 50% !important;
}
#picture_legend {
 display:none;
}

/* Borders */
li.header {
  border-bottom: solid 1px #4AA2E6;
}

li.row, ul.forums dd, li.row dd {
  border-color:rgba(0,0,0,0)/*#4AA2E6*/;
}

.forabg, .panel {
  margin-top: 15px;
}
/**********************
End - Forum Grid */




/* Forum content
*********************/
.post,.panel {
background:#262626;
}
  
.postbody .h3, .postbody h3 {
  border-color:#4AA2E6;
}

blockquote {
background:#333  url(http://i.imgur.com/fG5uX7q.png) 6px 8px no-repeat !important;
box-shadow: inset rgba(0,0,0,0.5) 2px 2px 5px;
border: solid rgba(0,0,0,0.5) 1px !important;
  border-bottom:solid rgba(255,255,255,0.1) 1px !important;;
  border-right:solid rgba(255,255,255,0.1) 1px !important;;
}
.content blockquote {
  color:#aaa;
}

.postprofile {
border-color: #111;
}

a.mainmenu {
  color:#ccc;
  transition: .1s;
  text-decoration:none;
  box-shadow: 0 2px 6px #777 inset !important;
}

.row1, .row2 {
background-color: #282828;
}
/*.row2 {
background-color: rgba(0,0,0,0.0);
}*/
/**********************
End - Forum content */

  


/* Post creation
*********************/
.sceditor-container, div[class|="sceditor"] {
background-color:#555 !important;
}

.sceditor-toolbar {
background-color: #444!important;
box-shadow: inset rgba(0,0,0,0.5) 2px 2px 5px;
}
    
div.sceditor-dropdown, form[name="post"] div[class|="sceditor"] {
border-color:rgba(255,255,255,0.2)!important;
background:#333;
}
    
div.sceditor-group {
background:#D9D9D9!important;
border: solid 0px #000!important;
box-shadow: 1px 1px 2px rgba(0,0,0,0.3), 0 4px 6px #eee inset!important;
}
  
.sceditor-button.active, .sceditor-button.hover, .sceditor-button:active, .sceditor-button:hover {
background: #4AA2E6!important;
}

#textarea_content .ltr textarea {
  background:#222 !important;
  /*padding:4px 8px;*/
}

#smiley-box, .smile-status-box {
background-color: #222;
  display:inline-block !important;
}

.inputbox {
 text-indent:5px; 
}
.inputbox:hover {
  border: 1px solid #4AA2E6;
}
/**********************
End - Post creation */





ul.clearfix li.rightside:before {
	content:"Userstyle created by Î›v Leon | "
}
}
