css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http(|s)://(|www.)2(|-)ch\.(so|ru|hk|ec|pm|re|tf|wf|yt).*") {
    html, body {
        color: #CCCCCC !important;
        background-color: #383838 !important;
        font-family: "Droid Sans",Trebuchet,tahoma,serif !important;

    }
     
    .oppost {
        color: #CCCCCC !important;
        background-color: #202020 !important;
        border: solid 3px #729FCF !important;
        border-radius: 3px !important;
        padding: 3px !important;
        display: table !important;
        width: 99% !important;
    }
    
    .reply {
        color: #CCCCCC !important;
        background-color: #333333 !important;
        border: solid 3px #666666 !important;
        border-radius: 3px !important;
        padding: 5px !important;
        background-image: none !important;
    }
    
    .postMessage {
        margin-left: 2px !important;
        min-width: 30% !important;
    }
        
    .postername {
        color: #FFAC3F !important;
    }
    
    .posttime {
        color: #666666 !important;
    }
   
    .omittedposts {
        font-size: 80% !important;
    }
    
    .adm {
        color: black !important;
        background-color: #FF6600 !important;
        border-radius: 2px !important;
        padding-left: 2px !important;
    }
      
    .thumbnailmsg {
        padding: 3px !important;
    }
       
    code, .filesize {
        border-radius: 3px !important;
        padding: 2px !important;
        border: dashed 1px #666666 !important;
    }
    
    .abbrev {
        font-size: 80% !important;
    }
    
    .abbrev a {
        text-decoration: none !important;
        border-bottom: 1px dashed !important;
        color: #FFAB3F !important;
    }  
    
    .abupost {
        font-size: 80% !important;
    }
    
    center span {
        color: #A6A6A6 !important;
        font-size: 80% !important;
    }
  
    .theader {
        color: #FFAB3F !important;
        border-radius: 3px !important;
    }
    
    /* ===[Правим косяки макабы или Абу, выпились уже нахуй, ссука]=== */
    
     /*  Нахуй и в пизду */
    .boardstats-box, .boardstats, .boardstats-header, .footer, #boardNavBottom, .menu, hr, .turnmeoff {
        display: none !important;
    }
    
     /* Страницы каталога тредов - под общий стиль */
    .pager {
        background-color: #383838 !important;
        margin-bottom: 10px;
        background: none repeat scroll 0% 0% #DDD;
        border: 1px solid #CCC;
         
    }
    
    /* Заголовок поста не заставляет извращаться глазам */
    .post .post-title {
        background: inherit;
        font-size: 1em;
        color: #F60;
        font-weight: bold;
        
    }
    
    /* Спойлер - народу! Или спасаем глаза от разглядывания белого на светло-сером */
    .spoiler, span.spoiler, span.spoiler a {
        color: white !important;
        background-color: #202020 !important;
                   
    }
    
    /* Ссылки - теперь не ядовито оранжевого цвета */          
    a, a-link-emulator {
        color: #EFA23C !important;
        cursor: pointer !important;
        
    }
    
  

}
