@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://127.0.0.1/"), url("http://localhost/") {
  
html{
background-color:#262626}
  
body{
background-color:#ececec;
color:#262626;
-webkit-border-radius:6px;
-moz-border-radius:6px;
border-radius:6px;
-webkit-box-shadow:0 0 15px rgba(0,0,0, .6);
-moz-box-shadow:0 0 15px rgba(0,0,0, .6);
box-shadow:0 0 15px rgba(0,0,0, .6)}
  
h2{
margin-bottom:10px;
padding:7px 0 6px 10px;
width:90%;
color:#424d04;
font-size:1.2em;
font-family:Verdana, "DejaVu Sans", "Bitstream Vera Sans", Geneva, sans-serif;
background-color:#BAD80A;
text-shadow:0 1px rgba(255,255,255, .7);
-webkit-box-shadow:0 5px 2px -2px rgba(0,0,0, .5);
-moz-box-shadow:0 5px 2px -2px rgba(0,0,0, .5);
box-shadow:0 5px 2px -2px rgba(0,0,0, .5)}
  
h2:nth-child(3){
background-color:#00ccff;
width:97%;
color:#003d4c}
  
#head{
margin:0.4em auto;
height:90px}
  
dd ul li {
background:url(data:image/png;base64,) no-repeat scroll 2px 50% rgba(0, 0, 0, 0)}
  
ul.tools a{
background:url(data:image/png;base64,) no-repeat scroll 2px 50% rgba(0, 0, 0, 0)}

ul.projects a{
background:url("data:image/png;base64,") no-repeat scroll 0 100% rgba(0, 0, 0, 0)}
  
ul.aliases a{background:url("data:image/png;base64,") no-repeat scroll 0 100% rgba(0, 0, 0, 0)}
  
.utility{display:none}

}

@-moz-document url("http://localhost/?phpinfo=1"), url("http://127.0.0.1/?phpinfo=1") {
  
body{background-color:#262626}
  
table[width="600"]{width:960px}
  
td,
th{border:1px solid #666}
  
.h{background-color:#00ccff}

.e{
width:150px;
background-color:#323232;
color:#fff}

.v{background-color:#ececec}

h1,
h2{
padding:10px;
width:60%}

h1{
margin:0 auto;
background-color:#00ccff}

h2{
margin:15px auto;
background-color:#BAD80A}

hr{
height:3px;
margin-bottom:30px}

}
