@-moz-document url-prefix("https://reader.aol.com") {
body,p,.article-title,.subscription-item,span,h2,h3{font-family:"微软雅黑";color:#333;}
body.theme-light .secondary-color, body.theme-light label{font-family:"微软雅黑";}
.article-item-full .article-content, .article-item-view .article-content{font-size:16px;max-width:1000px; line-height:30px;}
h2.article-title{font-size:1.3em;}
body.theme-light .article-item-read h2.article-title{color:#3498DB;}
/*.article-list header{display:none;}*/
.article-list-body a{font-size:15px; line-height:36px;}



