css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bakabt.me") {

body, select, input, .content, .searchcloud,
.torrents tbody td
{
background: #000000 !important;
}
.column_B_body, .stats, .comment_wrap, 
#statistics_tab a, .quote,
#filelist_tab a, #peerlist_tab a,
.hoverbox, #screenshots, .default td,
table, .mw-head, .mw-body, #tab_content
{
background-color: #000000 !important;
}
#tab_menu ul li a:active
{
background-color: #333333 !important;
}
th, .icon, .torrents tbody td .icon, #tab_outer, .box h2,
.suggestion
{
background-color: #0099CC !important;
}
.browse_filter .category,
.browse_filter .tags
{
padding: 5px !important;
border: 1px #0099CC solid !important;
}
.box, .quote, #screenshots, input
{
border: 1px #0099CC solid !important;
}
.download_link
{
padding: 5px !important;
background-color: #037703 !important;
border: 1px #037703 solid !important;
}
.note, .heading
{
color: #000000 !important;
background-color: #0099CC !important;
border: 1px #0099CC solid !important;
}
.searchcloud a
{
font-size: 15px !important;
}
body, span, select, input, h1, h2, h3, ul, p, em, dd, dt,
.icon, #didyouknow a, .pager a, .category a, .label, .text,
.tags a, .searchcloud a, .title, .pager, .category, .heading,
.alt_title, .faq a, .stats p, .comment_foot, dd a, .text a,
.column_B_body a, h2 a, h3 a, .comment_foot a, 
#filelist_tab a, #statistics_tab a, #peerlist_tab a
{
color: #FFFFFF !important;
text-shadow: none !important;
}
}

@-moz-document domain("forums.bakabt.me") {

#header, #header .frame
{
background: #000000 !important;
}
#content_section, #content_section .frame,
.icon, .info, .lastpost,
.upperframe, .roundframe, .description,
.clear span, .lowerframe, .lowerframe span,
#footer_section, #footer_section .frame,
.icon2, .stickybg, .subject, .post_wrapper,
.topslice, .poster, .botslice, .topslice,
 .topslice span
{
background: #111111 !important;
}
.locked_sticky, .stickybg, .stickybg2
{
background: red !important;
}
.cat_bar, .catbg, .title_barIC, .ie6_header,
.titlebg, .first_th, .lefttext, .catbg th,
.botslice span, .plainbox
{
background: #666666 !important;
}
input, select
{
color: #FFFFFF !important;
background: #000000 !important;
}
a, strong, .info, .lastpost, .stats, .inner
{
color: #FFFFFF !important;
}
}
