css:
@-moz-document domain(xivpads.com) {
body, th{
  background: #292929;
  color: #CCC;
}

a {
color: #33A4FF
}

body, th{
  background: #292929;
  color: #CCC;
}

header .nav .abtn { 
background: -webkit-linear-gradient(top,  rgba(100,100,100,1) 0%,rgba(70,70,70,1) 100%); /* Chrome10+,Safari5.1+ */
}

header .right .memberarea .memberinfo .name {
color: #CCC !important;
}

header .right .memberarea .avatar {
border-radius: 1px;
border: solid 1px #000;
}

header .right .memberarea .memberinfo .logoutbutton {
color: #DB4242 !important;
opacity: .70;
}

.a, .chat_link {
color: #33A4FF;
}

.blue {
color: #EEEEEE;
}

.btn_selected {
background: linear-gradient(to bottom, #4387bb 0%,#3f6aaf 100%);
border-radius: 2px;
}

.chat_avatar {
padding: 0px;
border: solid 1px #000;
border-radius: 2px;
}

.chat_message {
background-color: #363636;
box-shadow: 0 1px 1px #474747;
color: #ccc;
}
.generic-table tr:hover { background-color: #555; }

.hr-0, .hr-5, .hr-10, .hr-15, .hr-30 {
box-shadow: 0 0px 0px #ccc;
}

.info {
background: linear-gradient(to bottom, #777777 0%,#555555 100%);
}

.inpage-header {
background: linear-gradient(to bottom, rgba(60, 60, 60, 1) 0%,rgba(100, 100, 100, 1) 100%);
}

.inpagewindow {
background-color: #3d3d3d;
border: solid 2px #555;
box-shadow: 0 0 50px #444,0 0 15px #444;
}

.inpagewindow .nav .button {
background-color: #5E5E5E;
box-shadow: 0 0 0px #ddd;
}

.inpagewindow .nav .selected {
color: #CCCCCC !important;
background: linear-gradient(to bottom, rgba(150, 150, 150, 1) 4%,rgba(100, 100, 100, 1) 50%);
}

.inpagewindow .pm-selected-name:hover { 
background: -webkit-linear-gradient(top,  #646464 0%,#464646 100%); /* Chrome10+,Safari5.1+ */
}

.listpaging .btn {
background-color: #646464;
}

.pad-box {
border: solid 1px #aaa;
box-shadow: 0 0px 0px #ddd;
background-color: #363636;
}

.pad-box2 { 
border: solid 1px #1c1c1c;
box-shadow: 0 0px 0px #000;
background: #363636;
}

.pad .left .pad-avatar {
height: 360px;
background-position: center;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
border: solid 1px #1c1c1c;
}

.pad-nav span {
background: #464646;
}

.pad-nav-profile:hover, .pad-nav-profile-active {
color: #000 !important;
}

.pad-top-button {
background-color: #3e3e3e;
}

.page {
background-color: #646464;
box-shadow: 0 0px 0px #ccc;
}

.portal .left .portal_tabs {
box-shadow: 0 0px 0px #ccc;
border: solid 1px #aaa;
background: linear-gradient(to bottom, rgba(100,100,100,1) 1%,rgba(70,70,70,1) 100%);
}

.portal .right {
border: solid 1px #1c1c1c;
box-shadow: 0 0px 0px #1c1c1c;
background: linear-gradient(to bottom, rgba(90,90,90,1) 81%,rgba(50,50,50,1) 100%);
}

.portal .right .portalbox {
border-bottom: solid 1px #1c1c1c;
}

.pad .right .levels .levels-left .row-selected {
margin: 0 0px !important;
}
}
