css:
@-moz-document domain('ello.co') {
::-webkit-scrollbar {
height: 5px;
width: 7px;
background-color: rgb(0,0,0);
}
::-webkit-scrollbar-thumb {
	background-color: white;
}
body {
	background-color: #2c4762;
}
.omnibar {
	background-color: #2c4762;
}
.toolbar {
	background-color: #2c4762;
}
.drawer {
	background-color: #2c4762;
}
#streams {
	background-color: #2c4762;
} 
.panel-containment {
	background-color: #2c4762;
}
.post-content {
padding: 10px;
background-color: white;
border-radius: 5px;
}

.postbar {
top: -15px;
position: relative;
padding-left:10px;
        }
.notification-post{
height:50px;
margin-bottom: 35px;
}
.notification-post p{
font-size:12px;
color:white;
}
.post-summary {
border-radius: 0px;
background-color: #5E6F80;
color: white;
}
.post-comment {
padding-top: 10px;
border-radius: 2px;
background-color: #5E6F80;
top:-60px;
margin-bottom:0px;
}
html{
font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
font-style: normal;
font-weight: 400;
}
.post.notification-post:before {
background-color: #346575;
}
.post.comment-post:before {
background-color: #6477B3;
}
.post-content img {
width:100px;
-webkit-transition: all 0.5s ease-out;
-moz-transition: all 0.5s ease-out;
transition: all 0.5s ease-out;
}
.post-content p{
font-size:14px;
  }
  .post-content img:hover {
width:auto;
-webkit-transition: all 0.5s ease-out;
-moz-transition: all 0.5s ease-out;
transition: all 0.5s ease-out;
}
.layout-content.status.status-index .components-statuses .component-container {
background-color: white;
}
.layout-content.status .incidents-list {
padding:10px;
background-color:white;
border-radius:8px;
}
.post.mentioned:before {
background-color: #211B37;
}
.comment__body{
padding-left: 6px;
background-color: #365472;
margin-top: 10px;
padding-bottom: 5px;
left: 5.6rem;
position: relative;
max-width: 40rem;
    color:white;
}
.comment__actions{
  top:-15px;
  padding-left:5px;
  position:relative;
}
.comment__header{
z-index:1;
  }
.editor__actions {
z-index:2;
}
}
