css:
@-moz-document domain("wikipedia.org") {
*{font-family:Arial,"Microsoft Yahei" !important;}


body, #mw-page-base {
background-color: #474645 !important;
background-image: none !important;
}

div#content a {
color: #000000!important;
border-bottom: dotted 1px #b0b0b0 !important;
}

div#content a:hover {
text-decoration: none!important;
border-bottom: dotted 1px blue !important;
}

#content .reference a {
border-bottom: none !important;
font-size: 0.8em!important;
}

#content .reference a:hover {
color: blue!important;
}

#mw-head {
max-width: 1205px !important;
left: 245px !important;
}

#content {
	width: auto !important;
	max-width: 900px;
	padding: 22px 30px !important;
	margin: 0px auto !important;
	color: #404040;
	background: #f6f4ec !important;
	border: none !important;
	border-radius: 3px;
}

#mw-content-text p {
font-size: 1.4em !important;
line-height: 2em !important;
margin: 1em 0 1.2em 0 !important;
max-width: 28em !important;
}

#mw-content-text li {
  font-size: 125% !important;
  line-height: 2em !important;
  margin: 1em 0 1.2em 0 !important;
  max-width: 31em !important;
}

#mw-content-text dd {
  font-size: 125% !important;
  line-height: 2em !important;
}

#mw-content-text .mw-headline {
  font-size: 120% !important;
}

#toc.toc li { font-size:100%;
    line-height: .5em !important;
}

.thumb.tleft {
margin-right: 3em !important;
}

.thumb.tright, .infobox {
margin-left: 5em !important;
}

#mw-navigation > div {
opacity: 0.6 ;
-webkit-transition: opacity 0.5s !important;
left: 120px !important;
}

#mw-navigation:hover > div {
opacity: 1 !important;
-webkit-transition: opacity 0s;
text-shadow: 1px 1px 1px #303030 !important;
}

div.vectorTabs, div.vectorTabs ul, div.vectorTabs ul li, div.vectorTabs span, div.vectorMenu a{
background-image: none !important;
background-color: transparent !important;
}

div#mw-panel div.portal div.body ul li a, #mw-panel.collapsible-nav .portal.collapsed h3 a, #mw-panel.collapsible-nav, .portal.expanded h3 a,
div.vectorTabs li a, 
#mw-head a {
line-height: 1.6em;
color: #cecece !important;
}

#mw-panel.collapsible-nav .portal {
background: none!important;
}
}
